// https://en.wikipedia.org/wiki/Xorshift#Example_implementation
uint xorshift32(uint state)
{
	state ^= state << 13;
	state ^= state >> 17;
	state ^= state << 5;
	return state;
}

uint vec4touint(vec4 v) {
    uint i = 0u;
    i += uint(v.x * 255.0 + 0.5);
    i += uint(v.y * 255.0 + 0.5) << 8;
    i += uint(v.z * 255.0 + 0.5) << 16;
    i += uint(v.w * 255.0 + 0.5) << 24;
    return i;
}

