vec2 opU( vec2 d1, float d2, float material )
{
    return (d1.x<d2) ? d1 : vec2(d2, material);
}

vec2 map(vec2 p){
    vec2 d = vec2(99999.9, 0);
    for(int i = 0;i < P_COUNT;i ++) {
       d = opU(d, sdCircle(p - getInfo(i), R), P_COLOR);
    }

    for(int i = 0;i < DC_COUNT;i ++) {
      int a = int(dc[i][0]);
      int b = int(dc[i][1]);
      d = opU(d, sdSegment(p, getInfo(a).xy,  getInfo(b).xy), DC_COLOR);
    }

    for(int i = 0;i < AC_COUNT;i ++) {
      AC info = ac[i];
      d = opU(d, sdSegment(p, getInfo(info.a).xy,  getInfo(info.b).xy), AC_COLOR);
      d = opU(d, sdSegment(p, getInfo(info.b).xy,  getInfo(info.c).xy), AC_COLOR);
    }

    d = opU(d, sdSegment(p, vec2(-1, -1), vec2(-1, 1)), BOUNDS_COLOR);
    d = opU(d, sdSegment(p, vec2(-1, 1), vec2(1, 1)), BOUNDS_COLOR);
    d = opU(d, sdSegment(p, vec2(1, 1), vec2(1, -1)), BOUNDS_COLOR);
    d = opU(d, sdSegment(p, vec2(1, -1), vec2(1, 1)), BOUNDS_COLOR);

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
