    } else if (d.y == DC_COLOR) {
      col *= vec3(.3, .6, .9);
    } else if (d.y == AC_COLOR) {
      col *= vec3(.6, .9, .3);
    } else if (d.y == BOUNDS_COLOR) {
      col *= vec3(.5, .5, .5);
    }

