vec4 getPInfo(int index) {
    vec4 info = texture(iChannel0, vec2((float(index)+.5)/iResolution.x, 0));    
    return info;
}

void bounds(inout vec4 info) {
    float min = R/2.0;
    float max = 1.0 - R/2.0;

    if (info.x < min) {
        info.x = min;
    } else if (info.x > max) {
        info.x = max;
    }

    if (info.y < min) {
        info.y = min;
    } else if (info.y > max) {
        info.y = max;
    }
}

void relaxDC(int index, inout vec4 color) {
  for(int i = 0;i < DC_COUNT;i ++) {
    vec4 info = dc[i];
    int a = int(info[0]);
    int b = int(info[1]);
    float dist = info[2];
    float stiffness = info[3];

    if (a == index || b == index) {
      vec2 d;
      if(a == index) {
        d = color.xy - getPInfo(b).xy;
      } else {
        d = getPInfo(a).xy - color.xy;
      }
      d = getPInfo(a).xy - getPInfo(b).xy;
      float m = dot(d, d);
      d *=((dist*dist - m)/m)*stiffness*0.5;
      if (a == index) {
        color.xy += d;
      } else if(b == index) {
        color.xy -= d;
      }
    }
  }
}

void relaxPC(int index, inout vec4 color) {
  for(int i = 0;i < PC_COUNT;i ++) {
    vec3 info = pc[i];
    if (int(info.x) == index) {
      color.xy = info.yz;
    }
  }
}

void rotate(inout vec2 pos, vec2 origin, float angle) {
  vec2 offset = pos - origin;
  float c = cos(angle);
  float s = sin(angle);
  pos.x = origin.x + offset.x * c - offset.y * s;
  pos.y = origin.y + offset.x * s + offset.y * c;
}

void relaxAC(int index, inout vec4 color) {
  for(int i = 0;i < AC_COUNT;i ++) {
    AC info = ac[i];
    if (info.a == index || info.b == index || info.c == index) {
      vec2 aPos;
      vec2 bPos;
      vec2 cPos;
      if (info.a == index) {
        aPos = color.xy;
        bPos = getPInfo(info.b).xy;
        cPos = getPInfo(info.c).xy;
      } else if (info.b == index) {
        aPos = getPInfo(info.a).xy;
        bPos = color.xy;
        cPos = getPInfo(info.c).xy;
      } else {
        aPos = getPInfo(info.a).xy;
        bPos = getPInfo(info.b).xy;
        cPos = color.xy;
      }
      vec2 l = aPos-bPos;
      vec2 r = cPos-bPos;

      float diff = atan(l.x * r.y - l.y * r.x, dot(l,r)) - info.angle;

      if (diff <= -PI)
        diff += 2.0 * PI;
      else if (diff >= PI)
        diff -= 2.0 * PI;

      diff *= STEP * info.stiffness;
      rotate(aPos, bPos, diff);
      rotate(cPos, bPos, -diff);
      rotate(bPos, aPos, diff);
      rotate(bPos, cPos, -diff);
      if (info.a == index) {
        color.xy = aPos;
      } else if(info.b == index) {
        color.xy = bPos;
      } else if(info.c == index) {
        color.xy = cPos;
      }   
    }
  }
}

void relaxMouse(int index, inout vec4 color) {
  if (iMouse.z > 0.) {
    vec2 target = getPInfo(index).xy;
    vec2 mouse = iMouse.xy/iResolution.xy;
    mouse.x = (mouse.x - 0.5)*iResolution.x/iResolution.y + 0.5;
    float l = dot(mouse - target, mouse - target);

    if (l < R*R) {
      color.xy = mouse;
    }
  }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = fragCoord;
    int col = int(uv.x);
    int row = int(uv.y);

    col/=1;

    if (col >= P_COUNT || row > 0) {
        return;
    }

    vec4 color = getPInfo(col);

    if (iFrame == 0) {
        color.xy = pos[col];
        color.zw = color.xy;
    } else {
        vec2 v = color.xy - color.zw;
        v *= friction;
        vec2 g = vec2((sin(iTime*1.))*0.01, (cos(iTime*1.5))*0.01);
        v += g;

        color.zw = color.xy;
        color.xy += v;

        relaxAC(col, color);
        relaxDC(col, color);
        relaxMouse(col, color);
        relaxPC(col, color);
        bounds(color);
    }
    fragColor = color;
}

