const int KEY_FORWARD = 69;
const int KEY_BACKWARD = 68;

const int KEY_TILT_UP = 38;
const int KEY_TILT_DOWN = 40;
const int KEY_PAN_LEFT = 37;
const int KEY_PAN_RIGHT = 39;
const int KEY_ROLL_LEFT = 87;
const int KEY_ROLL_RIGHT = 82;


// Return the state of a key
float get_key(int key_code) {
    return texelFetch(BUFFER_KEYBOARD, ivec2(key_code,0), 0).x;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 address = ivec2(fragCoord);

    if (address == ADDR_CAMERA_POSITION) {
        // Move the camera based on keypress
    	vec4 camera_position = read_data(BUFFER_STATE, ADDR_CAMERA_POSITION);

        if (iTime < 0.1) {
            camera_position = vec4(START_POSITION, 0.0);
        }

        float distance_field = physics_sdf(camera_position.xyz);
        float penetration_distance = -(distance_field - PHYSICS_RADIUS);

        if (penetration_distance > 0.0) {
            vec3 normal = physics_normal(camera_position.xyz);
            camera_position.xyz += normal * penetration_distance;
        }


        vec3 translation = read_data(BUFFER_STATE, ADDR_CAMERA_LIN_VELOCITY).xyz;

        // Convert to local coordinate system
        mat4 orientation = quat_to_transform(
            read_data(BUFFER_STATE, ADDR_CAMERA_ORIENTATION),
            vec3(0.0)
        );
        translation = (orientation * vec4(translation, 0.0)).xyz;
        translation *= iTimeDelta;

        camera_position.xyz += translation;
        fragColor = camera_position;
        return;
    }


    if (address == ADDR_CAMERA_ORIENTATION) {
        // Rotate the camera based on keypress
        vec4 camera_orientation = read_data(BUFFER_STATE, ADDR_CAMERA_ORIENTATION);

        if (iTime < 0.1) {
            camera_orientation = START_QUATERNION;
        }

        vec4 velocity = read_data(BUFFER_STATE, ADDR_CAMERA_ANG_VELOCITY);
        velocity *= iTimeDelta;



        vec4 pan = quat_from_axis_angle(vec3(0.0, 1.0, 0.0), velocity.x);
        vec4 tilt = quat_from_axis_angle(vec3(1.0, 0.0, 0.0), velocity.y);
        vec4 roll = quat_from_axis_angle(vec3(0.0, 0.0, 1.0), velocity.z);


        camera_orientation = quat_mul(pan, camera_orientation); 
        camera_orientation = quat_mul(tilt, camera_orientation); 
        camera_orientation = quat_mul(roll, camera_orientation); 

        fragColor = camera_orientation;
        return;
    }
    if (address == ADDR_CAMERA_ANG_VELOCITY) {
        vec4 velocity = read_data(BUFFER_STATE, ADDR_CAMERA_ANG_VELOCITY);
        vec4 mouse_delta_data = read_data(BUFFER_STATE, ADDR_MOUSE_DELTA_STATE);
        vec2 mouse_delta = iMouse.xy - mouse_delta_data.xy;

        vec3 acceleration = vec3(
            get_key(KEY_PAN_LEFT) - get_key(KEY_PAN_RIGHT),
            get_key(KEY_TILT_UP) - get_key(KEY_TILT_DOWN),
            get_key(KEY_ROLL_RIGHT) - get_key(KEY_ROLL_LEFT)
        );
        if (mouse_delta_data.z > 0.0) {
            acceleration.xy += mouse_delta * MOUSE_SENSITIVITY;
        }
        acceleration *= ANG_ACCELERATION;
        velocity.xyz += acceleration * iTimeDelta;

        vec4 drag = velocity * ANG_DRAG;
        velocity -= drag * iTimeDelta;

        fragColor = velocity;
        return;
    }
    if (address == ADDR_CAMERA_LIN_VELOCITY) {
        vec4 velocity = read_data(BUFFER_STATE, ADDR_CAMERA_LIN_VELOCITY);

        vec3 acceleration = vec3(
            get_key(KEY_RIGHT) - get_key(KEY_LEFT),
            get_key(KEY_UP) - get_key(KEY_DOWN),
            get_key(KEY_FORWARD) - get_key(KEY_BACKWARD)
        ) * LIN_ACCELERATION;
        velocity.xyz += acceleration * iTimeDelta;

        vec4 drag = velocity * LIN_DRAG;
        velocity -= drag * iTimeDelta;

        fragColor = velocity;
        return;
    }
    if (address == ADDR_MOUSE_DELTA_STATE) {
        fragColor = iMouse;
    }
}

