const ivec2 ADDR_CAMERA_POSITION = ivec2(0,0);
const ivec2 ADDR_CAMERA_ORIENTATION = ivec2(0,1);
const ivec2 ADDR_CAMERA_ANG_VELOCITY = ivec2(0,2);
const ivec2 ADDR_CAMERA_LIN_VELOCITY = ivec2(0,3);
const ivec2 ADDR_MOUSE_DELTA_STATE = ivec2(0,4);

const float LENS = 0.5;
const float PHYSICS_RADIUS = 0.05;

const int PHYSICS_MAP_NOISE_ITERATIONS = 2;
const int PHYSICS_NORMAL_NOISE_ITERATIONS = 2;

const int SURFACE_MAP_NOISE_ITERATIONS = 4;
const int NORMAL_MAP_NOISE_ITERATIONS = 5;
const int AO_NORMAL_ITERATIONS = 1;


// --------------------- EXTERNAL FUNCTIONS --------------------------

// 1 out, 3 in...
// Taken from https://www.shadertoy.com/view/4djSRW
// Used under MIT
float hash13(vec3 p3)
{
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.zyx + 31.32);
    return fract((p3.x + p3.y) * p3.z);
}

// https://iquilezles.org/articles/smin
float smin( float a, float b, float k )
{
    float h = max(k-abs(a-b),0.0);
    return min(a, b) - h*h*0.25/k;
}

// https://iquilezles.org/articles/smin
float smax( float a, float b, float k )
{
    float h = max(k-abs(a-b),0.0);
    return max(a, b) + h*h*0.25/k;
}

float baseShape(vec3 viewpoint, vec3 shapeCenter, float scale) {
    return scale * 1.0 - length(viewpoint - shapeCenter);
}


float randBaseShape(vec3 viewpoint, float density, ivec3 shapeCenter) {
    vec3 center = vec3(shapeCenter);
    return baseShape(viewpoint, center, hash13(center * 1000.0) * density);
}


float ma(float a, float b) {
    return smax(a, b, 0.1);
}


float mi(float a, float b) {
    return smin(a, b, 0.1);
    //return min(a, b);
}


float layer(vec3 viewpoint, float density) {

    ivec3 center = ivec3(floor(viewpoint));
    return ma(ma(ma(randBaseShape(viewpoint,density,center+ivec3(0,0,0)),
                       randBaseShape(viewpoint,density,center+ivec3(0,0,1))),
                   ma(randBaseShape(viewpoint,density,center+ivec3(0,1,0)),
                       randBaseShape(viewpoint,density,center+ivec3(0,1,1)))),
               ma(ma(randBaseShape(viewpoint,density,center+ivec3(1,0,0)),
                       randBaseShape(viewpoint,density,center+ivec3(1,0,1))),
                   ma(randBaseShape(viewpoint,density,center+ivec3(1,1,0)),
                       randBaseShape(viewpoint,density,center+ivec3(1,1,1)))));
}






float map(vec3 viewpoint, int iter) {

    mat4 m = mat4(
		vec4(0.6373087, -0.0796581,  0.7664804, 0.0),
  		vec4(0.2670984,  0.9558195, -0.1227499, 0.0),
  		vec4(-0.7228389,  0.2829553,  0.6304286, 0.0),
        vec4(0.3, 0.2, 0.2, 0.0)
    );

    float sdf = layer(viewpoint, 0.8);
    float scale = 1.0;

    for (int i=0; i< iter; i++) {
        viewpoint = (m * vec4(viewpoint, 1.0)).xyz;
        scale *= 3.142;

        float carve_depth = 0.1 / scale;
        float blend_radius = 0.1 / scale;

        // Because we are always doing a max to join our new sdf, we
        // know that the sdf will only ever get larger by increasing iterations.
        // We also know that it will only ever increase in distance by maxval
        // so if our current distance to the surface is >> carve_depth, then theres
        // no point doing more iterations
        // This gives ~10fps on my machine.
        if (sdf > carve_depth * 2.0) {
            break;
        }

        float maxval = sdf + carve_depth;
        float new_sdf = layer(viewpoint * scale, 0.8) / scale;
        new_sdf = smin(maxval, new_sdf, blend_radius);
        sdf = smax(sdf, new_sdf, blend_radius);



    }
    return sdf;
}


const float NORMAL_SAMPLE_SIZE = 0.0005;

vec3 calc_normal(vec3 sample_point, int iter) {
    const float h = NORMAL_SAMPLE_SIZE; // replace by an appropriate value
    const vec2 k = vec2(1,-1);

    vec3 normal = normalize(
		k.xyy * map( sample_point + k.xyy*h, iter ) + 
		k.yyx * map( sample_point + k.yyx*h, iter ) + 
		k.yxy * map( sample_point + k.yxy*h, iter ) + 
		k.xxx * map( sample_point + k.xxx*h, iter ) );
    normal = normal.xyz;
    return normal;
}


float physics_sdf(vec3 position) {
    return map(position, PHYSICS_MAP_NOISE_ITERATIONS);
}

vec3 physics_normal(vec3 position) {
    return calc_normal(position, PHYSICS_NORMAL_NOISE_ITERATIONS);
}



// Fetch a single pixe from a buffer
vec4 read_data(sampler2D buffer, ivec2 address){
    return texelFetch(buffer, address, 0);
}


// Create a quaternion from axis-angle notation
vec4 quat_from_axis_angle(vec3 axis, float angle) {
    float factor = sin(angle) / 2.0;
    float w = cos(angle) / 2.0;
    return normalize(vec4(axis*factor, w));
}

// Convert a quaternion into a transformation matrix
mat4 quat_to_transform(vec4 quat, vec3 translation) {
    vec4 q = quat;
    vec4 q2 = quat * quat;

 	return mat4(
        1.0 - 2.0*(q2.y + q2.z), 2.0*(q.x*q.y - q.z*q.w), 2.0*(q.x*q.z + q.y*q.w), 0.0,
    	2.0*(q.x*q.y + q.z*q.w), 1.0 - 2.0*(q2.x + q2.z), 2.0*(q.y*q.z - q.x*q.w), 0.0,
    	2.0*(q.x*q.z - q.y*q.w), 2.0*(q.y*q.z + q.x*q.w),1.0 - 2.0*(q2.x + q2.y), 0.0,
        translation, 0.0
    );
}

// Multiply two quaternions
vec4 quat_mul(vec4 a, vec4 b) {
 	return vec4(
        a.w * b.x + a.x * b.w + a.y * b.z - a.z * b.y,
        a.w * b.y - a.x * b.z + a.y * b.w + a.z * b.x,
        a.w * b.z + a.x * b.y - a.y * b.x + a.z * b.w,
        a.w * b.w - a.x * b.x - a.y * b.y - a.z * b.z
    );   
}

