// HUD: Draws the heads up display

#define BUFFER_STATE iChannel0
#define BUFFER_FONT iChannel1

const vec3 HUD_COLOR = vec3(0.0, 0.8, 1.0);
const float HUD_LINE_SIZE = 0.005;
const float HUD_BORDER_OFFSET = 0.08;


vec4 hud(vec2 uv) {
    vec4 cam_ang_velocity = read_data(BUFFER_STATE, ADDR_CAMERA_ANG_VELOCITY);

    float aspect = iResolution.x / iResolution.y;
    float borders = step(0.0, HUD_LINE_SIZE - abs((aspect - HUD_BORDER_OFFSET) - abs(uv.x)));
    borders += step(0.0, HUD_LINE_SIZE - abs(1.0 - HUD_BORDER_OFFSET - abs(uv.y)));

    float reticle = step(abs(0.03 - length(uv + cam_ang_velocity.xy * vec2(0.2, -0.2))), HUD_LINE_SIZE);

    float hud = borders + reticle;

    hud = clamp(hud, 0.0, 0.5);

    return vec4(HUD_COLOR,hud);
}



vec4 sampleIntChar(int number, vec2 coords) {
    return texture(BUFFER_FONT, (coords + vec2(float(number), -4.0)) / 16.0);
}


float drawInt(int number, vec2 coords, int digits) {
    vec2 arr = coords * vec2(digits, 1.0);
    int digitId = digits - int(ceil(arr.x));
    float digitBase = (pow(10.0, float(digitId)));
    number = int(floor(float(number) / digitBase)) % 10;

    float sdf = sampleIntChar(number, fract(arr)).x;
    float edge = smoothstep(0.1, 0.9, sdf);

    return sdf;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 raw_uv = fragCoord/iResolution.xy;
    vec2 uv = raw_uv;
    uv = (uv - 0.5) * 2.0;
    uv.x *= iResolution.x / iResolution.y;



    fragColor = hud(uv);

    if (uv.y < -1.0 + HUD_BORDER_OFFSET) {
        // There is almost certainly a more effecient way to do this
        vec2 textuv = raw_uv * vec2(1.0/4.2, 1.0) / HUD_BORDER_OFFSET * 2.0;
        float texBuff = 0.0;
        vec4 camera_position = read_data(BUFFER_STATE, ADDR_CAMERA_POSITION);
        textuv.x -= HUD_BORDER_OFFSET * 3.0;
        texBuff += drawInt(int(abs(camera_position.x * 1000.0)), clamp(textuv, 0.0, 1.0), 7); 
        textuv.x -= HUD_BORDER_OFFSET * 15.0;

        texBuff += drawInt(int(abs(camera_position.y * 1000.0)), clamp(textuv, 0.0, 1.0), 7); 
        textuv.x -= HUD_BORDER_OFFSET * 15.0;

        texBuff += drawInt(int(abs(camera_position.z * 1000.0)), clamp(textuv, 0.0, 1.0), 7); 
        textuv.x -= HUD_BORDER_OFFSET * 15.0;

        fragColor = mix(fragColor, texBuff * vec4(0.0, 0.8, 1.0, 1.0), texBuff);
    }
}

