{   float t=iTime*5.0;
    float x=p.x;

    float fx1=4. + 4.*smoothstep(0.0,0.7,sin(x+t));
    float y1=p.y+fx1;
    float y2=p.y+fx1+0.2;
    float yg0= differenceSDF(y1,y2);
    float fxy1=yg0;

    float fx2=3.0*sin(x)/x;
    y1=p.y+fx2;
