#define MAX_STEPS 400
#define MAX_DIST 100.
#define SURF_DIST .001

#define FK(k) floatBitsToInt(k*k/7.)^floatBitsToInt(k)
float hash(float a, float b) {
    int x = FK(a), y = FK(b);
    return float((x*x+y)*(y*y-x)-x)/2.14e9;
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

vec3 face(vec3 p) {
     vec3 a = abs(p);
     return step(a.yzx, a.xyz)*step(a.zxy, a.xyz)*sign(p);
}

float sdBox(vec3 p, vec3 s) {
    p.xz = abs(p.xz)-s.xz;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

vec3 getRo() {
    vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 3, -3);
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    return ro;
}

float GetDist(vec3 p) {
    vec2 ipos = floor(p.xz) + 0.5;
    vec2 fpos = p.xz - ipos;

    float m = -cc(2., ipos.x + h21(ipos) * pi + iTime);
    p.y += 0.5 * m;
    p.y += 0.1 * length(p.xz);

    float d = sdBox(vec3(fpos.x,p.y,fpos.y), vec3(0.1)) 
            - 0.3 - 0.1 * m;
    return 0.6 * d;
}

float RayMarch(vec3 ro, vec3 rd, float z) {

    float dO=0.;
    float s = sign(z);
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if (s != sign(dS)) { z *= 0.5; s = sign(dS); }
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro = getRo();

    vec3 rd = GetRayDir(uv, ro, vec3(0), 1.);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd, 1.);

    float IOR = 1.05;
    vec3 p = ro + rd * d;
    if(d<MAX_DIST) {

        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        vec3 pIn = p - 4. * SURF_DIST * n;
        vec3 rdIn = refract(rd, n, 1./IOR);
        float dIn = RayMarch(pIn, rdIn, -1.);

        vec3 pExit = pIn + dIn * rdIn;
        vec3 nExit = -GetNormal(pExit); // *-1.; ?

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        col = vec3(dif);

        vec3 c1 = texture(iChannel0, 0.5 + 0.5 * p.xy).rgb;
        vec3 c2 = texture(iChannel0, 0.5 + 0.5 * p.yz).rgb;
        vec3 c3 = texture(iChannel0, 0.5 + 0.5 * p.zx).rgb;
        vec3 n2 = abs(n);
        r = abs(0.5 + 0.5 * r);
        vec3 c = r.z * c1 + r.x * c2 + r.y * c3;



        float fres = 1.-pow(1. + dot(rd, n), 1. + 5. * dIn);
        vec3 e = vec3(0.5);
        vec3 col2 = pal(-0.33, e, e, e, 0.5 * vec3(0,1,2)/3.);

        col *= c;

        col = mix(col2, col, fres);


    }
    vec3 e = vec3(0.5);
    vec3 col2 = pal(-0.33, e, e, e, 0.5 * vec3(0,1,2)/3.);
    col = mix(col2, col, exp(-0.1 * length(p.xz)));
    col = mix(col2, col, clamp(p.y + 1.5 + cos(0.2 * p.z + iTime), 0., 1.));
    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

