#define THICK_BASS 1
vec4 VISUALIZATION_PX = vec4(0.0);

#define Tau (2.*3.1415926)

#define FM(fc, fm, iom) sin(Tau*fract((fc)*t) + (iom)*sin(Tau*fract((fm)*t)))

vec2 mainSound( int samp, float t )
{
    vec2 sig = vec2(0);

    t = t*1.0;
    t = mod(t, Tau*32.);
    sig += FM(20., 2., 5.0); // thick bass
    //sig += FM(20., 5., 5.0) * smoothstep(0.,8.0,t); // thicker bass
    sig += FM(20., 1., 5.0); 
    sig = mix(sig, sig+FM(21., 1., 5.0*fract(t/8.)), smoothstep(0.66,1.,fract(t/1.))); 
    sig = mix(sig, sig+FM(21., 1., 10.0*fract(t/2.)), smoothstep(0.75,1.,fract(t/2.)));
    sig = mix(sig, sig+FM(21., 1., 10.0*fract(t/16.)), smoothstep(0.75,1.,fract(t/4.)));

    // Set our pixel value to be accessible from outside
    vec2 col = clamp(smoothstep(0.9,0.91,abs(sig/1.)),0.,1.);
    VISUALIZATION_PX = vec4(col.x,col.y,col.x,(fract(t/1.)+fract(t/2.)+fract(t/4.)+col.x)/4.);
    return sig*1.;
}

