struct Basis
{
    vec3 forward, right, up;
};

//Makes a vector-basis, given a Z direction and the world "up" axis.
Basis makeBasis(vec3 forward)
{
    vec3 right = cross(vec3(0, 1, 0), forward);
    vec3 up = cross(forward, right);
