////////////////////////////
//   Terrain Parameters   //
////////////////////////////

//Attempt to make the scale of the terrain similar to real terrains,
//    so that we can treat units like meters.
#define TERR_SCALE 1.0/2000.0
#define TERR_HEIGHT 1800.0

//An exponent that reduces the grassiness of the surface.
#define TERR_GRASS_THRESHOLD 13.0

#define TERR_NOISE_SEED 1.7253789

//This originally started as an attempt at making a cliff, like in IQ's video.
//What I ended up making was a pair of plateaus at two different heights.
//The second value must be larger than the first.
#define TERR_PLATEAU_A (590.0 / TERR_HEIGHT) /* The magic number is in meters */
#define TERR_PLATEAU_B (725.0 / TERR_HEIGHT)

//The change from one noise octave to the next. Sometimes called "persistence".
#define OCTAVE_SCALING 2.0


//////////////////////////////////////////
//   Terrain Noise Octave definitions   //
//////////////////////////////////////////

//The terrain uses octave noise.
struct NoiseOctave
{
    //Scales and rotates the input position before plugging it into the noise function.
    //Scaling is a core part of octave noise.
    //Rotation helps eliminate axis-aligned bias.
    mat2 transform;

    //The importance of this octave. Should change inversely with the octave's scale.
    float weight;
};

#define OCTAVE_SCALE(i) (pow(OCTAVE_SCALING, float(i)))
#define OCTAVE_WEIGHT(i) (pow(1.0/OCTAVE_SCALING, float(i)))

//Each octave needs to be scaled, and should also be rotated.
//2D rotation matrices can be made easily with pythagorean triples;
//    here we use (5, 12, 13).
#define OCTAVE_MAT (                                 \
    /* Rotation matrix: */                           \
    mat2(12.0/13.0, 5.0/13.0,                        \
         -5.0/13.0, 12.0/13.0) *                     \
    /* Scale matrix: */                              \
    mat2(OCTAVE_SCALING, 0.0, 0.0, OCTAVE_SCALING)   \
)
#define OCTAVE_MAT2 (OCTAVE_MAT * OCTAVE_MAT)
#define OCTAVE_MAT4 (OCTAVE_MAT2 * OCTAVE_MAT2)
#define OCTAVE_MAT8 (OCTAVE_MAT4 * OCTAVE_MAT4)


#define N_OCTAVES 14
const NoiseOctave OCTAVES[N_OCTAVES] = NoiseOctave[N_OCTAVES](
    NoiseOctave(mat2(1.0, 0.0, 0.0, 1.0), OCTAVE_WEIGHT(0)),
    NoiseOctave(OCTAVE_MAT,
                OCTAVE_WEIGHT(1)),
    NoiseOctave(OCTAVE_MAT2,
                OCTAVE_WEIGHT(2)),
    NoiseOctave(OCTAVE_MAT2 * OCTAVE_MAT,
                OCTAVE_WEIGHT(3)),
    NoiseOctave(OCTAVE_MAT4,
                OCTAVE_WEIGHT(4)),
    NoiseOctave(OCTAVE_MAT4 * OCTAVE_MAT,
                OCTAVE_WEIGHT(5)),
    NoiseOctave(OCTAVE_MAT4 * OCTAVE_MAT2,
                OCTAVE_WEIGHT(6)),
    NoiseOctave(OCTAVE_MAT4 * OCTAVE_MAT2 * OCTAVE_MAT,
                OCTAVE_WEIGHT(7)),
    NoiseOctave(OCTAVE_MAT8,
                OCTAVE_WEIGHT(8)),
    NoiseOctave(OCTAVE_MAT8 * OCTAVE_MAT,
                OCTAVE_WEIGHT(9)),
    NoiseOctave(OCTAVE_MAT8 * OCTAVE_MAT2,
                OCTAVE_WEIGHT(10)),
    NoiseOctave(OCTAVE_MAT8 * OCTAVE_MAT2 * OCTAVE_MAT,
                OCTAVE_WEIGHT(11)),
    NoiseOctave(OCTAVE_MAT8 * OCTAVE_MAT4,
                OCTAVE_WEIGHT(12)),
    NoiseOctave(OCTAVE_MAT8 * OCTAVE_MAT4 * OCTAVE_MAT,
                OCTAVE_WEIGHT(13))
);

//Gets the noise value (no derivative) at a point on the terrain's surface,
//    for the given noise octave.
float terrainNoiseOctaveValue(NoiseOctave octave, vec2 pos)
{
    //Simple value noise!

    //First, transform the position for this octave.
    pos *= TERR_SCALE;
    pos = octave.transform * pos;

    //Get the four corners of the grid cell surrounding this position.
    vec2 gridPos = pos,
         gridMin = floor(gridPos),
         gridMax = gridMin + 1.0;

    //Assign a randomized value to each corner.
    vec4 gridMinMax = vec4(gridMin, gridMax);
    float v00 = hashTo1(vec3(gridMinMax.xy, TERR_NOISE_SEED)),
          v10 = hashTo1(vec3(gridMinMax.zy, TERR_NOISE_SEED)),
          v01 = hashTo1(vec3(gridMinMax.xw, TERR_NOISE_SEED)),
          v11 = hashTo1(vec3(gridMinMax.zw, TERR_NOISE_SEED));

    //Interpolate.
    //Normally this would look like:
    /*
        vec2 t = gridPos - gridMin;
        t = smoothstep(0.0, 1.0, t);
        return mix(mix(v00, v10, t.x),
                   mix(v01, v11, t.x),
                   t.y);
    */
    //To attempt to figure out analytical derivatives, here is that same expression
    //    written out as plain, differentiable math operations.
    //It seems to be wrong though.
    /*
        mix(a, b, t) = a + ((b-a) * t)
        t(p) = (-3*p*p*p) + (2*p*p)
        noise(p)   = (v00 + ((v10 - v00) * t.x)) +
                     ( ((v01 + ((v11 - v01) * t.x)) - (v00 + ((v10 - v00) * t.x))) * t.y )
                   = v00 + (t.x * v10) + (t.x * -v00) +
                     (t.y * (v01 + (t.x * v11) + (t.x * -v01))) +
                     (t.y * -(v00 + (t.x * v10) + (t.x * -v00)))
                   = v00 +
                     t.x * (v10 - v00) +
                     t.y * (v01 - v00) +
                     (t.x * t.y) + (v11 + v10 - v01 - v00)
                   = v00 +
                     ((-3 * p.x^3) + (2 * p.x^2)) * (v10 - v00) +
                     ((-3 * p.y^3) + (2 * p.y^3)) * (v01 - v00) +
                     ((-3 * p.x^3) + (2 * p.x^2)) * ((-3 * p.y^3) + (2 * p.y^2)) * (v11 + v10 - v01 - v00)
                   = v00 +
                     (p.x^3 * (v10 - v00) * -3) +
                     (p.x^2 * (v10 - v00) * 2) +
                     (p.y^3 * (v01 - v00) * -3) +
                     (p.y^2 * (v01 - v00) * 2) +
                     ((v11 + v10 - v01 - v00) *
                      ((9 * p.x^3 * p.y^3) + (4 * p.x^2 * p.y^2) +
                       (-6 * p.x^3 * p.y^2) + (-6 * p.x^2 * p.y^3)))
                   = v00 +
                     (p.x^3 * (v10 - v00) * -3) +
                     (p.x^2 * (v10 - v00) * 2) +
                     (p.y^3 * (v01 - v00) * -3) +
                     (p.y^2 * (v01 - v00) * 2) +
                     (p.x^3 * p.y^3 * (v11 + v10 - v01 - v00) * 9) +
                     (p.x^2 * p.y^2 * (v11 + v10 - v01 - v00) * 4) +
                     (p.x^3 * p.y^2 * (v11 + v10 - v01 - v00) * -6) +
                     (p.x^2 * p.y^3 * (v11 + v10 - v01 - v00) * -6)
          => noise'dx = (p.x^2 * (v10 - v00) * (-3 * 3)) +
                        (p.x * (v10 - v00) * (2 * 2)) +
                        (p.x^2 * p.y^3 * (v11 + v10 - v01 - v00) * (9 * 3)) +
                        (p.x * p.y^2 * (v11 + v10 - v01 - v00) * (4 * 2)) +
                        (p.x^2 * p.y^2 * (v11 + v10 - v01 - v00) * (3 * -6)) +
                        (p.x * p.y^3 * (v11 + v10 - v01 - v00) * (2 * -6))
          => noise'dy = (p.y^2 * (v01 - v00) * (-3 * 3)) +
                        (p.y * (v01 - v00) * (2 * 2)) +
                        (p.y^2 * p.x^3 * (v11 + v10 - v01 - v00) * (3 * 9)) +
                        (p.y * p.x^2 * (v11 + v10 - v01 - v00) * (2 * 4)) +
                        (p.y * p.x^3 * (v11 + v10 - v01 - v00) * (2 * -6)) +
                        (p.y^2 * p.x^2 * (v11 + v10 - v01 - v00) * (3 * -6))
    */
    if (true)
    {
        vec2 t = gridPos - gridMin;
        t = smoothstep(0.0, 1.0, t);
        return mix(mix(v00, v10, t.x),
                   mix(v01, v11, t.x),
                   t.y);
    }
    else
    {
        vec2 p = gridPos - gridMin;
        vec2 p2 = p * p,
             p3 = p2 * p;
        return v00 +
               (p3.x * (v10 - v00) * -3.0) +
               (p2.x * (v10 - v00) * 2.0) +
               (p3.y * (v01 - v00) * -3.0) +
               (p2.y * (v01 - v00) * 2.0) +
               (p3.x * p3.y * (v11 + v10 - v01 - v00) * 9.0) +
               (p2.x * p2.y * (v11 + v10 - v01 - v00) * 4.0) +
               (p3.x * p2.y * (v11 + v10 - v01 - v00) * -6.0) +
               (p2.x * p3.y * (v11 + v10 - v01 - v00) * -6.0);
    }
}

//Alternative noise function that uses polynomial noise, based on IQ's video.
//I don't know how to calculate the analytical derivative for this.
float terrainNoiseOctaveValue_POLY(NoiseOctave octave, vec2 pos)
{
    //Split the world into a grid.
    //Each grid cell has a different polynomial function,
    //    whose parameters are based on the 4 corners.
    //This setup makes the corners/edges of the cells line up with their neighbors,
    //    and also provides a nice analytic derivative.

    //First, transform the position for this octave.
    pos *= TERR_SCALE;
    pos = octave.transform * pos;

    //Compute the parameters of the four corners.
    vec2 gridPos = pos,
         gridMin = floor(gridPos),
         gridMax = gridMin + 1.0;
    vec4 gridMinMax = vec4(gridMin, gridMax);
    float paramMinXMinY = -1.0 + (2.0 * hashTo1(vec3(gridMinMax.xy, TERR_NOISE_SEED))),
          paramMaxXMinY = -1.0 + (2.0 * hashTo1(vec3(gridMinMax.zy, TERR_NOISE_SEED))),
          paramMinXMaxY = -1.0 + (2.0 * hashTo1(vec3(gridMinMax.xw, TERR_NOISE_SEED))),
          paramMaxXMaxY = -1.0 + (2.0 * hashTo1(vec3(gridMinMax.zw, TERR_NOISE_SEED)));

    //Compute the polynomial.
    vec2 t = gridPos - gridMin;
    t = smoothstep(0.0, 1.0, smoothstep(0.0, 1.0, t));
    return paramMinXMinY +
           (t.x * (paramMaxXMinY - paramMinXMinY)) +
           (t.y * (paramMinXMaxY - paramMinXMinY)) +
           (t.x * t.y * (paramMinXMinY + paramMaxXMaxY - paramMinXMaxY - paramMaxXMinY));
}

