//Calculates the terrain height at a given horiziontal position.
float terrainHeight(vec2 horzPos)
{
    //Compute the octave noise.
    float value = 0.0,
          maxValue = 0.0;
    for (int i = 0; i < N_OCTAVES; ++i)
    {
        NoiseOctave octave = OCTAVES[i];
        value += (octave.weight * terrainNoiseOctaveValue(octave, horzPos));
        maxValue += octave.weight;
    }
    value /= maxValue;

    //Create the two plateaus by using smoothstep() on the height values between them.
    //This pushes the height values towards either end.
    if (value >= TERR_PLATEAU_A && value <= TERR_PLATEAU_B)
    {
        float cliffT = smoothstep(TERR_PLATEAU_A, TERR_PLATEAU_B, value);
        value = mix(TERR_PLATEAU_A, TERR_PLATEAU_B, cliffT);
    }

    //TODO: How did IQ make those cliffs exactly?

    return TERR_HEIGHT * value;
}

//Estimates how close a point is to the terrain.
//If the value is <= 0, you can interpret that as a collision.
//As the distance gets larger, the quality of the estimation gets worse.
float terrainDist(vec3 pos)
{
    //Check the distance from this point to the terrain's heightmap directly below it.
    //This distance estimation works well when close to the terrain.
    //When far from the terrain, the point may be closer to a hill or sheer cliff
    //    than to the spot directly below it,
    //    but at that point the estimate isn't useful anyway.

    //Don't forget to use signed distance,
    //    a.k.a. the output should be negative when below the terrain.

    return pos.y - terrainHeight(pos.xz);
}

vec3 terrainNormal(vec2 horzPos)
{
    /*
        Here's the implementation for analytic derivatives:
            float derivX = ...;
            float derivZ = ...;
            return normalize(-vec3(derivX, -1, derivZ));
    */

    //Use finite differences, which works OK when you can't figure out an analytic derivative.
    //TODO: Why does setting the epsilon to be smaller seem to create a much worse result?
    #define TERR_NORMAL_EPSILON 0.1
    vec2 offset2 = vec2(TERR_NORMAL_EPSILON, 0.0);

    return normalize(vec3(
        terrainHeight(horzPos - offset2.xy) -
           terrainHeight(horzPos + offset2.xy),
        2.0 * TERR_NORMAL_EPSILON,
        terrainHeight(horzPos - offset2.yx) -
           terrainHeight(horzPos + offset2.yx)
    ));
}
vec3 terrainNormal(vec3 pos) { return terrainNormal(pos.xz); }

//Determines how grassy or rocky a part of the terrain's surface is.
float terrainGrassiness(vec3 pos, vec3 normal)
{
    float grassiness = normal.y;
    grassiness = pow(grassiness, TERR_GRASS_THRESHOLD);
    grassiness = smoothstep(0.0, 1.0, smoothstep(0.0, 1.0, grassiness));
    return grassiness;
}


////////////////////
//   Raymarching  //
////////////////////

//Codes for what surface was hit.
#define HIT_SKY 0
#define HIT_TERRAIN 1
#define HIT_TREES 2

struct RayQuality
{
    //How far the ray moves each step, when close to its source.
    float initialInterval;
    //How much coarser the ray gets as it moves away from its source.
    float intervalIncrement;

    //TODO: Define the target range of the ray, compute the required number of march iterations, then cap it at the below value.

    //How many iterations of the raymarcher to run.
    int nIterations;

    //TODO: Quality flag for "shadow mode", a.k.a. ignore trees, terrain normals, and some octaves.
};

struct RayHit
{
    int code; //See the HIT_[X] tokens defined above.
    vec3 pos; //Not defined if hitting sky.
    float dist; //Not well-defined if hitting sky.

    //Estimates the closest the ray got to the terrain during its travel.
    float minTerrainDist;

    //Data that's only given if the terrain was hit:
    vec3 terrainNormal;
    float terrainGrassiness;

    //Channels of debug data for visualization.
    vec4 debug;
};

RayHit marchRay(vec3 rayPos, vec3 rayDir, RayQuality quality)
{
    float totalDist = 0.0;

    //If the ray starts above the terrain, jump to the 'ceiling' of the heightmap.
    if (rayPos.y > TERR_HEIGHT)
    {
        if (rayDir.y >= 0.0)
        {
            return RayHit(HIT_SKY, rayPos, 0.0,
                          rayPos.y - terrainHeight(rayPos.xz),
                          vec3(0.0), 0.0, vec4(0.0));
        }
        else
        {
            float intersectT = (TERR_HEIGHT - rayPos.y) / rayDir.y;
            rayPos += rayDir * intersectT;
            totalDist += intersectT;
        }
    }

    float stepDist = quality.initialInterval;
    float minTerrainDist = 9999999.0;
    float previousStepTerrainDist = 9999999.0;
    for (int i = 0; i < quality.nIterations; ++i)
    {
        //Check for collisions at the current position.

        //If the ray is above the terrain and heading further upwards,
        //    don't bother with collision-testing anymore.
        if ((rayDir.y > 0.0) && (rayPos.y > TERR_HEIGHT))
        {
            return RayHit(HIT_SKY, rayPos, totalDist, minTerrainDist,
                          vec3(0, 0, 0), 0.0, vec4(0));
        }

        //Estimate distance to the terrain with the point's elevation.
        //This estimation is less accurate when the point is far away from the terrain,
        //    but in that case the value isn't important anyway.
        float terrHeight = terrainHeight(rayPos.xz);
        float terrainDist = rayPos.y - terrHeight;
        if (terrainDist <= 0.0)
        {
            //The ray is most likely underneath the terrain now.
            //Approximate the point of collision by finding the intersection
            //    between the last ray step and a line running along the terrain's surface.
            float backwardsStepScale = 1.0 - (-previousStepTerrainDist / (terrainDist - previousStepTerrainDist));
            float backwardsStep = backwardsStepScale * (stepDist - quality.intervalIncrement);
            rayPos -= backwardsStep * rayDir;
            totalDist -= backwardsStep;

            minTerrainDist = 0.0;
            vec3 normal = terrainNormal(rayPos);
            float grassiness = terrainGrassiness(rayPos, normal);
            return RayHit(HIT_TERRAIN, rayPos, totalDist, minTerrainDist,
                          normal, grassiness,
                          //Debug data:
                          vec4(backwardsStep, terrainDist, 0.0, 0.0));
        }
        minTerrainDist = min(terrainDist, minTerrainDist);

        //March the ray forward.
        rayPos += rayDir * stepDist;
        totalDist += stepDist;
        stepDist += quality.intervalIncrement;
        previousStepTerrainDist = terrainDist;
    }

    //Nothing concrete was hit, so we "hit" sky.
    return RayHit(HIT_SKY, rayPos, totalDist, minTerrainDist, vec3(0, 0, 0), 0.0,
                  //Debug data:
                  vec4(0));
}


