#define SHADOW_RAY_QUALITY RayQuality(2.5, 0.3, 100)
#define SHADOW_START_AHEAD 5.3

//Gets a 0-1 value indicating how much light reaches the given position.
float castShadow(vec3 surfacePos, SunState sun)
{
    RayHit hit = marchRay(surfacePos + (sun.towardsSun * SHADOW_START_AHEAD),
                          sun.towardsSun,
                          SHADOW_RAY_QUALITY);

    //TODO: Use the 'minTerrainDist' term for soft shadows.
    return (hit.code == HIT_SKY) ? 1.0 : 0.0;
}


