#define LIGHT_AMBIENT vec3(0.85, 0.76, 0.5)

#define FOG_ATTEN (-0.0006 * vec3(1, 2, 4.0))
#define FOG_BRIGHTNESS 0.775
//TODO: Add height-based fog

#define TERR_COLOR_ROCK vec3(0.43, 0.33, 0.31)
#define TERR_COLOR_GRASS vec3(0.18, 0.39, 0.19)
vec3 lightTerrain(RayHit hit, SunState sun)
{
    vec3 albedo = mix(TERR_COLOR_ROCK, TERR_COLOR_GRASS, hit.terrainGrassiness);
    float diffuse = max(0.0, dot(hit.terrainNormal, sun.towardsSun));

    //TODO: Specular

    vec3 fogDensity = vec3(1.0 - exp(hit.dist * FOG_ATTEN));

    float shadow = diffuse * castShadow(hit.pos, sun); //TODO: Fix the mixture of diffuse, shadow, and ambient.
    vec3 shadowColor = min(vec3(shadow), LIGHT_AMBIENT);

    vec3 surfaceColor = albedo * sun.brightness * shadowColor;
    return mix(surfaceColor, vec3(FOG_BRIGHTNESS), fogDensity);
}

#define SKY_COLOR_HORIZON vec3(0.435, 0.65, 1.0)
#define SKY_COLOR_ZENITH vec3(0.05, 0.25, 0.775)
vec3 lightSky(vec3 camPos, vec3 camRayDir, SunState state)
{
    float zenithT = smoothstep(0.0, 1.0, max(0.0, camRayDir.y));
    vec3 baseColor = mix(SKY_COLOR_HORIZON, SKY_COLOR_ZENITH, zenithT);

    //TODO: Clouds.

    return baseColor;
}


