#define CAM_FOV_SCALE 1.0

struct CamState
{
    vec3 pos, forward;
    float fovScale;
};

//Gets the direction of a ray starting at the camera
//    and passing through the given point on the screen.
vec3 camRay(CamState state, vec2 uv)
{
    Basis basis = makeBasis(state.forward);
    vec3 offset = ((1.0/state.fovScale) * basis.forward)
                     + (mix(-basis.right, basis.right, uv.x))
                     + (mix(-basis.up, basis.up, uv.y));
    return normalize(offset);
}


