struct CamKeyframe
{
    vec2 horzPos;
    float heightOffset;

    //Heading/yaw is given as 0-1.
    //Pitch is given as a value which is put into the view direction before normalization.
    float headingT, pitchForce;

    float fovScale;
};
CamKeyframe interpolateCamOrientation(CamKeyframe a, CamKeyframe b, float t)
{
    //TODO: Use different interpolation curves for each kind of data. E.x:
    //    * Pitch should spend most of its time around 0
    //    * Heading should check for wraparound angle being smaller

    return CamKeyframe(
        mix(a.horzPos, b.horzPos, t),
        mix(a.heightOffset, b.heightOffset, t),
        mix(a.headingT, b.headingT, t),
        mix(a.pitchForce, b.pitchForce, t),
        mix(a.fovScale, b.fovScale, t)
    );
}
//Converts camera keyframe data into a state.
CamState computeCamState(CamKeyframe keyframe)
{
    vec3 pos = vec3(keyframe.horzPos.x,
                    terrainHeight(keyframe.horzPos) + keyframe.heightOffset,
                    keyframe.horzPos.y);

    float headingRad = keyframe.headingT * (2.0 * 3.14159265);
    vec3 forward = normalize(vec3(cos(headingRad),
                                  keyframe.pitchForce,
                                  sin(headingRad)));

    return CamState(pos, forward, keyframe.fovScale);
}


