//High-quality, for show:
//#define MAIN_RAY_QUALITY RayQuality(0.2, 0.002, 900)
//Low-quality, for testing:
#define MAIN_RAY_QUALITY RayQuality(0.4, 0.008, 450)

//Affects the number of samples used for anti-aliasing.
//Larger values improve quality but kill performance.
//A value of 1 means "no AA", and a value of 0 effectively disables rendering.
#define AA_SAMPLES 1

//Computes a single pixel sample.
//Antialiasing can use multiple such samples.
vec3 sampleColor(CamState camera, SunState sun, vec2 uv)
{
    vec3 rayDir = camRay(camera, uv);
    RayHit hit = marchRay(camera.pos, rayDir, MAIN_RAY_QUALITY);
    if (hit.code == HIT_TERRAIN)
        return lightTerrain(hit, sun);
    else if (hit.code == HIT_SKY)
        return lightSky(camera.pos, rayDir, sun);
    else if (hit.code == HIT_TREES)
        return vec3(1, 0, 0);
    else
        return vec3(1, 0, 1); //Unknown surface! Return 'error' color.
}

//Computes the color for a pixel, including anti-aliasing.
//Takes aspect-ratio-corrected UV data (a.k.a. at least one axis has a different range than 0-1).
vec3 pixelColor(CamState camera, SunState sun,
                vec2 uvScreenMin, vec2 uvScreenMax,
                vec2 uvAspected, vec2 screenResolution)
{
    //SSAA: Render multiple times per-pixel, with subpixel offsets,
    //    and average them together.
    vec3 sampleSum = vec3(0.0);

    //This pixel covers some amount of UV space.
    vec2 uvCoverage = (uvScreenMax - uvScreenMin) / screenResolution; //Note that we'd expect uvCoverage.x == uvCoverage.y
    vec2 uvCoverageMin = uvAspected - (uvCoverage / 2.0),
         uvCoverageMax = uvCoverageMin + uvCoverage;

    //Take regular samples across this UV pixel.
    for (int x = 0; x < AA_SAMPLES; ++x)
    {
        float tX = (float(x) + 0.5) / float(AA_SAMPLES);
        for (int y = 0; y < AA_SAMPLES; ++y)
        {
            float tY = (float(y) + 0.5) / float(AA_SAMPLES);

            vec2 uvSample = mix(uvCoverageMin, uvCoverageMax, vec2(tX, tY));
            sampleSum += sampleColor(camera, sun, uvSample);
        }
    }
    vec3 rawColor = sampleSum / float(AA_SAMPLES * AA_SAMPLES);

    return postProcess(rawColor, camRay(camera, uvAspected));
}



