void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Get screen UV coordinates.
    vec2 uv = fragCoord / iResolution.xy;

    //Correct for aspect ratio by stretching UV's horizontally.
    float aspect = iResolution.x / iResolution.y;
    float uvExtraWidth = aspect - 1.0,
          uvHorzPadding = uvExtraWidth / 2.0;
    vec2 uvMin = vec2(-uvHorzPadding, 0.0),
         uvMax = vec2(1.0 + uvHorzPadding, 1.0);
    vec2 uvAspected = mix(uvMin, uvMax, uv);

    //Get camera data.
    CamState camera = camCurrentState(iTime);
    vec3 rayDir = camRay(camera, uvAspected);

    //Debug outputs:
    {
        //DEBUG: Output screen data.
        if (false)
        {
            fragColor = vec4(uvAspected, 0.0, 1.0);
            return;
        }

        //DEBUG: Output terrain-related data.
        if (false)
        {
            vec3 testPos = camera.pos + vec3(uvAspected*30.0, 0.0).xzy;
            float testDist = terrainDist(testPos) / TERR_HEIGHT;

            fragColor = vec4(testDist, testDist, testDist, 1.0);
            return;
        }

        //DEBUG: Output camera/ray data.
        if (false)
        {
            Basis basis = makeBasis(camera.forward);
            fragColor = vec4(basis.up, 1.0);
            return;
        }

        //DEBUG: Raymarching collision data.
        if (false)
        {
            RayHit hit = marchRay(camera.pos, rayDir, MAIN_RAY_QUALITY);
            if (hit.code == HIT_TERRAIN)
            {
                //Color based on hit distance.
                float strength = hit.dist / 550.0;
                strength = max(0.35, strength);
                fragColor = vec4(strength * 0.2, strength, 0.0, 1.0);

                //Color based on estimated distance to the terrain.
                fragColor = vec4(vec3(hit.minTerrainDist / 5.0), 1.0);

                //Color based on debug visualization data.
                fragColor = vec4(hit.debug.xy * vec2(1.0, -1.0),
                                 0.0, 1.0);

                //Color based on surface normal.
                fragColor = vec4(abs(hit.terrainNormal), 1.0);

                //Color based on finite-differences math.
                vec2 posXMin = vec2(hit.pos.x - TERR_NORMAL_EPSILON, hit.pos.z),
                     posXMax = vec2(hit.pos.x + TERR_NORMAL_EPSILON, hit.pos.z),
                     posZMin = vec2(hit.pos.x, hit.pos.z - TERR_NORMAL_EPSILON),
                     posZMax = vec2(hit.pos.x, hit.pos.z + TERR_NORMAL_EPSILON);
                float heightXMin = terrainHeight(posXMin),
                      heightXMax = terrainHeight(posXMax),
                      heightZMin = terrainHeight(posZMin),
                      heightZMax = terrainHeight(posZMax);
                float heightY = 2.0 * TERR_NORMAL_EPSILON;
                vec2 change2D = vec2(heightXMin - heightXMax,
                                     heightZMin - heightZMax);
                vec3 dir = vec3(change2D.x, heightY, change2D.y);
                vec3 norm = normalize(dir);
                vec2 heightsXLinearized = pow(-vec2(heightXMin, heightXMax) / TERR_HEIGHT,
                                              vec2(4.0));
                vec2 change2DLinearized = pow(abs(change2D), vec2(0.2));
                fragColor.rgb = vec3(sign(norm.xz), fract(hit.pos.x));

                //Color based on height.
                fragColor.rgb = vec3(terrainHeight(hit.pos.xz) / TERR_HEIGHT);
            }
            else if (hit.code == HIT_SKY)
            {
                float strength = hit.minTerrainDist / 100.0;
                fragColor = vec4(0.0, 0.0, strength, 1.0);
            }
            else
            {
                //Uh-oh, what are we looking at??
                fragColor = vec4(1.0, 0.0, 1.0, 1.0);
            }

            return;
        }
    }

    //The actual logic used for coloring the scene:
    if (true)
    {
        fragColor.rgb = pixelColor(camera, sunCurrentState(),
                                   uvMin, uvMax, uvAspected,
                                   iResolution.xy);
        fragColor.a = 1.0;
        return;
    }

    //DEBUG: Fallback, return noise to indicate "you forgot to return anything".
    fragColor = vec4(hashTo3(uv * 3999.0), 1.0);
}

#define S(v)        smoothstep(.8,-.8, v )
//#define fwidth(v) length(vec2(dFdx(v),dFdy(v)))

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( 2.*u - R ) / R.y;
    U = vec2( atan(U.y,U.x)/3.14, length(U)-.7 );   // ring coords
    O-=O;

    float s = texture(iChannel0,2.* U.yy , .5 ).r,  // profile of brush hair thickness
          v = u.y > floor(R.y/2.)
                ? 1.*U.x - s                        // brush start
                : -6.*(U.x+.2) - s;                 // brush end

    O += S( -v / fwidth(v) );                       // brush mask in whole polar field
    v  = S( ( abs(U.y)-.2 ) / fwidth(U.y) );        // ring mask
  //  O =  1. - O * v;                              // just brush mask
    O = mix( vec4(1), vec4(1,0,0,1)*s, O*v );       // color scheme
}

#define MAX_STEPS 400
#define MAX_DIST 100.
#define SURF_DIST .001

#define FK(k) floatBitsToInt(k*k/7.)^floatBitsToInt(k)
float hash(float a, float b) {
    int x = FK(a), y = FK(b);
    return float((x*x+y)*(y*y-x)-x)/2.14e9;
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

vec3 face(vec3 p) {
     vec3 a = abs(p);
     return step(a.yzx, a.xyz)*step(a.zxy, a.xyz)*sign(p);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

vec3 getRo() {
    vec2 m = iMouse.xy/iResolution.xy;

    float t = 0.1 * iTime;
    vec3 ro = vec3(2., 3. * cos(t), -3. * sin(t));
   // ro.yz *= Rot(-m.y*3.14+1.);
    //ro.xz *= Rot(-m.x*6.2831);
    return ro;
}

