    float r = 0.05 + 0.2 * h21(vec2(i.x, h21(i.yz)));

    float d = sdBox(p, vec3(r)) - 0.01; 
   // if (k == 0.)
        d = length(p) - r;

    return 0.5 * d;
}

float RayMarch(vec3 ro, vec3 rd, float z) {

    float dO=0.;
    float s = sign(z);
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if (s != sign(dS)) { z *= 0.5; s = sign(dS); }
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro = getRo();

    vec3 rd = GetRayDir(uv, ro, vec3(0), 1.);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd, 1.);

    float IOR = 1.05;
    vec3 p = ro + rd * d;
    if(d<MAX_DIST) {

        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        vec3 pIn = p - 4. * SURF_DIST * n;
        vec3 rdIn = refract(rd, n, 1./IOR);
        float dIn = RayMarch(pIn, rdIn, -1.);

        vec3 pExit = pIn + dIn * rdIn;
        vec3 nExit = -GetNormal(pExit); // *-1.; ?

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        col = vec3(dif);

        col = mix(vec3(0.5 + 0.5 * n), col, 0.5 + 0.5 * r);
        //col *= 0.5 + 0.5 * n.y;
        float fres = pow(1. + dot(rd, n), 5.);
        col = mix(vec3(1), col, 0.5 - 0.5 * fres);


        col *= 1.-exp(-8. * dIn);

        vec3 e = vec3(0.5);
        vec3 col2 = pal(1. * length(p) - 0. * iTime, e, e, e, 0.5 * vec3(0,1,2)/3.);
        col = mix(col2, col, 0.1 * clamp(exp(5. * length(p -pExit)), 0., 10.));

    }

    col *= exp(-0.15 * length(p));
    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

