    float mag = dot(uv,uv);
    if( mag > 1.0 ){
    	fragColor = vec4( 0.0, 0.0, 0.0, 1.0 );
        return;
    }


    vec3 norm = vec3( uv, sqrt( 1.0 - mag ));

    float time = iTime;
    float s = atan( norm.z, norm.x ) / (PI * 2.);
    float t = asin( norm.y ) / PI;
    float h = octal(
        vec3(s * 5. +time, t * 5. + time * .075, 0. ),
        8,
        .5
    );
    vec3                   col = water;
    if(h >     sandLevel ) col = sand;
    if(h >    grassLevel ) col = grass;
    if(h > mountainLevel ) col = mountain;
    if(h >     snowLevel ) col = snow;
    fragColor = vec4( vec3(col * h), 1. );



    vec3 lightDir = normalize( vec3( -1, -0.3, 1.0 ));
    float mflight = max(dot( norm, lightDir), 0.0);
    fragColor.rgb *= mflight;
}

const float eps = 1e-10;

float way1(vec2 p) {
    float d = length(p - vec2(0., .1)) - .1;

    d = min(d, length(p - vec2(0., -.1)) - .1);

    float l = 1. / max(d - .015, eps);

    return pow(l * .012, 1.3);
}

float way2(vec2 p) {
    float l = 0.;

    float d = length(p - vec2(0., .1)) - .1;
    l = 1. / max(d - .015, eps);

    d = max(0., length(p - vec2(0., -.1)) - .1);
    l += 1. / max(d - .015, eps);

    return pow(l * .012, 1.3) * .5;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    const vec2 splitUv = vec2(2., 1.);

    vec2 uv = fragCoord / iResolution.xy;

    float line = abs(uv.x - .5);
    float id = step(.5, uv.x);

    uv = fract(uv * splitUv) * 2.;
    uv -= 1.;
    uv.x *= (iResolution.x * .5 / iResolution.y);

    float c1 = way1(uv);
    float c2 = way2(uv);

    vec3 col = mix(c1, c2, id) * vec3(1., .8, .41);

    float w = fwidth(line);
    col += (1. - smoothstep(0., w, line)) * vec3(.01);

    col = 1. - exp(-col);
    col = pow(col, vec3(.454545));

    fragColor = vec4(col, 1.);
}

