  vec3 p  = ro;
  for (int i = 0; i < steps; ++i)
  {
    vec2 res = map(p);
    if (res.x < 0.01)
      return vec3(res.x, distance(p, ro), res.y);
    if (res.y < 0.)
      accLight += (vec3(172, 38, 235)/255.)*0.1+vec3(sin(distance(p, ro)*1.+iTime)*.5+.5, .5, .1)*(1.-sat(res.x/5.5))*.2;

    p+=rd*res.x*.5;
  }
  return vec3(-1.);
}

