    float pi = 3.1415926535897932384626433832795;

    for(float i = .5; i <3.; ++i)
    {
        float e = -1.;
        float f = 1.;
        u = vec2(atan(u.y,u.x),length(u));
        u+= vec2(0,-1.4);
        u = cos(u.x*e-vec2(0,pi*.5))*pow(u.y,e);
        u = vec2(atan(u.y,u.x),length(u));
        u = cos(u.x*f-vec2(0,pi*.5))*pow(u.y,f);
        u+= v;
    }
	fragColor = vec4(2./(2.+length(u)));
