#define RESOLUTION iResolution

// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
float sRGB(float t) { return mix(1.055*pow(t, 1./2.4) - 0.055, 12.92*t, step(t, 0.0031308)); }
// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
vec3 sRGB(in vec3 c) { return vec3 (sRGB(c.x), sRGB(c.y), sRGB(c.z)); }

// License: Unknown, author: Matt Taylor (https://github.com/64), found: https://64.github.io/tonemapping/
vec3 aces_approx(vec3 v) {
  v = max(v, 0.0);
  v *= 0.6f;
  float a = 2.51f;
  float b = 0.03f;
  float c = 2.43f;
  float d = 0.59f;
  float e = 0.14f;
  return clamp((v*(a*v+b))/(v*(c*v+d)+e), 0.0f, 1.0f);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;

#define BLUR
#if defined(BLUR)
  const int c = 3;

  vec2 pq = q;


  vec2 aa = 4.0/RESOLUTION.xy;
  vec2 start = pq-float(c)*aa;
  vec3 bcol = vec3(0.0); 
  for (int y = -c; y <= c; ++y) {
    vec2 pp = start;
    for (int x = -c; x <= c; ++x) {
      vec4 fcol = texture(iChannel1, pp);
      fcol *= fcol;
      bcol += fcol.xyz;
      pp.x += aa.x;
    }
    start.y += aa.y;
  }

  bcol /= float((2*c+1)*(2*c+1));
  float l = max(max(bcol.x, bcol.y), bcol.z);
  bcol *= smoothstep(0.2, 1.0, l);

  vec4 pcol = texture(iChannel0, q);
  vec3 col = pcol.xyz;
  col = aces_approx(col);
  col += 0.25*bcol;
  col = sRGB(col);
#else
  vec4 bcol = texture(iChannel1, q);
  bcol *= bcol;
  float l = max(max(bcol.x, bcol.y), bcol.z);

  vec4 pcol = texture(iChannel0,q);
  vec3 col = pcol.xyz;
  col = aces_approx(col);
  col += 0.25*bcol.xyz*smoothstep(0.2, 1.0, l);
  col = sRGB(col);
#endif

  fragColor = vec4(col, 1.0);
}

