// the "obj" function is one I made to allow me to quickly generate shapes for the pumpjacks in my 4k exegfx demo "grazing"
// can be used to make I-beams with internal supports, steel objects with ridges, and ladders


//fancy repetition to make the dictionary compressor happy :3
float mx(vec2 p) {
    return max(p.x,p.y);
}
float mx(vec3 p) {
    return max(p.x,mx(p.yz));
}
float mx(vec4 p) {
    return max(p.x,mx(p.yzw));
}
#define corner(p) (length(max(p,0.)) + min(0.,mx(p)))
#define box(p,d) corner(abs(p)-(d/2.))
//todo: make tri less..... like this
#define tri(p,r) (asin(sin((p)*r))/r)

float hex(vec2 p, vec3 rad) {
    vec3 p2 = vec3(.81,-.4,-.4)*p.y + vec3(0,-.71,.71)*p.x;
    return box(p2,vec3(rad));
}
float smin(float a, float b, float k) {
    float h = max(0.,k-abs(a-b))/k;
    return min(a,b)-h*h*h*k/6.;
}

float feature(vec3 p, float d, float w, float t) {
    float body = corner(p.xy-vec2(-t,d)/2.); // main body or nothing
    float perp = box(p.xy,vec2(t,w)); // perpendicular beam
    float supp = corner(vec3(p.x,abs(p.yz))-vec3(-t,mix(w,d,.3),t)/2.); // supporting beam
    return smin(min(supp,body),perp,t/2.)-0.0100;
}

//d.x = I extrusion depth, d.yz = body dimensions
//dp = depth of webbing
//t = I extrusion thickness
//y = greeble dimension
//o = 45deg cut offset
float obj(vec3 p, vec3 d, float dp, float t, float y, float o) {
    float body = -smin(-box(p.yz,d.yz),(o+p.y-p.z)*.71,0.0500);
    return feature(vec3(body,abs(p.x),y),dp,d.x,t);
}

float scene(vec3 p) {
    p.z -= .5;
    float o1 = obj(p, vec3(.2,2.,.3), .01, .01, p.y, 1e4);
    p.z += .5;
    float o2 = obj(p, vec3(.2,1.5,.3), .16, .01, p.z, .7);
    p.z += .5;
    float o3 = obj(p, vec3(.05,2.,.3), -.1, .01, tri(p.y,12.), 1e4);
    return min(min(o1,o2),o3);

}

vec3 norm(vec3 p) {
    mat3 k = mat3(p,p,p)-mat3(0.0001);
    return normalize(scene(p)-vec3(scene(k[0]),scene(k[1]),scene(k[2])));
}

vec3 erot(vec3 p, vec3 ax, float ro) {
    return mix(dot(ax,p)*ax,p,cos(ro))+sin(ro)*cross(ax,p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 mouse = (iMouse.xy-0.5*iResolution.xy)/iResolution.y;

    vec3 cam = normalize(vec3(1.5,uv));
    vec3 init = vec3(-3.,0,0);

    float yrot = 0.5;
    float zrot = iTime*.2;
    if (iMouse.z > 0.) {
        yrot += -4.*mouse.y;
        zrot = 4.*mouse.x;
    }
    cam = erot(cam, vec3(0,1,0), yrot);
    init = erot(init, vec3(0,1,0), yrot);
    cam = erot(cam, vec3(0,0,1), zrot);
    init = erot(init, vec3(0,0,1), zrot);

    vec3 p = init;
    vec3 atten = vec3(1);
    vec3 first = vec3(0);
    bool hit = false;
    for (int i = 0; i < 150; i++) {
        float dist = scene(p);
        //hit = dist*dist < 1e-6;
        p+=dist*cam;
        if (distance(p,init)>10.) break;
        if (dist*dist < 1e-6) {
            vec3 n = norm(p);
            float fres = 1. - pow(abs(dot(cam,n)),.5)*.98;
            cam = reflect(cam,n);
            atten *= vec3(.9) * fres * smoothstep(-.04,.02,scene(p+cam*.02));
            p += n*.01;
            if (!hit) {
                hit = true;
                first = n;
            }
        }
    }
    fragColor.xyz = sqrt(atten * length(sin(cam*2.)*.4+.6) );
    return;
    /*
    vec3 n = norm(p);
    vec3 r = reflect(cam,n);
    //don't ask how I stumbled on this texture
    float spec = length(sin(r*3.5)*.4+.6)/sqrt(3.);
    spec *= smoothstep(-.04,.02,scene(p+r*.02));
    vec3 col = vec3(.4,.1,.1)*pow(spec,2.) + pow(spec,8.);
    float bgdot = length(sin(cam*2.)*.4+.6)/2.;
