//buffer A: game of life, and camera state
int get(in ivec2 p) { //get GOL pixel
    ivec2 rp = (p-ivec2(1, 0));

    //the oposite edges are joind, resulting in a torus 
    if (rp.x > int(res.x)) rp.x = 0;
    if (rp.x < 0) rp.x = int(res.x);
    if (rp.y > int(res.y)) rp.y = 0;
    if (rp.y < 0) rp.y = int(res.y);


    return int(texelFetch(iChannel0, ivec2(rp)+ivec2(1, 0), 0).x);
}
float hash(in vec2 p) {
    vec3 p3 = fract(vec3(p.xyx)*.1031);
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.x+p3.y)*p3.z);
}
float hash1(in float p) {
    p = fract(p*0.1031);
    p *= p+33.33;
    p *= p+p;
    return fract(p);
}
float n(in float p) { //1D noise
    float f = floor(p);
    return mix(hash1(f), hash1(f+1.), fract(p));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 fc = ivec2(fragCoord);
    vec4 old = texelFetch(iChannel0, fc, 0);
    if (fc == ivec2(0)) {
        float y = (((iMouse.y)/(iResolution.y))*3.14)+0.01;
        float ty = 3.1415*(cos(iTime+n(iTime))+1.0)*0.5;
        fragColor = vec4(iMouse.z > 0.0 ? vec3(
            iMouse.x*0.01,
            y,
            y*0.1
        ) : vec3((iTime*3.0)+sin(iTime*2.0+n(iTime*0.5))*2., ty, ty*0.5)
        , 0);
    }
    if (fc == ivec2(0, 1)) {
        fragColor = texelFetch(iChannel0, ivec2(0, fc.y-1), 0);
    }
    if (fc.x < 1 || fc.x > int(res.x)+1 || fc.y > int(res.y)) return;
    fragColor = old;

    if (iFrame < 10) { //init

        //lots of gliders
        fc = (fc-ivec2(1, 0))%6;
        if (fc == ivec2(0, 0)) fragColor.x = 1.;
        if (fc == ivec2(1, 0)) fragColor.x = 1.;
        if (fc == ivec2(2, 0)) fragColor.x = 1.;
        if (fc == ivec2(2, 1)) fragColor.x = 1.;
        if (fc == ivec2(1, 2)) fragColor.x = 1.;
        return;
    } else {
        #if JUSTGLIDERS
        #else
        if (hash(fragCoord+iTime) < 0.000001) {
            fragColor = vec4(1.-fragColor.x, 0, 0, 0);
            return;
        }
        #endif
        int sum = get(fc+ivec2(-1, -1))+
                  get(fc+ivec2(-1, 0))+
                  get(fc+ivec2(-1, 1))+

                  get(fc+ivec2(0, -1))+
                  get(fc+ivec2(0, 1))+

