//Buffer B: render scene pre bloom
float hash(in vec2 p) {
    vec3 p3 = fract(vec3(p.xyx)*.1031);
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.x+p3.y)*p3.z);
}
vec2 hash2(in vec2 p) {
    vec3 p3 = fract(p.xyx*vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}
float nh(in vec3 p3) {
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.x+p3.y)*p3.z);
}
float noise(in vec3 p) {
    vec3 c = floor(p);
    vec3 f = fract(p);
    return mix( 
                mix(
                    mix( nh(c+vec3(0, 0, 0)), nh(c+vec3(1, 0, 0)), f.x), 
                    mix( nh(c+vec3(0, 1, 0)), nh(c+vec3(1, 1, 0)), f.x), 
                f.y),   
                mix(
                    mix( nh(c+vec3(0, 0, 1)), nh(c+vec3(1, 0, 1)), f.x), 
                    mix( nh(c+vec3(0, 1, 1)), nh(c+vec3(1, 1, 1)), f.x), 
                f.y), 
            f.z); 
}
float fbm(in vec3 p) {
    float v = 0.0;
    for (int i = 0; i < 5; i += 1) {
        float s = float(1<<i);
        v += noise(p*s)/s;
    }
    return v;
}
void Camera(in vec2 fragCoord, out vec3 ro, out vec3 rd, in int i) 
{
    vec2 uv = ((fragCoord.xy+(hash2(fragCoord+float(i))-0.5))/iResolution.xy)-0.5;
    uv.x *= iResolution.x/iResolution.y;
    float y = (((iMouse.y)/(iResolution.y))*3.14)+0.01;

    //linear interplate from old camera inputs to current camera inputs by a random factor for motion blur
    vec3 an = mix(texelFetch(iChannel0, ivec2(0, 0), 0).xyz,
                  texelFetch(iChannel0, ivec2(0, 1), 0).xyz,
                  hash(fragCoord+iTime+float(i)*0.1));
    //calculate camera position from poler coords
    ro = vec3(
        cos(an.x)*sin(an.y),
        sin(an.x)*sin(an.y),
        cos(an.y)
    )*3.0;

    //get ray direction twords the center
    vec3 cam_z = normalize(-ro);
    vec3 cam_x = normalize(cross(vec3(0, 0, 1), cam_z));
    vec3 cam_y = cross(cam_z, cam_x);
    rd = normalize(uv.x * cam_x + uv.y * cam_y + 1.1 * cam_z);

    //camera xz rototion
    mat2 r = mat2(cos(an.z), -sin(an.z), sin(an.z), cos(an.z));
    rd.xz *= r;
    ro.xz *= r;
}

//torus intersection and normal by Iq
float tor( in vec3 ro, in vec3 rd, in vec2 tor )
{
    float po = 1.0;
    float Ra2 = tor.x*tor.x;
    float ra2 = tor.y*tor.y;
    float m = dot(ro,ro);
    float n = dot(ro,rd);
    float k = (m + Ra2 - ra2)*0.5;
    float k3 = n;
    float k2 = n*n - Ra2*dot(rd.xy,rd.xy) + k;
    float k1 = n*k - Ra2*dot(rd.xy,ro.xy);
    float k0 = k*k - Ra2*dot(ro.xy,ro.xy);

    if( abs(k3*(k3*k3-k2)+k1) < 0.01 )
    {
        po = -1.0;
        float tmp=k1; k1=k3; k3=tmp;
        k0 = 1.0/k0;
        k1 = k1*k0;
        k2 = k2*k0;
        k3 = k3*k0;
    }

    float c2 = k2*2.0 - 3.0*k3*k3;
    float c1 = k3*(k3*k3-k2)+k1;
    float c0 = k3*(k3*(c2+2.0*k2)-8.0*k1)+4.0*k0;
    c2 /= 3.0;
    c1 *= 2.0;
    c0 /= 3.0;
    float Q = c2*c2 + c0;
    float R = c2*c2*c2 - 3.0*c2*c0 + c1*c1;
    float h = R*R - Q*Q*Q;

    if( h>=0.0 )  
    {
        h = sqrt(h);
        float v = sign(R+h)*pow(abs(R+h),1.0*0.3333333); // cube root
        float u = sign(R-h)*pow(abs(R-h),1.0*0.3333333); // cube root
        vec2 s = vec2( (v+u)+4.0*c2, (v-u)*sqrt(3.0));
        float y = sqrt(0.5*(length(s)+s.x));
        float x = 0.5*s.y/y;
        float r = 2.0*c1/(x*x+y*y);
        float t1 =  x - r - k3; t1 = (po<0.0)?2.0/t1:t1;
        float t2 = -x - r - k3; t2 = (po<0.0)?2.0/t2:t2;
        float t = 1e20;
        if( t1>0.0 ) t=t1;
        if( t2>0.0 ) t=min(t,t2);
        return t;
    }

    float sQ = sqrt(Q);
    float w = sQ*cos( acos(-R/(sQ*Q)) / 3.0 );
    float d2 = -(w+c2); if( d2<0.0 ) return -1.0;
    float d1 = sqrt(d2);
    float h1 = sqrt(w - 2.0*c2 + c1/d1);
    float h2 = sqrt(w - 2.0*c2 - c1/d1);
    float t1 = -d1 - h1 - k3; t1 = (po<0.0)?2.0/t1:t1;
    float t2 = -d1 + h1 - k3; t2 = (po<0.0)?2.0/t2:t2;
    float t3 =  d1 - h2 - k3; t3 = (po<0.0)?2.0/t3:t3;
    float t4 =  d1 + h2 - k3; t4 = (po<0.0)?2.0/t4:t4;
    float t = 1e20;
    if( t1>0.0 ) t=t1;
    if( t2>0.0 ) t=min(t,t2);
    if( t3>0.0 ) t=min(t,t3);
    if( t4>0.0 ) t=min(t,t4);
    return t;
}

vec3 torNormal( in vec3 pos, vec2 tor )
{
    return normalize( pos*(dot(pos,pos)-tor.y*tor.y - tor.x*tor.x*vec3(1.0,1.0,-1.0)));
}
vec3 sky(in vec3 rd) {
    return (vec3(0.0, 0.5, 0.3)*pow(fbm(1.0+rd*4.), 4.0));
}
const vec2 t = vec2(1 ,0.4);
vec4 getColor(in vec3 ro, in vec3 rd) {
    float d = tor(ro, rd, t); //depth to torus
    if (d < 0.0) { //calculate sky
        vec3 s = sky(rd);
        return vec4(0);
    }
    vec3 p = ro+rd*d; //position on torus
    vec3 n = torNormal(p, t); //normal
    vec3 op = p; //untransformed position on torus

    p /= vec2(t.x+t.y, t.y).xxy; //normalize coords between -1, 1

    //toroidal and poloidal coordinates from point on surface
    float y = (((length(vec2(length(vec2(n.x, n.y))-1.0, n.z)))));
    if (p.z < 0.0) y = -y;
    if (length(op.xy) < t.x) {
        y = -(y)-2.8;
    }
    vec2 z = (vec2((atan(p.x, p.y)+3.141592653589793)*0.15915494309189535, ((y+4.13)*0.18)))*res;

    //map game of life from buffer A to torus
    vec4 o = texelFetch(iChannel0, ivec2(ceil(z))+ivec2(1, 0), 0);
    vec3 c = mix(vec3((-dot(rd, n))*0.25), vec3(0.2, 0.2, 1)*10., o.x);
    if (o.x == 0.0) c += (30.-min(30., o.y))*vec3(1.0, 0.2, 0.2)*0.1;
    return vec4(c, o.x > 0.0 || o.y < 10.0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
	vec3 rd;
	vec3 ro;

    //AA and motion blur sampling
    for (int i = 0; i < 10; i += 1) {
        Camera(fragCoord, ro, rd, i);
        vec4 c = getColor(ro ,rd);
        fragColor += vec4(c);
    }
    fragColor *= 0.1;
    fragColor = max(fragColor, 0.0);
}

