void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    #if BLOOM
    vec3 b = vec3(0);
    int r = int(iResolution.x/20.);
    float rr = 1./float(r);
    for (int x = -r; x < r; x += 1) {
        vec4 s = texelFetch(iChannel0, ivec2(fragCoord)+ivec2(x, 0), 0);
        if (s.w > 0.0) {
            float ss = 1.-abs(float(x)*rr);
            b += (s.xyz*ss*ss)*0.1;
        }
    }
    fragColor = vec4((b)*rr*10., 1);
    #endif
}

