// Facebook: https://www.facebook.com/groups/theartofcode/
//
// You can use this shader as a template for ray marching shaders

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001
#define useAA false
#define S smoothstep
#define T iTime *0.25

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}
float anisotropicAA(vec3 p) {
    p *= -10.+ T; // # TIME
    vec3 p2 = p  *  -9.9 + T ; // # 10 ms TIME anisotropic anti-alias!
    vec3 p3 = p  *  -9.8 + T ; // # 20 ms TIME anisotropic anti-alias!
    return (dot(sin(p), cos(p.zxy)) +  dot(sin(p2), cos(p.zxy)) +  dot(sin(p3), cos(p.zxy)) );
}
float sdGyroid(vec3 p) {
    if( useAA == true) { return anisotropicAA(p); } 
    p *= -20.+ T; // # TIME
    return dot(sin(p), cos(p.zxy)) ;
}
float GetDist(vec3 p) {
    float box = sdBox(p, vec3(1));
    float gyroid = sdGyroid(p);
    return dot(box, gyroid);
    return gyroid;
    return box;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 3, -3);
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0),1.);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd);

    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        col = n * vec3(dif);
    }

   // col = pow(col, vec3(.3));	// gamma correction
//
   // fragColor += texelFetch(iChannel1,ivec2(fragCoord),0) +  vec4(col,1.0);

    //fragColor =  texelFetch(iChannel1,ivec2(fragCoord),0) + texelFetch(iChannel2,ivec2(fragCoord),0) + vec4(col,1.0);
    fragColor = vec4(col,1.0);
}

