void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = fragCoord/iResolution.xy;
    vec2 q = p - iMouse.xy / iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5 * sin(vec3(0,2,4) + iTime / 2.);

    float r = 0.4 + 0.1 * cos(atan(-p.x + 2., q.y / 2.) * 60.0 + 20.0 * -q.x + ((length(iMouse) / 50.) * 2.));

    float r1 = 0.2 + 0.1 * cos(atan(-p.x + 2., -p.y / 2.) * 5.0 + 20.0 * -q.x + iTime * 2.);
    float r2 = 0.4 + 0.1 * cos(atan(-p.x + 4., -q.y / 2.) * 10.0 + 20.0 * -q.x + iTime * 4.);
    float r3 = 0.6 + 0.1 * cos(atan(-p.x + 6., -q.y / 2.) * 20.0 + 20.0 * -q.x + iTime * 6.);

    col /= smoothstep(r / 100., r, length(q)) / .2;

    col.x /= smoothstep(r1 / 100., r1, length(q)) / .2;
    col.y /= smoothstep(r2 / 100., r2, length(q)) / .2;
    col.z /= smoothstep(r3 / 100., r3, length(q)) / .2;

    // Output to screen
    fragColor = vec4(col,1.0);
}

float Hash31(in vec3 p) {
     return fract(937.276 * cos(836.826 * p.x + 263.736 * p.y + 374.723 * p.z + 637.839));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 4.0;
    float time = iTime * 2.0;
    vec3 color = vec3(0.0);

    for (float i=-3.0; i <= 3.0; i += 1.25) {
        for (float j=-2.0; j <= 2.0; j += 1.25) {
            vec2 p = uv;

            float freq = fract(643.376 * cos(264.863 * i + 136.937)) + 1.0;
            vec2 pos = 5.0 * vec2(i, j) + vec2(sin(freq * (iTime + 10.0 * j) - i), freq * iTime);
            pos.y = mod(pos.y + 15.0, 30.0) - 15.0;
            pos.x *= 0.1 * pos.y + 1.0;
            p -= 0.2 * pos;

            float an = mod(atan(p.y, p.x) + 6.2831 / 3.0, 6.2831 / 6.0) - 6.2831 / 3.0;
            p = vec2(cos(an), sin(an)) * length(p);

            float sec = floor(time);
            float frac = fract(time);
            float flicker = mix(Hash31(vec3(i, j, sec)), Hash31(vec3(i, j, sec + 1.0)), frac);

            float rad = 25.0 + 20.0 * flicker;
            float br = 250.0 * pow(1.0 / max(10.0, rad * (sqrt(abs(p.x)) + sqrt(abs(p.y))) + 0.9), 2.5);
            float rand = fract(847.384 * cos(483.846 * i + 737.487 * j + 264.836));
            if (rand > 0.5) color += mix(vec3(br, 0.4 * br, 0.0), vec3(1.0), br);
            else color += mix(vec3(0.0, 0.0, 0.6 * br), vec3(1.0), br);

            color *= 0.955 + 0.1 * flicker;
        }
    }

    fragColor = vec4(color, 1.0);
}

/********************************************************

I don't know if this algorithm is new (probably not),
but if it is
    or you use my implementation
    or any modified version of it,
[PLEASE GIVE CREDIT TO ME] FOR DEVELOPING THE ALGORITHM

Follow-up: https://www.shadertoy.com/view/flGcWz

********************************************************/





// CONSTANTS
#define E cosh(1.0) + sinh(1.0)
#define PI acos(-1.0)

// SDF SETTINGS
#define MAX_SDF_ITERATIONS (iTime * 5.)

#define FAST


float f(float x)
{
    //return sin(x + iTime) + sin(x - iTime);
    return 15.0 * (sin(x * 2.0) + sin(1.1 * (x + PI * 0.5)));  // Rather steep function - good example
    //return tan(x);  // Good example for the limits of this algorithm - the SDF for tan(x) takes a lot of iterations for it to converge because tan(x) is so steep
}

// WITH COMMENTS (scroll down a bit for version without comments)
float sdf_by_Spi3lot(vec2 p)
{
    float y0 = f(p.x);
    float minDist = p.y - y0;

    // Above function -> 1.0
    // Exactly on function -> 0.0
    // Below function -> -1.0
    float sgn = sign(minDist);

    // Make minDist non-negative (probably faster than abs because I already have the sign)
    minDist *= sgn;

    // i=1 and not i=0 (at the start of the loop)
    //     because I count the first minDist initialization as the first iteration
    //   AND
    // Using < and not <=
    //     because when i == MAX_SDF_ITERATIONS then dx == minDist and
    //     when dx (>)= minDist then the distance can only be >= the current minDist
    //     which means we can prune this iteration where i == MAX_SDF_ITERATIONS
    for (float i = 1.; i < MAX_SDF_ITERATIONS; i++)
    {
        float dx = minDist * i / MAX_SDF_ITERATIONS;

        float xL = p.x - dx;
        float yL = f(xL);

        float xR = p.x + dx;
        float yR = f(xR);

        float distL, distR, mini;

        // THE PRUNING SECTION CAN BE COMMENTED OUT
        // BECAUSE THE PERFORMANCE IS WORSE THAN WITHOUT IT

        /* (put/remove character between * and / for commenting out) -> * /
        // vvvvvvvvvv PRUNING vvvvvvvvvv
        bool pruneL = (yL < y0);  // Distance to left must be greater than p.y-y0 -> PRUNE
        bool pruneR = (yR < y0);  // Distance to right must be greater than p.y-y0 -> PRUNE

        if (pruneL && pruneR)
        {
            continue;  // Nothing changed
        }

        else if (pruneL)
        {
            distR = distance(p, vec2(xR, yR));
            mini = distR;  // Minimum for sure
        }

        else if (pruneR)
        {
            distL = distance(p, vec2(xL, yL));
            mini = distL;  // Minimum for sure
        }

        else
        // ^^^^^^^^^^^^ PRUNING ^^^^^^^^^^^^
        /* */
        {
            distL = distance(p, vec2(xL, yL));
            distR = distance(p, vec2(xR, yR));

            mini = min(distL, distR);
        }


        if (mini < minDist)
        {
        #ifndef FAST
            // Resetting i - The crucial difference which slows down everything by A LOT
            // but also increases the speed of convergence of the minDist by quite a bit.

            // BUT the total amount of iterations will still increase by such a big amount
            // that this "(actually not) optimized" method where i is reset
            // becomes COMPLETELY USELESS compared to the FAST one where i is NOT reset
            i = 1.;
        #endif

            // The algorithm works very well even without resetting i (resetting i whenever
            // a smaller minDist was found) -> because when minDist gets updated, dx will
            // get smaller which means p.x+dx will be closer to p.x and when i is reset
            // we get closer and closer to p.x everytime we find a new and smaller distance,
            // but as we get closer to p.x, the distance from p to the function
            // approaches p.y-f(p.x) which is exactly what we DO NOT want
            // since we could just scrap the iteration part
            // and just return p.y-f(p.x) if this was what we wanted.
            minDist = mini;
        }
    }

    return sgn * minDist;
}


// WITHOUT COMMENTS AND UNNECESSARY CODE (scroll up a bit for version with comments)
// copy/paste ready (name of function can be changed of course,
// but if you remove my name please give credit to me in a comment or so)
float sdf_by_Spi3lot_pure_no_comments(vec2 p)
{
    float y0 = f(p.x);
    float minDist = p.y - y0;
    float sgn = sign(minDist);
    minDist *= sgn;

    for (float i = 1.; i < MAX_SDF_ITERATIONS; i++)
    {
        float dx = minDist * i / MAX_SDF_ITERATIONS;

        float xL = p.x - dx;
        float yL = f(xL);

        float xR = p.x + dx;
        float yR = f(xR);

        float distL = distance(p, vec2(xL, yL));
        float distR = distance(p, vec2(xR, yR));
        float mini = min(distL, distR);

        minDist = min(minDist, mini);
    }

    return sgn * minDist;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    vec2 pos = 100.0 * (uv - vec2(0.0, 0.5));
    float dist = sdf_by_Spi3lot(pos);
