#define MAX_STEPS 400
#define MAX_DIST 100.
#define SURF_DIST .001

#define FK(k) floatBitsToInt(k*k/7.)^floatBitsToInt(k)
float hash(float a, float b) {
    int x = FK(a), y = FK(b);
    return float((x*x+y)*(y*y-x)-x)/2.14e9;
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

vec3 face(vec3 p) {
     vec3 a = abs(p);
     return step(a.yzx, a.xyz)*step(a.zxy, a.xyz)*sign(p);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

vec3 getRo() {
    vec2 m = iMouse.xy/iResolution.xy;

    //was 0.15
    float t = 0.04 * iTime;
    float r = 3.4;
    vec3 ro = vec3(r * cos(t), 1.58, r * sin(t));
    //ro.yz *= Rot(-m.y*3.14+1.);
    //ro.xz *= Rot(-m.x*6.2831);
    return ro;
}

float test(vec2 uv) {
    float t;
    float m = 1.;
    for (int i = 0; i < 6; i++) {
        t += m * texture(iChannel0, uv)[i % 3];
        m *= 0.3;
        uv *= 3.;
    }
    return t;
}

float GetDist(vec3 p) {
    float d = p.y;

    vec2 uv = 0.02 * p.xz;
    float t = test(uv);
    d -= t;
    // falloff from center gives feeling of roundness like a planet
    // looks worse?
    d += 0.025 * (length(p.xz) - 1.);
    return 0.5 * d;
}

float RayMarch(vec3 ro, vec3 rd, float z) {

    float dO=0.;
    float s = sign(z);
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if (s != sign(dS)) { z *= 0.5; s = sign(dS); }
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro = getRo();

    vec3 rd = GetRayDir(uv, ro, vec3(0,1.15,0), 4.); //was vec3(0), 1.
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd, 1.);

    vec3 e = vec3(0.5);
    vec3 col2 = pal(-0.15, e, e, e, 0.5 * vec3(0,1,2)/3.);

    vec3 p = ro + rd * d;
    if(d<MAX_DIST) {     
        vec3 n = GetNormal(p);

        float dif = dot(n, normalize(vec3(0,1.5,3)))*.6+.4;
        col = vec3(dif);

        vec3 c = texture(iChannel0, p.xz).rgb;
      //  col *= c;
        col *= p.y-0.25;
        vec2 uv = 0.02 * p.xz;
        float t = test(uv);
        col *= t;
        vec3 e = vec3(0.5);
        col *= pal(1.15 + t, e, e, e, 0.5 * vec3(0,1,2)/3.);

        float fres = pow(1. + dot(rd, n), 1.);
        col = mix(col, col2, 0.04 * fres);

        float sc = 0.25;

        vec3 c1 = texture(iChannel1, sc * (0.5 + 0.5 * p.xy)).rgb;
        vec3 c2 = texture(iChannel1, sc * (0.5 + 0.5 * p.yz)).rgb;
        vec3 c3 = texture(iChannel1, sc * (0.5 + 0.5 * p.zx)).rgb;

        n = abs(n);
        vec3 c4 = n.z * c1 + n.x * c2 + n.y * c3;
        col *= c4 * 1.3;

    }

    // I like 
    col *= 0.82 + 0.18 * thc(1.,30. * p.y) * exp(-0.2 * p.y);

    col = mix(col, col2, 1.-exp(-0.05 * length(p.xz)));
    vec3 col3 = pow(col, vec3(.4545));	// gamma correction
    col = col3;
    //col = mix(col, col3, step(fract(10. * uv.x), 0.1));
    fragColor = vec4(col,1.0);
}

