void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.xy;

    float sc = 14. + 0.25 * cos(3.5 * uv.x + iTime);
    vec2 ipos = floor(sc * uv) + 0.;
    vec2 fpos = fract(sc * uv) - 0.;

    // m determines what new cell the old cell lies in
    // 3 -> small
    // 0 -> tl big, 2 -> tr big, 4 -> bl big, 1 -> br big
    float m = mod(2. * ipos.x - ipos.y, 5.);

    // id = 2 if small cell, id = 1 if big cell (used to make outlines same size)
    float id = 2.;
    vec2 o = vec2(0);

    if (m != 3.) { fpos *= 0.5;  id = 1.; }    
    if (m == 2.)      o = vec2(1,0); // top right
    else if (m == 4.) o = vec2(0,1); // bottom left
    else if (m == 1.) o = vec2(1);   // bottom right

    // if in big cell, halve fpos + translate
    fpos += 0.5 * o - 0.5;
    ipos -= o;

    float h = 2. * pi * h21(ipos);

    float c = cos(h + pi * id + iTime);    
    float d = mix(mlength(fpos), length(fpos), 0.5 + 0.5 * c);

    float k = 20. / iResolution.y;
    float s = 1. - smoothstep(-k, k, -d + 0.5 - (0.1 + 0.05 * c) * id);// - length(fpos);

    vec3 e = vec3(0.5);
    vec3 col = s * pal(h + 0.05 * iTime, e, e, e, 0.5 * vec3(0., 0.33, 0.66));
    col += 0.14;

    fragColor = vec4(col,1.0);
}

