float pie(vec3 q, float t, float r, float a){
    vec2 p = q.xy,
         c = vec2(sin(t), cos(t));
    p *= rot(a * -6.28);
    p.x = abs(p.x);
    float l = length(p),
          m = length(p-c*clamp(dot(p,c),0.0,r)),
          d = max(l - r,m*sign(c.y*p.x-c.x*p.y));
    return max(d, r - l - 0.2);
}

float logo(vec3 p) {
	p.y += .05;
	p.z += .2;
	p.xz *= rot(0.2);
	p.yz *= mat2(cos(.8 + vec4(0, 11, 33, 0)));

	// Inner circle.
	float l = length(p.xy),
	      d = abs(l - .3) - .1;
	MN(pie(p, .4, .55, .625));

	// Middle circle.
	MN(abs(l - .8) - .05);
	MN(pie(p, 1., .85, .05));
	MN(pie(p, 1., .85, .4));
	MN(pie(p, .6, .85, .8));
	MN(pie(p, .3, .72, .4));

	// Outer circle.
	MN(abs(l - 1.2) - .052);
	MN(pie(p, .6, 1.25, .7));
	MN(pie(p, .07, 1.3, .63));
	MN(pie(p, .2, 1.25, .5));

	// Crop depth.
	return smin(d - 0.01, abs(p.z) - .08, -.04) - .002 * n31(p * 40.);
}

