void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = 0.15 * iTime;

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.xy;
    vec2 ouv = uv;
    uv.y += 0.12 * time;
    //time += uv.x * 4.;
    float sc = 10.;
    vec2 ipos = floor(sc * uv) + 0.5;
    vec2 fpos = fract(sc * uv) - 0.5;

    float h = h21(ipos);
    float h2 = 2. * pi * h21(ipos + 2.);

    vec2 p = fract(fpos - 4. * thc(2., vec2(h2, 2. * pi * h) + time)) - 0.5;

    float d = length(p);
    float k = 1. + h;
    float s = smoothstep(-k, k, -d + 0.2);

    vec2 pw = vec2(5. + 4. * thc(20., 8. * d + h2 + time));
    vec2 b = pow(1. - 4. * fpos * fpos, pw);

    vec3 e = vec3(1.5);
    // 50% chance of having white in it, .5+.5*cos gives white to some of it
    float cp = step(0.5 + 0.5 * step(h, 0.5) * cos(8. * d), 0.5);
    vec3 col = pal(h2 + 0.25 * d, e, e, e, cp * vec3(0.,0.33,0.66));
    col *= b.x * b.y * s; // * step(h21(ipos + 1.), 0.5); 
    col += 0.03;

    col *= smoothstep(-0.01, 0.01, -mlength(ouv) + 0.2);
    //col *= step(mlength(uv), 10.);

    fragColor = vec4(col,1.0);
}

