// Texturing
// Eric Galin

const int Steps = 600;      // Number of steps
const float Epsilon = 0.01; // Marching epsilon
const float k = 8.0;


// Structure for objects
// v : Field value
// i : Texture index
struct V {
    float v; 
    int i;
};

// Structure for texture
// c : Color
// s : Specular
struct T {
vec3 c;
float s;
};

// Primitive functions -----------------------------------------------------------------------------------

// Sphere 
// p : point
// c : center 
// r : radius
V Sphere(vec3 p, vec3 c,float r,int index)
{
  return V(length(p-c)-r,index);
}


V Union(V a,V b)
{
    if (a.v<b.v)
        return V(a.v,a.i);
    else 
    {
        return V(b.v,b.i);
    }
}

// Intersection, preserve the index of first object
// a, b : field function of left and right sub-trees
V Intersection(V a,V b)
{
    if (a.v>b.v)
    {
        return V(a.v,a.i);
    }
    else 
    {
        return V(b.v,a.i);
    }
}

// Terrain -----------------------------------------------------------------------------------------------

float Terrasement(vec2 p, vec2 c, float h,float ri, float re)

{
    float d = length(p-c);
    float m;
    if(d<ri)
    {
        m=0.0;
    }
    else if(d>re)
    {
        m=1.0;
    }
    else
    {
        m = smoothstep(ri,re,d);
    }
    return mix(5.0f,h,m);
}

float plateau(float h, float e, float a)
{
    if(h<a)
    {
        return h;
    }
    if(h>e+a)
    {
        return h;
    }
    else
    {
        float u= (h-e)/((e+a)-e);
        h = e+a*u;
        return h;
    }

}

float Ridge(float n)
{
    return 1.0-2.0*abs(n);

}

float fbm(vec2 p, float l, int o, float a)
{
    float t=0.0;
    for(int i=0;i<o;i=i+1)
    {
        t += a * Noise(p/l);
        a*=0.5;
        l*=0.5;
    }
    return 50.0+Ridge(t);


}



float hetefdmbridge(vec2 p , float l, float a, int r, float amin, float amax)
{
    float t = a * Ridge(10.0);
    for(int i = 1; i<10; i = i+1)
    {
        a*= 0.5;
        l*=0.5;
        float att = smoothstep(amin,amax,t);
        t+= att*a*Ridge(10.0);
    }
    return t;
}

// Terrain heightfield
// x : Position in the plane
float Terrain( in vec2 p )
{
    float h = fbm(p,100.0,4,150.0);
    return Terrasement(p.xy,vec2(0.0,0.0), h,100.0,260.0);

} 


V Difference(V a,V b)
{
    if (a.v>-b.v)
    {
        return V(a.v,a.i);
    }
    else 
    {
        return V(-b.v,b.i);
    }
}


// Implicit surface defining the terrain
// p : Point

V ImplicitTerrain(in vec3 p)
{
    float z = Terrain( p.xy );
    float h = p.z - z; 
    V ret;
    ret.v = h;
    if(z>-90.0)
    {
        ret.i = 2;
    }
    else
    {
        ret.i = 1;
    }
    return ret;
}

// Potential field of the object
// p : point
V object(vec3 p)
{
  V u;
  u = Union(
      Union(
      Difference(
      ImplicitTerrain(p),
      Sphere(p,vec3(0.0,0.0,0.0),90.0,0)),
      Sphere(p,vec3(0.0,1000.0,(cos(iTime)*400.0-250.0)),65.0,3)),
      Sphere(p,vec3(0.0,-1000.0,(-cos(iTime)*400.0-250.0)),65.0,4));

  return u;
}
// Analysis of the scalar field --------------------------------------------------------------------------

// Calculate object normal
// p : point
vec3 ObjectNormal(in vec3 p )
{
  float eps = 0.001;
  vec3 n;
  V vp = object(p);
    float v = vp.v;
  n.x = object( vec3(p.x+eps, p.y, p.z) ).v - v;
  n.y = object( vec3(p.x, p.y+eps, p.z) ).v - v;
  n.z = object( vec3(p.x, p.y, p.z+eps) ).v - v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// e : Maximum distance 
// h : hit
// s : Number of steps
float SphereTrace(vec3 o, vec3 u, float e,out bool h,out int s)
{
  h = false;

    // Start at the origin
    float t=0.0;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = object(p).v;
    // Hit object
      if (v < 0.0)
      {
          s=i;
          h = true;
          break;
      }
      // Move along ray
      t += max(Epsilon,v/k);
      // Escape marched too far away
      if (t>e)
      {
          break;
      }
  }
  return t;
}

// Background color
// d : Ray direction
vec3 background(vec3 d)
{
  if(cos(iTime)<-0.5)
  {
       d.z *= cos(iTime)*-2.0-1.0;
      return mix(vec3(0.22,d.z*100.0*0.3,0.4),vec3(0.0,0.0,d.z*0.4), d.z*0.5+0.5);
  } 
  d.z = cos(iTime)*(d.z*-4.0 +1.3);
  return vec3(d.z*1.0,d.z*0.607,d.z*0.4);
}


// Direct lighting
float Light(vec3 p,vec3 n)
{
   // point light
  const vec3 lp = vec3(0.0, 0.0, 3000.0);

  vec3 l = normalize(lp - p);

  // Phong shading
  float diff = clamp(dot(n, l),0.0,1.0);

    bool h;
    int s;
    float t=SphereTrace(p+0.1*n,l,100.0,h,s);
if (!h)
    {
     return diff;
    }
    return 0.0; 
}


// Shading and lighting ---------------------------------------------------------------------------

T Texture0(in vec3 p)
{
  return T( mix(vec3(0.52,0.75,0.91),vec3(0.0,1.0,1.0),Fbm(p/50.0)),0.0);

}

T Texture1(in vec3 p)
{
   return T( mix(vec3(1.0,1.0,1.0),vec3(0.0,0.0,0.0),Fbm(p/50.0)),0.0);
}

T Texture2(in vec3 p)
{
  return T(vec3(1.0,1.0,1.0),0.0);
}

T Texture3(in vec3 p)
{
    vec3 f = vec3(1.0,1.0,0.094);
    vec3 c = vec3(1.0,1.0,1.0);
    vec3 d = f + (Noise(p)*c/2.0);
    return T(d,0.0);       
}

T Texture4(in vec3 p)
{
    vec3 f = mix(vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),Fbm(p/50.0));
    vec3 c = vec3(0.5,0.5,0.5);
    vec3 d = f + (Noise(p)*c/2.0);
    return T(d,0.0);            
}

// Compute texture 
// p : Point
// n : Normal
T Color(in vec3 p,in vec3 n)
{
    V vp= object(p);
    if (vp.i==0){

        return Texture0(p);
    }
    else if (vp.i==1)
    {
        return Texture1(p);
    }
    else if (vp.i==2)
    {
        return Texture2(p);
    }
    else if (vp.i==3)
    {
        return Texture3(p);
    }
    else if (vp.i==4)
    {
        return Texture4(p);
    }
}

// Shadowing
// p : Point
// n : Normal
// l : Light direction
float Shadow(vec3 p,vec3 n,vec3 l)
{
    bool h;
    int s;
    float t = SphereTrace(p,l,0.0,h,s);
    if (!h)
    {
     return 1.0;
    }
    return 0.0; 
}

// Shading and lighting
// p : Point
// n : Normal at point
// e : Eye direction
vec3 Shade(vec3 p, vec3 n, vec3 e)
{
     // Point light
    const vec3 lp = vec3(500.0, 500.0, 500.0);

    // Light direction to point light
    vec3 l = normalize(lp - p);

    T tex=Color(p,n);

    // Ambient color
    vec3 ambient = 0.5*tex.c;

    // Shadow computation
    float s = Shadow(p+0.01*n,n,l);

// Phong diffuse
    vec3 diffuse = 0.5  * clamp(dot(n, l),0.0,1.0) * tex.c;

    // Specular
    vec3 r = reflect(e,n);
    vec3 specular = tex.s * pow(clamp(dot(r,l),0.0,1.0),28.0) * vec3(1.0,1.0,1.0);
    vec3 c = ambient + s * (diffuse + specular);
    return c;
}

// Picture in picture ------------------------------------------------------------------------------

// Shading according to the number of steps in sphere tracing
// n : Number of steps
vec3 ShadeSteps(int n)
{
   float t=float(n)/(float(Steps-1));
   return 0.5+mix(vec3(0.05,0.05,0.5),vec3(0.65,0.39,0.65),t);
}

// Picture in picture
// pixel : Pixel
// pip : Boolean, true if pixel was in sub-picture zone
vec2 Pip(in vec2 pixel, out bool pip)
{
    // Pixel coordinates
    vec2 p = (-iResolution.xy + 2.0*pixel)/iResolution.y;
   if (pip==true)
   {    
    const float fraction=1.0/4.0;
    // Recompute pixel coordinates in sub-picture
    if ((pixel.x<iResolution.x*fraction) && (pixel.y<iResolution.y*fraction))
    {
        p=(-iResolution.xy*fraction + 2.0*pixel)/(iResolution.y*fraction);
        pip=true;
    }
       else
       {
           pip=false;
       }
   }
   return p;
}


// Image
void mainImage( out vec4 color, in vec2 pxy )
{
    // Picture in picture on
    bool pip=true;

   // Pixel
     vec2 pixel=Pip(pxy, pip);

    // Mouse
    vec2 m=iMouse.xy/iResolution.xy;

    // Camera
    vec3 ro,rd;
    Ray(m,pixel,ro,rd);

  // Trace ray

  // Hit and number of steps
  bool hit;
  int s;

  float t = SphereTrace(ro, rd, 2000.0,hit, s);

    // Position 
    vec3 pt = ro + t * rd;

  // Shade background
  vec3 rgb = background(rd);

  if (hit)
  {
    // Compute normal
    vec3 n = ObjectNormal(pt);

    // Shade object with light
    rgb = Shade(pt, n, rd);
  }

  // Uncomment this line to shade image with false colors representing the number of steps
  if (pip==true)
  {
      rgb = ShadeSteps(s); 
  }


  color=vec4(rgb, 1.0);
}

common_code:
// Texturing and noise 

// Hashing function
// Returns a random number in [-1,1]
// p : Vector in space
float Hash(in vec3 p)  
{
    p  = fract( p*0.3199+0.152 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

// Procedural value noise with cubic interpolation
// x : Point 
// Hashing function
// Returns a random number in [-1,1]
// p : Vector in the plane
float Hash(in vec2 p)  
{
    p  = fract( p*0.3199+0.152 );
	p *= 17.0;
    return fract( p.x*p.y*(p.x+p.y) );
}


// Procedural value noise with cubic interpolation
// x : Point 
float Noise(in vec2 p)
{
    vec2 i = floor(p);
    vec2 f = fract(p);

    f = f*f*(3.0-2.0*f);
    // Could use quintic interpolation instead of cubic
    // f = f*f*f*(f*(f*6.0-15.0)+10.0);

    return mix(mix( Hash(i+vec2(0,0)), 
                        Hash(i+vec2(1,0)),f.x),
                   mix( Hash(i+vec2(0,1)), 
                        Hash(i+vec2(1,1)),f.x),f.y);
}

float Noise(in vec3 p)
{
    vec3 i = floor(p);
    vec3 f = fract(p);

    f = f*f*(3.0-2.0*f);
    // Could use quintic interpolation instead of cubic
    // f = f*f*f*(f*(f*6.0-15.0)+10.0);

    return mix(mix(mix( Hash(i+vec3(0,0,0)), 
                        Hash(i+vec3(1,0,0)),f.x),
                   mix( Hash(i+vec3(0,1,0)), 
                        Hash(i+vec3(1,1,0)),f.x),f.y),
               mix(mix( Hash(i+vec3(0,0,1)), 
                        Hash(i+vec3(1,0,1)),f.x),
                   mix( Hash(i+vec3(0,1,1)), 
                        Hash(i+vec3(1,1,1)),f.x),f.y),f.z);
}

// Compute the distance to the Voronoi boundary
// x : Point
// Return (closest distance, second closest, cell id)

float Fbm(in vec3 p)
{
    return Noise(p)/2.0+Noise(p*2.0)/4.0+Noise(p*4.0)/8.0+Noise(p*8.0)/16.0;
}


vec3 Voronoi( in vec3 x )
{
    vec3 p = floor( x );
    vec3 f = fract( x );

	float id = 0.0;
    vec2 res = vec2( 100.0 );
    for( int k=-1; k<=1; k++ )
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec3 b = vec3( float(i), float(j), float(k) );
        vec3 r = vec3( b ) - f + Hash( p + b );
        float d = dot( r, r );

        if( d < res.x )
        {
			id = dot( p+b, vec3(1.0,57.0,113.0 ) );
            res = vec2( d, res.x );			
        }
        else if( d < res.y )
        {
            res.y = d;
        }
    }

    return vec3( sqrt( res ), abs(id) );
}

// Camera -----------------------------------------------------------------------

// Rotation matrix around z axis
// a : Angle
mat3 Rz(float a)
{
    float sa = sin(a); float ca = cos(a); 
    return mat3(ca,sa,0.0,    -sa,ca,0.0,  0.0,0.0,1.0);
}

// Compute the ray
//      m : Mouse position
//      p : Pixel
// ro, rd : Ray origin and direction
void Ray(in vec2 m, in vec2 p,out vec3 ro,out vec3 rd)
{
    float a = 3.0*3.14*m.x;
   	float le = 3.8;

    ro=vec3(1000.0,0.0,200.0);
    ro*=Rz(3.0*3.14*m.x); 

    vec3 ta = vec3(0.0,0.0,0.0);
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,0.0,1.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	rd = normalize( p.x*uu + p.y*vv + le*ww );
}

