    uv += 0.3 * iTime;
    float d = 0.001;
    return 0.5 * (vec2(
        warp21(uv + vec2(d, 0.0), 1.0)
             - warp21(uv - vec2(d, 0.0), 1.0),
            warp21(uv + vec2(0.0, d), 1.0) - warp21(uv - vec2(0.0, d), 1.0)
        )) / d;
