    // Position and rotation
    p = rotate2D(p, PI * -.5);
    p.x -= .5;

    // Make egg shape
    vec3 egg_shape = vec3(egg(p, 1., .7));

    // Output to screen
    fragColor = vec4(egg_shape,1.0);
}

const int MAX_STEPS = 256;
const float MIN_DIST = 0.001;
const float MAX_DIST = 1000.0;
const vec3 PLANET_COLOR = vec3(118., 89., 89.) / 255.;
const vec3 SEA_COLOR = vec3(115., 168., 245.) / 255.;

struct RayMarch {
    float totalDist;
    float currentDist;
    int steps;
};

float mod289(float x){return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec4 mod289(vec4 x){return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec4 perm(vec4 x){return mod289(((x * 34.0) + 1.0) * x);}

float noise(vec3 p){
    vec3 a = floor(p);
    vec3 d = p - a;
    d = d * d * (3.0 - 2.0 * d);

    vec4 b = a.xxyy + vec4(0.0, 1.0, 0.0, 1.0);
    vec4 k1 = perm(b.xyxy);
    vec4 k2 = perm(k1.xyxy + b.zzww);

    vec4 c = k2 + a.zzzz;
    vec4 k3 = perm(c);
    vec4 k4 = perm(c + 1.0);

    vec4 o1 = fract(k3 * (1.0 / 41.0));
    vec4 o2 = fract(k4 * (1.0 / 41.0));

    vec4 o3 = o2 * d.z + o1 * (1.0 - d.z);
    vec2 o4 = o3.yw * d.x + o3.xz * (1.0 - d.x);

    return o4.y * d.y + o4.x * (1.0 - d.y);
}


float displacement(vec3 p) {
    float d = 0.0;
    d += noise(p) * 0.8;
    d += noise(p * 2.0) * 0.4;
    d += noise(p * 4.0) * 0.2;
    d += noise(p * 8.0) * 0.1;

    return d - 0.75;
}

float sphereSDF(vec3 p, vec3 c, float r)
{
    return length(p - c) - r;
}

float planetSDF(vec3 p)
{
    float planet = sphereSDF(p, vec3(0.), 10.);
    return planet + displacement(p);
}

float seaSDF(vec3 p)
{
    return sphereSDF(p, vec3(0.), 10.);
}

RayMarch planet_ray_march(vec3 ro, vec3 rd)
{
    float d = 0.0;
    for(int i = 0; i < MAX_STEPS; i++)
    {
        vec3 p = ro + rd * d;
        float sdf = planetSDF(p);
        if(abs(sdf) < MIN_DIST || abs(sdf) > MAX_DIST)
        {
            return RayMarch(d, sdf, i);
        }
        d += sdf * 0.707107;
    }
    return RayMarch(d, MAX_DIST, MAX_STEPS);
}

RayMarch sea_ray_march(vec3 ro, vec3 rd)
{
    float d = 0.0;
    for(int i = 0; i < MAX_STEPS; i++)
    {
        vec3 p = ro + rd * d;
        float sdf = seaSDF(p);
        if(sdf < MIN_DIST || sdf > MAX_DIST)
        {
            return RayMarch(d, sdf, i);
        }
        d += sdf;
}
return RayMarch(d, MAX_DIST, MAX_STEPS);
}


vec3 rotateY(vec3 p, float angle)
{
    mat3 ry = mat3(
    cos(angle), 0., sin(angle),
    0., 1., 0.,
    -sin(angle), 0, cos(angle)
    );

    return ry * p;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //rotate the camera
    float theta = iTime / 10.;

    vec3 ro = vec3(25. * sin(theta), 0., -25. * cos(theta) );
    vec2 uv = fragCoord / iResolution.xy;
    uv = uv * 2. - 1.;
    vec3 rd = vec3(vec2(uv.x, uv.y / iResolution.x * iResolution.y), 1.);

    rd = rotateY(rd, theta);

    //find the distance between the camera and the planet / sea
    RayMarch planet = planet_ray_march(ro, rd);
    RayMarch sea = sea_ray_march(ro, rd);

    vec3 planetColor = step(planet.currentDist, MIN_DIST) * PLANET_COLOR;

    vec3 planetHighlight = vec3(step(30., float(planet.steps)));

    //planetColor += planetHighlight;

    vec3 seaColor = step(sea.currentDist, MIN_DIST) * SEA_COLOR;
    vec3 seaDepth = vec3(sea.totalDist - planet.totalDist) * step(sea.currentDist, MIN_DIST);

    seaColor += seaDepth * 0.5;

    vec3 color = sea.totalDist < planet.totalDist ? seaColor : planetColor;

    //color = vec3(planet.totalDist - sea.totalDist);

    fragColor = vec4(color, 1.0);
}

