// CONSTANTS
#define PI 3.1415926535

// RAY MARCHING SETTINGS
#define EPSILON 0.001
#define MAX_STEPS 100

// MANDELBULB SETTINGS
#define POWER 2.0
#define BAILOUT 2.0
#define MAX_ITERATIONS 50

// LIGHT SETTINGS
#define LIGHT_ROTATION_SPEED 1.0
#define LIGHT_ANGLE iTime * LIGHT_ROTATION_SPEED
#define LIGHT 10.0 * vec3(cos(LIGHT_ANGLE), sin(LIGHT_ANGLE), -1)
//#define LIGHT 100.0 * vec3(cos(iTime), 0, sin(iTime))

// ZOOM
#define ZOOM 1.0

// FOV (leave as is for best results)
#define FOVX 0.4 * PI / ZOOM
#define FOVY FOVX * iResolution.y / iResolution.x

// GLOW EFFECT
#define GLOW_EFFECT

// 3D ANAGLYPH (RED-CYAN)
//#define ANAGLYPH
#define ANAGLYPH_OFFSET vec3(0.05, 0.0, 0.0)


float iterations = -1.0;

// http://blog.hvidtfeldts.net/index.php/2011/09/distance-estimated-3d-fractals-v-the-mandelbulb-different-de-approximations/
float sdf(vec3 pos)
{
    vec3 z = pos;
	float dr = 1.0;
    float r;

    for (int i = 0; i < MAX_ITERATIONS; i++) {
		r = length(z);

		if (r > BAILOUT) {
            iterations = float(i);
            break;
        }

		// convert to polar coordinates
		float theta = POWER * acos(z.z / r) - PI * 0.5;
		float phi = POWER * atan(z.y, z.x);
		dr = pow(r, POWER - 1.0) * POWER * dr + 1.0;

		// scale and rotate the point
		float zr = pow(r, POWER);

		// convert back to cartesian coordinates
		z = zr * vec3(sin(theta)*cos(phi), sin(phi)*sin(theta), cos(theta));
		z += pos;
	}

	return 0.5 * log(r) * r / dr;
}

vec3 GetNormal(vec3 p)
{
    const float eps = 5.0;  // The larger, the better (for some reason)
    const vec2 h = vec2(eps, 0);

    vec3 a = vec3( sdf(p - h.xyy),
                   sdf(p - h.yxy),
                   sdf(p - h.yyx) );

    vec3 b = vec3( sdf(p + h.xyy),
                   sdf(p + h.yxy),
                   sdf(p + h.yyx) );

    return normalize(b - a);
}

vec3 GetLight(vec3 pos)
{
    return normalize(LIGHT - pos);
}



vec4 march(vec3 ro, vec3 rd)
{
    rd = normalize(rd);
    float minDist = 1.0 / 0.0;  // Used for glow effect

    // Marching to the Mandelbulb
    for (int i = 0; i < MAX_STEPS; i++)
    {
        float dist = sdf(ro);

        if (dist < minDist)
            minDist = dist;

        if (dist <= EPSILON)
        {
            vec3 normal = GetNormal(ro);
            vec3 ro2Light = GetLight(ro);

            float light = clamp(dot(normal, ro2Light), 0.0, 1.0);
            float ambientExposure = 1.0;

            ro -= rd * EPSILON * 2.0;

            // Ambient Occlusion
            //   Marching to the light source
            //   and checking for collisions on the way
            for (int j = 0; j < MAX_STEPS; j++)
            {
                float distLightSource = length(LIGHT - ro);
                if (distLightSource <= EPSILON)
                {
                    ambientExposure = 1.0;
                    break;
                }

                float distMandel = sdf(ro);
                if (distMandel <= EPSILON)
                {
                    ambientExposure = 0.5;
                    break;
                }

                ro += ro2Light * min(distLightSource, distMandel);
            }

            vec4 col = vec4(1);
            //vec4 col = vec4(iterations);
            //col = cos(col);

            return light * ambientExposure * col;
        }

        ro += rd * dist;
    }

#ifdef GLOW_EFFECT
    return vec4(1e-3 / minDist);  // Glow
#else
    return vec4(0.0, 0.0, 0.0, 1.0);
#endif
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Normalized pixel coordinates (from -0.5 to 0.5)
    vec2 norm = uv - 0.5;

    vec2 fov = vec2(FOVX, FOVY);
    vec3 ro = vec3(LIGHT.xy / 20.0, -4);
    vec3 rd = vec3(norm * fov, 1.0);  //vec3 rd = vec3(norm * fov, ZOOM);
    vec4 col;

#ifdef ANAGLYPH
    vec4 red = vec4(1,0,0,1) * march(ro + ANAGLYPH_OFFSET, rd);
    vec4 cyan = vec4(0,1,1,1) * march(ro - ANAGLYPH_OFFSET, rd);
    col = red + cyan;
#else
    col = march(ro, rd);
#endif

    // Output to screen
    fragColor = col;
}

// Texturing
// Eric Galin

const float View = 2800.0;  // View distance
const float Epsilon = 0.01; // Marching epsilon
const int Steps = 400;      // Number of steps

// Lipschitz constant
const float K=1.0;

// Structure for objects
// v : Field value
// i : Texture index
struct V {
    float v; 
    int i;
};

// Structure for texture
// c : Color
// s : Specular
struct T {
    vec3 c;
    float s;
};

vec3 Translation(vec3 p, vec3 T){
    return p - T;
}

// Terrain -----------------------------------------------------------------------------------------------

float plateau(float h,float altE,float altA){
    if((h<altE) || (h>altE+altA))return h;
    float u=(h-altE)/((altE+altA)-altE);
    float c= u*u;
    return altE+altA*c;
}

// Terrain heightfield
// x : Position in the plane
float Terrain( in vec2 p )
{
	//return 50.*sin(p.x/100.0)*cos(p.y/100.0);
    //return Fbm(p,50.,30.0,1);
    return 3.*TurbulenceRidge(p,500.,50.,7,-100.,100.);
} 

// Implicit surface defining the terrain
// p : Point
V ImplicitTerrain(in vec3 p,int index)
{
    p = Translation(p,vec3(-1500.,900.,0.));//nécessaire pour eviter la symétrie de mon coté, en plus l'endroit est sympa
    float z = Terrain( p.xy );
    z = plateau(z,0.,70.);
    float h = p.z - z; 
    return V(h/2., index);
}

// Water ------------------------------------------------------------------------------------------------

// Water heightfield
// x : Position in the plane
float Water( in vec2 p ) {    
    return 3.*Turbulence(p,200.,5.*cos(iTime*0.2)+20.,2,0.5*iTime) -90.0;
} 

// Implicit surface defining the terrain
// p : Point
// index : texture index
V ImplicitWater(in vec3 p, int index){
	float h = p.z - Water( p.xy );
    return V(h,index);
}


// Primitive functions -----------------------------------------------------------------------------------

// Union
// a,b : field function of left and right sub-trees
float Union(float a,float b)
{
    return min(a,b);
}
V Union(V a,V b)
{
    if (a.v<b.v)
        return V(a.v,a.i);
    else 
    {
        return V(b.v,b.i);
    }
}

// Différence
// a,b : les deux distance de la fonction SDF des deux object
float Differance(float a, float b){
    return max(a, -b);
}

//matrice de rotation
// a : prend en paramÃ¨tre iTime
mat2 Rotation(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

// Intersection, preserve the index of first object
// a, b : field function of left and right sub-trees
float Intersection(float a,float b)
{
    return max(a,b);
}
V Intersection(V a,V b)
{
    if (a.v>b.v)
    {
        return V(a.v,a.i);
    }
    else 
    {
        return V(b.v,a.i);
    }
}

// Sphere 
// p : Point
// c : Center 
// r : Radius
V Sphere(vec3 p, vec3 c,float r, int index)
{
  return V(length(p-c)-r, index);
}

float Plane(vec3 p, vec3 n, vec3 o)
{
    return dot((p-o),n);
}

//la boite
// p : le point Ã  qui on calcul la distance Ã  la boite
// S : l'espace entre deux plan de la boite
float Boite(vec3 p, vec3 s){
    vec3 q = abs(p)-s;
    return length( max(q , 0.0))+ min(max(q.x, max(q.y, q.z)), 0.0);
}

float cylinder (vec3 a, vec3 b, float r, vec3 p)
{
    float aq = dot((p-a),(b-a)/length(b-a));
    float l = sqrt(dot(p-a,p-a)- aq*aq);
    return l-r;
}

//DemicylindreFini
// p : point
// r : rayon
// t : taille du cylindre
float DemiCylindreFini(vec3 p, float r, float t)
{
    float cylindreInf = cylinder(vec3(0.0, 0.0, 0.0), vec3(0.0, 0.0, t), r, p);
    float demiCylindreInf1 = Differance(cylindreInf, Plane( p, vec3(-0.6, 0.0, -1.0) , vec3(0.0, 0.0, t+1.0))); 
    demiCylindreInf1 = Differance(demiCylindreInf1, Plane( p, vec3(0.6 ,0.0 ,-1.0) , vec3(0.0,0.0,t+1.0) ));
    return demiCylindreInf1;

}

//moulin
// p : point
// t : taille du moulin
V Moulin(vec3 p, float t,int indexC, int indexP){
    V Colone = V(DemiCylindreFini(p, 70.0, t),indexC);
    vec3 ptr= Translation(p,vec3(0.,70.,200.));
    ptr.xz = ptr.xz * Rotation(iTime);
    ptr.yz = ptr.yz * Rotation(1.53);
    float raccord = Boite(ptr,vec3(20.,20.,50.));
    ptr = Translation(ptr,vec3(0.,0.,30.));
    float pale1 = Boite(ptr,vec3(150.,30.,10.));
    ptr.xy = ptr.xy * Rotation(1.53);
    V pale = V(Union(raccord,Union(pale1, Boite(ptr,vec3(150.,30.,10.)))),indexP);
    V moulin = Union(Colone, pale);
    return moulin;
}

V Bateau(in vec3 p,int indexVoile){
    V base=V(Boite(p,vec3(50.,27.,4.)),1);
    p.xy = p.xy*Rotation(cos(iTime)/2.);
    p = Translation(p,vec3(0.,0.,-30.));
    V mat=V(Boite(p,vec3(3.,3.,25.)),1);
    p = Translation(p,vec3(20.,0.,0.));
    V voile=V(Differance( Boite(p,vec3(20.,2.,15.)) , Plane(p,vec3(-0.6,0.,-1.),vec3(0.0,0.,5.)) ),indexVoile);
    return Union(base,Union(mat,voile));
    }

// Scene --------------------------------------------------------------------------

// Potential field of the object
// p : point
V Object(vec3 p)
{
  V terrain = ImplicitTerrain(p,0);
  V Limite = Sphere(p,vec3(0.0,0.0,0.0),800.0,0);
  V surface = Intersection(terrain, Limite);
  V Ocean = Intersection(ImplicitWater(p,3), Limite);
  vec3 pt = Translation(p,vec3(320.,0.,0.));
  pt.xy = pt.xy * Rotation(-1.0);
  V moulin1 = Moulin(pt,290.,2,1);
  pt = Translation(p,vec3(150.,250.,Ocean.v+p.z-5.));
  pt = Translation(pt,vec3(55.*cos(iTime),100.*cos(iTime),0.));
  V bateau = Bateau(pt,4);
  moulin1.v = Differance(moulin1.v,terrain.v);
  V u = Union(moulin1,Union(surface,Ocean));
  u = Union(u,bateau);
  return u;
}

// Analysis of the scalar field --------------------------------------------------------------------------

// Calculate object normal
// p : point
vec3 ObjectNormal(in vec3 p )
{
  float eps = 0.001;
  vec3 n;
    float v = Object(p).v;
  n.x = Object( vec3(p.x+eps, p.y, p.z) ).v - v;
  n.y = Object( vec3(p.x, p.y+eps, p.z) ).v - v;
  n.z = Object( vec3(p.x, p.y, p.z+eps) ).v - v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// e : Maximum distance 
// h : hit
// s : Number of steps
float SphereTrace(vec3 o, vec3 u, float e,out bool h,out int s)
{
  h = false;

    // Start at the origin
    float t=0.0;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = Object(p).v;
    // Hit object
      if (v < 0.0)
      {
          s=i;
          h = true;
          break;
      }
      // Move along ray
      t += max(Epsilon,v/K);
      // Escape marched too far away
      if (t>e)
      {
          break;
      }
  }
  return t;
}

// Shading and lighting ---------------------------------------------------------------------------

T TextureRock(in vec3 p)
{
  return T(mix(vec3(0.92,0.91,0.90),vec3(0.74,0.72,0.72),Fbm(p/50.0)),0.0);   
}

int convert(float a){
    if(a<0.0){
        a= a-1.0;
    }
    return int(a);
}

T TextureDamier(in vec3 p, T a, T b){
    p.y = p.y+Turbulence(p,150.,10.,13);
    int x = convert(p.x/20.);//convertion en entier
    int y = convert(p.y/20.);//correction partie entière des valeurs négatifs pour evité le cas de int(0.4)=int(-0.4)
    int z = convert(p.z/20.);
        if( (x+y+z)%2 == 0 ){//modulo 2 pour faire 2 cas: coordonées paire, coordonées impaire
        return a;//couleur 1
    }else{
        return b;//couleur 2
    }   
}

T TextureBois(in vec3 p, T a, T b, float E, float bruit){
    p = p + Translation(p,vec3(460.,0.,360.));
    //p.xz = p.xz * Rotation(iTime);
    p.yz = p.yz * Rotation(1.53);

    p = p + bruit*Noise(p)*(p/E);//perturbation de p en fonction de l'indice bruit et de la longeur d'onde E
    float d = length(p.xy/50.);//calcul de la distance pour créer les cercles
    float v = 0.5*cos(d*E) + 1.0;//utilisation de cos pour interpoler les deux couleurs quand v=1 couleur a quand v= 0 couleur b
    return T( (a.c*v + b.c*(1.0-v)), (a.s*v + b.s*(1.0-v)) );//vrai interpolation de la couleur mais aussi de l'indice spéculaire
}

T TextureWater(in vec3 p){
  float eauSombre = smoothstep(-10.,15.,p.z);
  return T(mix(vec3(0.16,0.29,0.37),TextureRock(p).c,eauSombre),1.);
}

T TextureBorder(in vec3 p)
{
    return T(vec3(0.2,0.2,0.2),1.);   
}

T TextureTerrain(in vec3 p){
    float herbe,neige,roche;
    p.z = p.z+Turbulence(p,100.,50.,1);
    herbe = smoothstep(-9.0, 10.0, p.z);
    neige = smoothstep(80., 200., p.z);
    roche = smoothstep(30.,70.,p.z);
    if(p.z > 100.) return T(mix(TextureBorder(p).c,vec3(1., 1., 1.), neige),0.);

    if(p.z > -30. && p.z < 40.) return T(mix(vec3(0.8, 0.8, 0.6), vec3(0., 0.6, 0.), herbe),0.);

    if(p.z > 40. && p.z < 100.) return T(mix(vec3(0.,0.6,0.),TextureBorder(p).c, roche),0.);

    if(p.z > -70. && p.z < -30.) return T(vec3(.8, 0.8, 0.6),0.);

    if(p.z <= -70.) return TextureWater(p);
}

// Compute color 
// p : Point
// n : Normal
T Color(in vec3 p,in vec3 n)
{
    V vp= Object(p);
    if (vp.i==0){
        return TextureTerrain(p);
    }
    else if (vp.i==1){
        return TextureBois(p, T(vec3(0.87, 0.72, 0.53),0.0), T(vec3(0.54, 0.27, 0.07),0.0), 6.0, 0.1);
    }
    else if (vp.i==2){
        return TextureDamier(p,T(vec3(0.52,0.18,0.11),0.0),T(vec3(0.81,0.28,0.16),0.0));
    }
    else if (vp.i==3){
        return TextureWater(p);
    }
    else if (vp.i==4){        
        return T(vec3(0.9,0.1,0.2),0.);//juste du rouge couleur unie.
    }
}

// Rendering 
// ro, rd : Ray origin and direction
// pip : Picture in picture boolean
vec4 Render( in vec3 ro, in vec3 rd, bool pip )
{
    // Light direction
    vec3 light1 = normalize( vec3(-0.8,-0.3,0.4) );


	vec3 col;
    int it;
    bool b;
    float t = SphereTrace( ro, rd, View,b , it);
	vec3 p = ro + t*rd;
    V vp= Object(p);
    vec3 n = ObjectNormal( p );


    col = Color(p,n).c;
    // Sky
    if( b==false )
    {
        col = vec3(0.79,0.5,0.43) - rd.z*rd.z*2.5;
    }
        vec3 ref = reflect( rd, n );
        float fre = clamp( 1.0+dot(rd,n), 0.0, 1.0 );
        vec3 hal = normalize(light1-rd);


         // Pseudo diffuse lighting
		float dif = 0.5*(1.0+dot( light1, n ));
        dif*=dif;

        col = 0.45*col+0.55*dif*vec3(0.35,0.35,0.35);

    // Shading for iterations
    if (pip==true)
    {
    	float s=float(it)/float(Steps);
		return vec4(0.112+0.824*s,0.418+0.556*s,0.611-0.515*s,t);
    }
    else
    {
    	// Gamma with square root
    	return vec4( sqrt(col), t );
    }
}

vec2 RayDirection(in vec2 pixel, out bool pip)
{
    // Pixel coordinates
    vec2 p = (-iResolution.xy + 2.0*pixel)/iResolution.y;
   if (pip==true)
   {    
    const float fraction=1.0/5.0;
    // Picture in picture
    if ((pixel.x<iResolution.x*fraction) && (pixel.y<iResolution.y*fraction))
    {
        p=(-iResolution.xy*fraction + 2.0*pixel)/(iResolution.y*fraction);
        pip=true;
    }
       else
       {
           pip=false;
       }
   }
   return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   // Time as function of the position of the mouse
    float a = 3.14*2.0*1.5*iMouse.x/iResolution.x;

    // Camera transform
    vec3 ro; 
    mat3 cam = Camera( a, ro);   

    // Picture in picture
    bool pip=true;

    // Pixel coordinates
    vec2 p = RayDirection(fragCoord, pip);

    // Camera ray    
    vec3 rd = cam * normalize(vec3(p,3.0));

    // Render
    vec4 res = Render( ro, rd, pip );

    fragColor = vec4( res.xyz, 0.0 );

}

