// Texturing
// Eric Galin

// Texturing and noise ---------------------------------------------------------

// Hashing function
// Returns a random number in [-1,1]
// p : Vector in space
float Hash(in vec3 p)  
{
    p  = fract( p*0.3199+0.152 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}
// Procedural value noise with cubic interpolation
// x : Point 
float Noise(in vec3 p)
{
    vec3 i = floor(p);
    vec3 f = fract(p);

    f = f*f*(3.0-2.0*f);
    // Could use quintic interpolation instead of cubic
    // f = f*f*f*(f*(f*6.0-15.0)+10.0);

    return mix(mix(mix( Hash(i+vec3(0,0,0)), 
                        Hash(i+vec3(1,0,0)),f.x),
                   mix( Hash(i+vec3(0,1,0)), 
                        Hash(i+vec3(1,1,0)),f.x),f.y),
               mix(mix( Hash(i+vec3(0,0,1)), 
                        Hash(i+vec3(1,0,1)),f.x),
                   mix( Hash(i+vec3(0,1,1)), 
                        Hash(i+vec3(1,1,1)),f.x),f.y),f.z);
}
float Ridge(in vec3 p){
    return 1.-2.*abs(Noise(p));
}

// Hashing function
// Returns a random number in [-1,1]
// p : Vector in the plane
float Hash(in vec2 p)  
{
    p  = fract( p*0.3199+0.152 );
	p *= 17.0;
    return fract( p.x*p.y*(p.x+p.y) );
}
// Procedural value noise with cubic interpolation
// x : Point 
float Noise(in vec2 p)
{
    vec2 i = floor(p);
    vec2 f = fract(p);

    f = f*f*(3.0-2.0*f);
    // Could use quintic interpolation instead of cubic
    // f = f*f*f*(f*(f*6.0-15.0)+10.0);

    return mix(mix( Hash(i+vec2(0,0)), 
                        Hash(i+vec2(1,0)),f.x),
                   mix( Hash(i+vec2(0,1)), 
                        Hash(i+vec2(1,1)),f.x),f.y);
}
float Ridge(in vec2 p){
    return 1.-2.*abs(Noise(p));
}

float Turbulence(in vec3 p,float longueurOnde ,float coef, int detail){// somme de bruits 
    float somme = coef*Noise(p/longueurOnde);
    for(int i = 0; i<detail; i++){//boucle pour calculer la somme de bruit
        coef = coef *0.5;
        longueurOnde = longueurOnde *0.5;
        somme = somme + coef*Noise(p/longueurOnde);
    }
    return somme;
}

float Turbulence(in vec2 p,float longueurOnde ,float coef, int detail, float temps){// somme de bruits vec2
    float somme = coef*Noise(p/longueurOnde);;
    for(int i = 0; i<detail; i++){//boucle pour calculer la somme de bruit
        coef = coef *0.5;
        longueurOnde = longueurOnde *0.4;
        somme = somme + coef*Noise(p/longueurOnde+temps);
    }
    return somme;
}

float TurbulenceRidge(in vec2 p,float longueurOnde, float coef, int detail, float amin, float amax){//somme de ridge
    float somme = coef*Ridge(p/longueurOnde);
    for(int i=1;i<detail;i++){
        coef *= 0.5;
        longueurOnde *= 0.5;
        float att=smoothstep(amin,amax,somme);
        somme+= att*coef*Ridge(p/longueurOnde);
    }
    return somme;
}


// Fractal brownian motion with 4 frequencies
// p : Point
float Fbm(in vec3 p)
{
    return Noise(p)/2.0+Noise(p*2.0)/4.0+Noise(p*4.0)/8.0+Noise(p*8.0)/16.0;
}

// Camera
// a: Rotation angle around z axis
// ro : Ray origin
mat3 Camera(float a, out vec3 ro)
{
	float range=1350.0;
    // Origin
    ro = vec3(range*cos(a),range*sin(a),range/4.0);

    // Target
    vec3 ta = vec3(0.0,0.0,50.0);

	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(0.0, 0.0,1.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

