// https://www.iquilezles.org/articles/smoothvoronoi/
float voronoiSmooth(vec3 p, float s)
{
    vec3 i = floor(p);
    vec3 f = fract(p);

    float res = 8.;
    for(int j=-1; j<=1; j++)
    for(int k=-1; k<=1; k++)
    for(int l=-1; l<=1; l++)
    {
        vec3 b = vec3(j,k,l);
        vec3 r = b-f+hash33(i+b);

        float d = length( r );
        res = fOpUnionSmooth( res, d, s );
    }
    return res;
}

float vFbm(vec3 p, float freq, float s)
{
    return (1.-voronoiSmooth(p*freq, s)) * .7 +
        	 (1.-voronoiSmooth(p*freq*2., s*.5)) * .2 +
        	 (1.-voronoiSmooth(p*freq*4., s*.25)) * .1;
}

float map(vec3 p){
    vec3 q = p;
    float ns = pow(voronoiSmooth(q*2.+vec3(0,0,iTime*.5), .85), 1.)*.3;
    float ns2 = u2s(pow(vFbm(q*1.+vec3(0,0,iTime*.1), 8., .5), 1.))*.15;
    float smoothness = .3;
    float d = fOpUnionSmooth(sdEllipsoid(q, vec3(.5, .25, .5)), sdEllipsoid(q+vec3(sin(iTime), sin(iTime), cos(iTime))*.5, vec3(.5, .25, .5)), smoothness)+ns-ns2;
    return d*.25;
}

