  //p = spiral(p,10);
  //p = spiral(p,5);

  b.x = length(p.xz)-2.;
  b.x*=0.7;
  glow+=exp(-b.x)*vec3(0.8,0.7,0.2)*0.1;

  a.x = min(a.x,b.x);
