float sdfBox(vec3 pos, vec3 center, vec3 size){
    pos -= center;
    vec3 d = abs(pos) - size*.5;
    float m = (vmax(d));
    return max(length(min(sign(d) + vec3(1.), 1.) * d), abs(m)) * sign(m);
}

float sdfSphere(vec3 pos, vec3 center, float r) {
    pos -= center;
    return length(pos) - r;
}

void mmin(inout float dis, float b, inout uint id, uint idB, uint ignoreId) {
    if(dis < b || ignoreId == idB) return;
    dis = b;
    id = idB;
}

void mmin(inout float dis, float b, inout uint id, uint idB, uint ignoreId, uint inverseId) {
    if(idB == inverseId) b = -b;
    if(dis < b || ignoreId == idB) return;
    dis = b;
    id = idB;
}

