float getDistance(vec3 pos, out uint hitId, uint ignoreId){
    float dis = RENDER_DISTANCE;

    mmin(dis, sdfSphere(pos, LIGHT_SOURCE_POSITION, LIGHT_SOURCE_RADIUS), hitId, LIGHT_SOURCE_ID, ignoreId);
    mmin(dis, abs(pos.y+0.1)-0.1, hitId, 2u, ignoreId);
    mmin(dis, sdfBox(pos, BOX_POSITION, BOX_SIZE), hitId, 3u, ignoreId);
    mmin(dis, sdfBox(pos, VOLUME_POSITION, VOLUME_SIZE), hitId, 4u, ignoreId);

    return dis;
}

float getDistanceInVolume(vec3 pos, out uint hitId, uint ignoreId){
    float dis = RENDER_DISTANCE;

    mmin(dis, sdfSphere(pos, LIGHT_SOURCE_POSITION, LIGHT_SOURCE_RADIUS), hitId, LIGHT_SOURCE_ID, ignoreId);
    mmin(dis, abs(pos.y+0.1)-0.1, hitId, 2u, ignoreId);
    mmin(dis, sdfBox(pos, BOX_POSITION, BOX_SIZE), hitId, 3u, ignoreId);
    mmin(dis, sdfBox(pos, VOLUME_POSITION, VOLUME_SIZE), hitId, 4u, ignoreId, 4u);

    return dis;
}

