/*
 * Normal function by Inigo Quilez: https://iquilezles.org/articles/normalsSDF/
*/
const vec2 e = vec2(.01, 0);
vec3 getNormal(vec3 p){
 	float d = getDistance(p);

    vec3 n = d - vec3(getDistance(p - e.xyy), getDistance(p - e.yxy), getDistance(p - e.yyx));
    return normalize(n);
}


const vec3 colors[] = vec3[](
            vec3(0.0),
            vec3(1.),
            vec3(.7, .1, 1.),
            vec3(1., 0., .0)
            );
vec3 color(vec3 pos, uint id) {
    return colors[id]/20.;
}



struct Path {
    vec3 pos;
    vec3 dir;
    float dis;
    uint hitId;
    vec3 multiply;
    vec3 add;
};

vec3 attenuation(float dis, Path path) {
    return exp(-(dis) * (ABSOPRTION_C(path.pos, path.dir) + SCATTERING_C(path.pos, path.dir)));
}

vec3 volumeLightMarch(vec3 inPos, out vec3 ligthDir) { 
    ligthDir = normalize(LIGHT_SOURCE_POSITION - inPos);


    Path path = Path(inPos, ligthDir, 0.0, NO_HIT_ID, vec3(1.0), vec3(0.0));    
    for(uint i = 0u; i < MAX_STEPS; i++){
        #ifdef CONSTANT_ANT
            float dis = getDistanceInVolume(path.pos, path.hitId, 0u);
        #else
            float dis = min(VOLUME_MAX_STEP_DISTANCE, getDistanceInVolume(path.pos, path.hitId, 0u));
        #endif

        path.dis += dis;
        path.pos = inPos + path.dir * path.dis;

        if(dis < MIN_DISTANCE){
            //Processes
            path.multiply *= attenuation(path.dis, path);
            path.add += ADD_EMISSION(path.pos, path.dir) * path.dis * path.multiply;

            if(path.hitId == LIGHT_SOURCE_ID) {
                return (LIGHT_SOURCE_LIGHT) * path.multiply + path.add;
            } else if(path.hitId != 4u){
                return vec3(0.);  
            }


            break;
        }
    }


    vec3 pos = path.pos;
    uint id = NO_HIT_ID;
    float dis = 0.0;

    for(uint i = 0u; i < MAX_STEPS; i++){
        float cdis = getDistance(pos, id, 4u);
        dis += cdis;

        pos = path.pos + ligthDir * dis;

        if(cdis < MIN_DISTANCE){
            if(id == LIGHT_SOURCE_ID) {
                return (LIGHT_SOURCE_LIGHT) * path.multiply + path.add;
            }
            return vec3(0.);  
        }

        if(dis > RENDER_DISTANCE) break;

    }

    return  vec3(0.,0., 0.);
}

Path volumeMarch(vec3 inPos, vec3 rayDir, float maxStep, uint ignoreId) {
    Path path = Path(vec3(0.0), rayDir, MIN_DISTANCE*20., NO_HIT_ID, vec3(1.0), vec3(0.0));
    path.pos = inPos + path.dir * path.dis;
    vec3 atten = attenuation(path.dis, path);
    path.multiply *= atten;
    path.add += ADD_EMISSION(path.pos, path.dir) * path.dis * atten;   

    for(uint i = 0u; i < MAX_STEPS; i++){
        float dis = min(maxStep, getDistanceInVolume(path.pos, path.hitId, ignoreId));

        path.dis += dis;
        path.pos = inPos + path.dir * path.dis;

        //Processes
        path.multiply *= attenuation(dis, path);
        path.add += ADD_EMISSION(path.pos, path.dir) * dis*path.multiply;
        vec3 lightDir;
        vec3 light = volumeLightMarch(path.pos, lightDir);
        path.add += light*henyeyGreenstein(HG_G, lightDir, path.dir)*dis*SCATTERING_C(path.pos, path.dir)*path.multiply;


        if(dis < MIN_DISTANCE){
            return path;
        }
    }

    return path;
}

vec3 lightMarch(vec3 inPos, uint ignoreId) {
    if(ignoreId == NO_HIT_ID) return vec3(1.); //make light not apply to the sky
    vec3 ligthDir = normalize(LIGHT_SOURCE_POSITION - inPos);
    float lightIntensity = dot(getNormal(inPos), ligthDir);

    if(lightIntensity <= 0.0) return vec3(0.0);

    Path path = Path(vec3(0.), vec3(0.), 0., 0u, vec3(1.), vec3(0.0));
    vec3 pos = inPos;
    uint id = NO_HIT_ID;
    float dis = 0.0;

    for(uint i = 0u; i < MAX_STEPS; i++){
        float cdis = getDistance(pos, id, ignoreId);
        dis += cdis;

        pos = inPos + ligthDir * dis;

        if(cdis < MIN_DISTANCE){
            if(id == LIGHT_SOURCE_ID) {
                return (LIGHT_SOURCE_LIGHT * (lightIntensity)) * path.multiply + path.add;
            } else if(id == 4u) {
                //return volumeLightMarch(pos, ligthDir);
                #ifdef CONSTANT_ANTs
                    path = volumeMarch(pos, ligthDir, 1000., ignoreId);
                #else
                    path = volumeMarch(pos, ligthDir, VOLUME_MAX_STEP_DISTANCE, ignoreId);
                #endif

                if(path.hitId == LIGHT_SOURCE_ID) {
                    return (LIGHT_SOURCE_LIGHT * (lightIntensity)) * path.multiply + path.add;
                }else if(path.hitId != 4u) {
                    return vec3(0.0);
                }

                ignoreId = 4u;
                dis += path.dis;
                pos = inPos + ligthDir * dis;
                continue;
            }

            return vec3(0.0);

        }

        if(dis > RENDER_DISTANCE) break;

    }

    return vec3(0.0);
}

vec3 diffuse(vec3 pos, uint hitId, Path path) {
    return color(pos, hitId) * lightMarch(pos, hitId) * path.multiply + path.add;
}

Hit rayMarch(Ray r, uint ignoreId){

    Hit hit = Hit(r.o, 0.0, NO_HIT_ID, vec3(0.0), r);
    Path path = Path(vec3(0.), vec3(0.), 0., 0u, vec3(1.), vec3(0.0));

    for(uint i = 0u; i < MAX_STEPS; i++){
        float dis = getDistance(hit.pos, hit.id, ignoreId);
        hit.dis += dis;

        hit.pos = r.o + r.d * hit.dis;

        if(dis < MIN_DISTANCE){

            if(hit.id == LIGHT_SOURCE_ID){
                hit.color = LIGHT_SOURCE_LIGHT;
                return hit;
            } else if(hit.id == 4u) {
                path = volumeMarch(hit.pos, r.d, VOLUME_MAX_STEP_DISTANCE, 0u);

                if(path.hitId != 4u) {
                    if(path.hitId == LIGHT_SOURCE_ID){
                        hit.color = LIGHT_SOURCE_LIGHT;
                        return hit;
                    }
                    hit.color = diffuse(path.pos, path.hitId, path);
                    return hit;
                }

                ignoreId = 4u;
                hit.dis += path.dis;
                hit.pos = r.o + r.d * hit.dis;
                continue;
            }

            hit.color = diffuse(hit.pos, hit.id, path);

            return hit;
        }

        if(hit.dis > RENDER_DISTANCE) break;

    }

    hit.id = NO_HIT_ID;
    hit.color = diffuse(hit.pos, hit.id, path);
    return hit;
}

Ray calcRay(vec2 uv, sampler2D varChannel) {
    vec4 camPosition = GET(varChannel, CAM_POS_TEXEL); //xyz = pos; w = distance to screen
    vec4 viewDirection = GET(varChannel, VIEW_DIR_TEXEL);

    //calc ray Direction
    vec3 screenXVector = cross(viewDirection.xyz, UP_VECTOR);
    vec3 screenYVector = -cross(viewDirection.xyz, screenXVector);

    vec3 rayDirection = normalize(viewDirection.xyz * camPosition.w
                    + screenXVector * uv.x + screenYVector  * uv.y);

    return Ray(camPosition.xyz, rayDirection);
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy*.5) / (iResolution.yy*.5);
    Hit hit = rayMarch(calcRay(uv, iChannel1), 0u);  
    fragColor = vec4(hit.color,1.0);
}

