            vec4 pos = GET(BACK_BUFFER_CHANNEL, CAM_POS_TEXEL);
            vec4 vdir = GET(BACK_BUFFER_CHANNEL, VIEW_DIR_TEXEL);
            vec2 v = normalize(vdir.xz);
            vec2 t = normalize(cross(UP_VECTOR, vdir.xyz)).xz;

            float speed = MOVE_SPEED;

            //move cam
            if(isKey(KEY_W, IS_PRESSED, KEYBOARD_CHANNEL)){//forward
                pos.xz += v * speed;
            }
            if(isKey(KEY_S, IS_PRESSED, KEYBOARD_CHANNEL)){//backward
                pos.xz -= v * speed;
            }
            if(isKey(KEY_A, IS_PRESSED, KEYBOARD_CHANNEL)){//left
                pos.xz += t * speed;
            }
            if(isKey(KEY_D, IS_PRESSED, KEYBOARD_CHANNEL)){//right
                pos.xz -= t * speed;
            }
            if(isKey(KEY_SHIFT, IS_PRESSED, KEYBOARD_CHANNEL)){
                pos.y -= 1.0 * speed;
            }
            if(isKey(KEY_SPACE, IS_PRESSED, KEYBOARD_CHANNEL)){
                pos.y += 1.0 * speed;
            }

            //Zoom
            if(isKey(KEY_C, IS_PRESSED, KEYBOARD_CHANNEL)){
                pos.w *= 1.052631578947368;
            }
            if(isKey(KEY_X, IS_PRESSED, KEYBOARD_CHANNEL)){
                pos.w *= 0.95;
            }

            fragColor = pos;
