         if(DO_SAVE(CAM_POS_TEXEL)){
            fragColor = vec4(CAM_START_POS, CAM_START_DIS_TO_SCREEN);

         }else if(DO_SAVE(VIEW_DIR_TEXEL)){
            vec3 viewDirection = normalize(CAM_START_LOOK_AT_POSITION - CAM_START_POS);
            fragColor = vec4(viewDirection, 1.0);

         }    
    }
}

