#define GREENCOLOR vec3(.117, .4125, .115)*0.7
#define REDCOLOR vec3(.611, .0555, .062)*0.7

const float eps = 0.0001;
vec4 lightSphere = vec4(0.);

float hash1(inout float seed) {
    return fract(sin(seed += 0.1)*43758.5453123);
}

vec3 randomVec3(inout float seed)
{
    vec3 d;
    do
    {
        d = 2.0f * vec3(hash1(seed), hash1(seed), hash1(seed)) - vec3(1, 1, 1);
    } while (dot(d, d) > 1.0);
    return normalize(d);
}

vec3 randomDirection(vec3 n, inout float seed)
{
  return normalize(randomVec3(seed) + n);
}

struct Ray
{
  vec3 ro;
  vec3 rd;
};

struct Material
{
  vec3 normal;
  vec3 color;
  vec3 emissive;
  float specularRate;
  float roughness;
};

struct HitResult
{
  bool isHit;
  float distance;
  vec3 hitPoint;
  vec3 viewDir;
  Material material;
};

vec3 nSphere( in vec3 pos, in vec4 sph ) {
    return (pos-sph.xyz)/sph.w;
}

float iSphere(vec3 ro, vec3 rd, vec4 sph)
{
  vec3 oc = ro - sph.xyz;
  float b = dot(oc, rd);
  float c = dot(oc, oc) - sph.w * sph.w;
  float h = b * b - c;
  if (h < 0.0) return -1.0;

  float s = sqrt(h);
  float t1 = -b - s;
  float t2 = -b + s;

  return t1 < 0.0 ? t2 : t1;
}

vec3 nPlane( in vec3 ro, in vec4 obj ) {
  return obj.xyz;
}

float iPlane( vec3 ro, vec3 rd, in vec4 pla ) {
  return (-pla.w - dot(pla.xyz,ro)) / dot( pla.xyz, rd );
}

HitResult intersect( in vec3 ro, in vec3 rd) {
  float mint = 1e20;
  float t = -1.;
  vec3 normal = vec3(0.);

  HitResult result;

  t = iPlane( ro, rd, vec4( 0.0, 1.0, 0.0,0.0 ) ); 
  if( t>eps && t<mint ) 
      { mint = t; normal = vec3( 0., 1., 0.); result.material.color = WHITECOLOR;}

  t = iPlane( ro, rd, vec4( 0.0, 0.0,-1.0,8.0 ) ); 
  if( t>eps && t<mint ) 
      { mint = t; normal = vec3( 0., 0.,-1.); result.material.color = WHITECOLOR;}

  t = iPlane( ro, rd, vec4( 1.0, 0.0, 0.0,0.0 ) ); 
  if( t>eps && t<mint ) 
      { mint = t; normal = vec3( 1., 0., 0.); result.material.color = GREENCOLOR;}

  t = iPlane( ro, rd, vec4( 0.0,-1.0, 0.0,5.49) ); 
  if( t>eps && t<mint ) 
      { mint = t; normal = vec3( 0., -1., 0.); result.material.color = WHITECOLOR;}

  t = iPlane( ro, rd, vec4(-1.0, 0.0, 0.0,5.59) ); 
  if( t>eps && t<mint ) 
      { mint = t; normal = vec3( -1., 0., 0.); result.material.color = REDCOLOR;}

  t = iSphere( ro, rd, vec4( 1.5,1.0, 2.7, 1.0) ); 
  if( t>eps && t<mint ) 
  { 
    mint = t; 
    normal = nSphere( ro+mint*rd, vec4( 1.5,1.0, 2.7,1.0) ); 
    result.material.color = WHITECOLOR;
    result.material.specularRate = 0.8;
    result.material.roughness = 0.1;
  }

  t = iSphere( ro, rd, vec4( 4.0,1.0, 4.0, 1.0) ); 
  if( t>eps && t<mint ) 
  { 
    mint = t;
    normal = nSphere( ro+mint*rd, vec4( 4.0,1.0, 4.0,1.0) ); 
    result.material.color = GREENCOLOR;
    result.material.specularRate = 0.3;
    result.material.roughness = 1.0;
  }

  t = iSphere( ro, rd, lightSphere ); 
  if( t>eps && t<mint ) 
  { 
    mint = t;  
    normal = nSphere( ro+mint*rd, lightSphere ); 
    result.material.color = LIGHTCOLOR; 
    result.material.emissive = vec3(1., 0.894, 0.768) * 20.;
  }

  result.isHit = mint > eps && mint < 1e20 ? true : false;

  if(result.isHit)
  {
    result.distance = mint;
    result.hitPoint = ro + rd * mint;
    result.material.normal = normal;
  }

  return result;					  
}

vec3 PathTracing(vec3 ro, vec3 rd, inout float seed)
{
  vec3 tcol = vec3(0.);
  vec3 fcol = vec3(1.);

  float p = 0.;
  HitResult result;

  for(int i = 0; i < 8; i++)
  {
    result = intersect(ro, rd);
    if(!result.isHit)
      return vec3(0);

    float r = hash1(seed);
    p = 0.95;
    if(r > p) 
        fcol = vec3(0.);

    Ray randomRay;
    randomRay.ro = result.hitPoint;
    randomRay.rd = randomDirection(result.material.normal, seed);

    float cosine = abs(dot(-rd, result.material.normal));

    r = hash1(seed);
    if(r < result.material.specularRate)
    {
      vec3 ref = normalize(reflect(rd, result.material.normal));
      randomRay.rd = mix(ref, randomRay.rd, result.material.roughness);
    }

    tcol += (fcol * result.material.emissive) * cosine;

    fcol = fcol * result.material.color;

    rd = randomRay.rd;
    ro = randomRay.ro;
  }
  return tcol / p;
}

mat3 setCamera(in vec3 ro, in vec3 rt, in float cr)
{
    vec3 cw = normalize(rt - ro);
    vec3 cp = vec3(sin(cr), cos(cr), 0.0);
    vec3 cu = normalize(cross(cw, cp));
    vec3 cv = normalize(cross(cu, cw));
    return mat3(cu, cv, cw);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 p = -1.0 + 2.0 * (fragCoord.xy) / iResolution.xy;
  p.x *= iResolution.x/iResolution.y;

  float seed = p.x + p.y * 3.43121412313 + fract(1.12345314312*iTime);

  lightSphere = vec4(vec3(3.), 1.);

  vec2 of = -0.5 + vec2(hash1(seed), hash1(seed));
  p = (2.0 * (fragCoord + of) - iResolution.xy) / iResolution.y;

  vec3 ro = vec3(2.78, 2.73, -8.00);
  vec3 ta = vec3(2.78, 2.73,  0.00);


  mat3 ca = setCamera(ro, ta, 0.0);
  vec3 rd = normalize(ca * vec3(p, 3.0));

  vec3 col = vec3(0.);
  vec3 tot = vec3(0.);

  for( int a=0; a<16; a++ ) 
  {
    col = PathTracing(ro, rd, seed);

    tot += col;

    seed = mod( seed*1.1234567893490423, 13.);
  }
  tot /= float(16.);

  tot = pow(clamp(tot, 0., 1.), vec3(0.45));

  vec3 lastFrameColor = texture(iChannel0, fragCoord / iResolution.xy).rgb;
  tot = mix(lastFrameColor, tot, 1.0f / float(iFrame+1));

  fragColor = vec4(tot, 1.0);
}

