// Fugu in fishbowl in non-Faberge egg - mousing advised

// Optics includes reflection, refraction, total internal reflection, Fresnel reflection

/*
  No. 7 in "Egg" series; others are listed in "Nautilus Egg" (7dj3Wz).
*/

#define AA  0  // (= 0/1) optional antialiasing

#if 1
#define VAR_ZERO min (iFrame, 0) 
#else
#define VAR_ZERO 0
#endif

float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float PrCylAnDf (vec3 p, float r, float w, float h);
float PrCapsDf (vec3 p, float r, float h);
vec4 SphFib (vec3 v, float n);
float Minv2 (vec2 p);
float Maxv2 (vec2 p);
float SmoothMin (float a, float b, float r);
float SmoothMax (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
vec3 RotToDir (vec3 v1, vec3 v2, vec3 p);
float Fbm2 (vec2 p);

vec3 qHit, ltDir, sphPos, fishPos, pFish, vuPos, eyeOff, hingSz;
vec2 opCs;
float dstFar, tCur, opPhs, shellRad, sphRad, sphHt, shHalf, fishSz, fishAng, rotDir,
   bodyEx, spkEx, mthShp;
int idObj;
bool isSh;
const int idShell = 1, idHing = 2, idArm = 3, idSup = 4, idFlr = 5,
   idBody = 11, idFinV = 12, idFinL = 13, idTail = 14, idMouth = 15, idEye = 16;
const float pi = 3.1415927, phi = 1.618034;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ShRad (vec3 p)
{
  return shellRad + 0.02 * (1. - p.y / length (p)) * cos (64. * atan (p.x, p.z));
}

float ExtObjDf (vec3 p)
{  // (from "Nautilus Egg")
  vec3 q;
  float dMin, d, ds, r, y;
  dMin = dstFar;
  q = p;
  q.y -= -0.1;
  d = PrCylDf (q.xzy, 40., 0.1);
  DMINQ (idFlr);
  q = p;
  q.y -= shellRad;
  q.z -= - (shellRad - 0.1) - 2. * hingSz.z;
  d = PrCapsDf (q.yzx, hingSz.y + 0.02, hingSz.x);
  DMINQ (idHing);
  q.yz = Rot2Cs (q.yz, opCs);
  shHalf = sign (q.y);
  q.y = abs (q.y);
  q.yz = Rot2Cs (q.yz, opCs);
  q.z -= (shellRad - 0.1) + 2. * hingSz.z;
  r = ShRad (q);
  ds = length (q) - r;
  d = 0.9 * SmoothMax (abs (ds) - 0.05, -0.01 - q.y, 0.01);
  DMINQ (idShell);
  q.z -= - (shellRad - 0.1) - hingSz.z;
  d = max (PrRoundBoxDf (q, vec3 (hingSz.xy, hingSz.z + 0.01) - 0.02, 0.02),
     max (- ds, 0.05 - q.y));
  DMINQ (idArm);
  q = p;
  q.y -= 0.5 * sphHt;
  d = PrCylDf (q.xzy, 0.03, 0.5 * sphHt);
  DMINQ (idSup);
  return dMin;
}

float ExtObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ExtObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ExtObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ExtObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec2 SpkShp ()
{
  vec4 f4;
  vec3 uf;
  float s;
  f4 = SphFib (normalize (pFish + vec3 (0., 0., 0.1)), 2048.);
  uf = f4.yzw;
  s = smoothstep (0.95, 0.99, dot (uf, normalize (vec3 (0., 0.24, 1.))));
  if (s == 0.) s = smoothstep (0.92, 0.99, dot (uf, normalize (vec3 (0., 0.35, -1.))));
  if (s == 0.) s = smoothstep (0.9, 0.94, dot (uf, normalize ((eyeOff - vec3 (0., 0., -0.03)) *
     vec3 (sign (uf.x), 1., 1.))));
  if (s == 0.) s = smoothstep (0.97, 0.99, dot (uf, normalize (vec3 (sign (uf.x), 0., 0.25))));
  if (s == 0.) s = smoothstep (0.97, 0.99, dot (uf, normalize (vec3 (0., sign (uf.y), -0.1))));
  return vec2 (1. - s, f4.x);
}

float FishBodyDf ()
{
  vec3 p, q;
  vec2 spk;
  float spkLen, db, dt;
  spk = SpkShp ();
  spkLen = spk.x * (1. - smoothstep (0.001, 0.02, spk.y)) * (0.001 + 0.04 * spkEx);
  p = pFish;
  p.y -= -0.03 * bodyEx;
  p.x *= 1. + 1.5 * (p.y + 0.2) * (p.y + 0.2);
  p.xy *= 1. + 0.5 * smoothstep (0., 0.5, - p.z);
  q = (p - vec3 (0., 0.2 + 0.025 * bodyEx, 0.5 * (p.y - 0.2) - 0.14)) * vec3 (1.4, 1.7, 0.6);
  dt = PrSphDf (q, 0.4 - 0.2 * smoothstep (0., 1.5, 1.3 - p.z) + spkLen);
  q = p * vec3 (1.2, 1.2, 1. + 0.5 * smoothstep (0., 0.5, - p.y - p.z));
  db = PrSphDf (q, 0.37 + smoothstep (0., 1.7, - p.z) + 0.03 * bodyEx + spkLen);
  q = pFish;
  q.yz -= vec2 (0.1, 0.1);
  q.xy *= vec2 (0.5, 1.) + vec2 (0.1, -0.4) * mthShp;
  return SmoothMin (max (db, - PrCylDf (q, 0.02, 0.3)), dt, 0.05);
}

float FishMthDf ()
{
  vec3 p;
  p = pFish;
  p.yz -= vec2 (0.1, 0.34 + 0.02 * bodyEx - 2. * p.x * p.x);
  p.xy *= vec2 (0.5 + 0.1 * mthShp, 1. - 0.5 * mthShp);
  return PrCylAnDf (p, 0.015, 0.0015, 0.03);
}

float FishTailDf ()
{
  vec3 p;
  float d, r, a;
  p = pFish;
  p.yz -= vec2 (0.11, -0.45);
  p.xz = Rot2D (p.xz, 0.5 * cos (2. * tCur - 3. * p.y + 5. * p.z));
  a = 0.003 * sin (32. * atan (p.y, p.z));
  r = length (p.yz);
  d = min (0.01 - 0.008 * smoothstep (0.15, 0.25, r) - abs (p.x -
     a * smoothstep (0.04, 0.08, r)), 0.35 - 0.05 * p.y - 0.01 * cos (a * 1024.) - r);
  d = - SmoothMin (abs (p.y) + 0.3 * p.z, SmoothMin (-0.3 * abs (p.y) - p.z, d, 0.02), 0.02);
  return d;
}

float FishFinLDf ()
{
  vec3 p;
  float d, r, a, t, w;
  p = pFish;
  t = 5. * tCur + 0.2 * sign (p.x);
  p.x = abs (p.x) - 0.26 - 0.02 * bodyEx;
  p.xz = Rot2D (p.xz, 0.4 * pi);
  w = 0.15 * (1. - 0.15 * bodyEx) * (1. + 5. * length (p));
  p.yz = Rot2D (p.yz, 0.2 + 0.5 * w * cos (t + 2. * atan (p.x, - p.y)) - 0.5 * pi);
  p.xz = Rot2D (p.xz, 1.2 + 0.75 * bodyEx + w * sin (t - w) - 0.5 * pi);
  a = atan (p.x, - p.y);
  r = length (p.xy);
  d = min (0.01 - 0.008 * smoothstep (0.2, 0.3, r) -
     abs (p.z + 0.002 * sin (32. * a) * smoothstep (0.05, 0.08, r)),
     0.4 - 0.15 * smoothstep (1., 3., abs (a)) - 0.01 * cos (32. * a) - r);
  d = - SmoothMin (-0.2 * p.x + p.y, SmoothMin (p.x - 0.7 * p.y, d, 0.02), 0.02);
  return d;
}

float FishFinVDf ()
{
  vec3 p;
  float d, r, a, y;
  p = pFish;
  p.y = abs (p.y + 0.035 * bodyEx) - 0.035 * bodyEx - 0.26 - 0.02 * sign (p.y);
  p.z -= -0.1;
  y = smoothstep (0., 0.2, p.y);
  p.z *= 1. - 0.3 * y * y;
  a = - cos (4. * tCur + 5. * (- p.y + p.z)) * (0.1 - 0.3 * p.z);
  p.xz = Rot2D (p.xz, a);
  p.xy = Rot2D (p.xy, a);
  a = 0.0025 * sin (32. * atan (p.y, p.z));
  r = length (p.yz);
  d = min (0.01 - 0.009 * smoothstep (0.1, 0.2, r) -
     abs (p.x + a * smoothstep (0.04, 0.1, r)), 0.22 - 0.015 * cos (512. * a) - r);
  d = - SmoothMin (p.y + 0.4 * p.z, SmoothMin (-0.4 * p.y - p.z, d, 0.02), 0.02);
  return d;
}

float FishDf (vec3 p, float dMin)
{  // (from "Fibonacci's Fugu" and "Fugu Watching")
  float d, a, c;
  p.xz *= rotDir;
  c = cos (2. * tCur);
  a = -0.5 * smoothstep (-0.2, 1.1, - p.z) * c;
  p.xz = Rot2D (p.xz, 0.1 * c + a);
  p.xy = Rot2D (p.xy, 0.5 * a);
  pFish = p;
  d = 0.3 * FishBodyDf ();
  if (d < dMin) idObj = idBody;
  dMin = min (dMin, d);
  d = FishMthDf ();
  if (abs (d) < dMin) idObj = idMouth;
  dMin = SmoothMin (dMin, d, 0.01);
  d = 0.6 * FishTailDf ();
  if (d < dMin) idObj = idTail;
  dMin = SmoothMin (dMin, d, 0.01);
  d = 0.6 * FishFinLDf ();
  if (d < dMin) idObj = idFinL;
  dMin = SmoothMin (dMin, d, 0.01);
  d = 0.7 * FishFinVDf ();
  if (d < dMin) idObj = idFinV;
  dMin = SmoothMin (dMin, d, 0.01);
  p.x = abs (p.x);
  d = PrSphDf (p - eyeOff, 0.13);
  if (d < dMin) idObj = idEye;
  dMin = SmoothMin (dMin, d, 0.02);
  return dMin;
}

float IntObjDf (vec3 p)
{
  float dMin, d;
  p -= fishPos;
  p.xz = Rot2D (p.xz, - fishAng);
  dMin = dstFar / fishSz;
  p /= fishSz;
  if (! isSh) d = PrSphDf (p, 0.8);
  dMin = (isSh || d < 0.1) ? FishDf (p, dMin) : d;
  return fishSz * dMin;
}

float IntObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = IntObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 IntObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = IntObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec4 IntObjCol (vec3 p)
{
  vec4 col4;
  vec3 ve;
  vec2 spk;
  float s;
  col4 = mix (vec4 (0.9, 0.6, 0.8, 0.2), vec4 (0.9, 0.5, 0.5, 0.2), spkEx);
  if (idObj == idBody) {
    col4.rgb *= 1. - 0.5 * smoothstep (-0.2, 0.1, pFish.y);
    spk = SpkShp ();
    col4.rgb = mix (col4.rgb, vec3 (0.9, 0.7, 0.7), 0.5 * smoothstep (0., 0.01, spk.x) *
       SmoothBump (0.25, 0.75, 0.1, mod (128. * atan (0.7 * pFish.x, pFish.y - 0.1) / pi, 1.)));
    col4 = mix (col4, vec4 (0.9, 0.9, 0.95, 0.5), 0.5 * step (0.02, spk.x) * step (spk.y, 0.02));
  } else if (idObj == idMouth) {
    col4.rgb = mix (col4.rgb, vec3 (0.9, 0.7, 0.7), 0.5);
  } else if (idObj == idFinV || idObj == idFinL) {
    col4.rgb = mix (col4.rgb, vec3 (1.), 0.3 * smoothstep (0.4, 0.5, length (pFish)));
  } else if (idObj == idTail) {
    col4.rgb = mix (col4.rgb, vec3 (1.), 0.3 * smoothstep (0.6, 0.7, length (pFish)));
  } else if (idObj == idEye) {
    if (abs (pFish.y - eyeOff.y - 0.04) < 0.001 + 0.1 * SmoothBump (0.13, 0.87, 0.07,
       mod (0.5 * tCur, 1.))) {
      ve = eyeOff * vec3 (sign (pFish.x), 1., 1.) * fishSz;
      ve.xz = Rot2D (ve.xz, fishAng) * rotDir;
      ve += fishPos;
      ve = RotToDir (normalize (vuPos - ve), vec3 (sign (pFish.x), 0., 0.), p - ve);
      s = 0.8 * length (ve.yz) / fishSz;
      col4 = (s > 0.05 + 0.03 * SmoothBump (0.25, 0.75, 0.25, mod (8. *
         atan (ve.y, ve.z) / pi, 1.))) ? vec4 (1., 1., 1., 0.3) * (0.5 +
         0.5 * smoothstep (0.07, 0.1, s)) : vec4 (0., 0.5, 0., -1.);
    } else idObj = idBody;
  }
  return col4;
}

vec4 ExtObjCol ()
{
  vec4 col4;
  float r, rr, s, y;
  if (idObj == idShell) {
    r = ShRad (qHit);
    rr = length (qHit);
    s = 0.;
    if (rr > r) {
      s = smoothstep (0.7, 0.8, cos (32. * asin (qHit.y / rr) + 8. * atan (qHit.z, qHit.x) *
         shHalf));
    } else if (qHit.y > 0.01) {
      y = 1. - qHit.y / rr;
      s = smoothstep (0.05, 0.1, y) * smoothstep (-0.2, 0.2, (r - shellRad) / (0.02 * y));
    }
    col4 = mix (vec4 (0.2, 0.3, 1., 0.2), vec4 (0.8, 0.8, 1., 0.1), s);
  } else if (idObj == idHing) {
    col4 = vec4 (0.1, 0.1, 0.5, 0.2) * (1. - 0.4 * SmoothBump (-0.04, 0.04, 0.02,
       mod (4. * qHit.x / hingSz.x + 0.5, 1.) - 0.5));
  } else if (idObj == idArm) {
    col4 = vec4 (0.1, 0.1, 0.5, 0.2);
  } else if (idObj == idSup) {
    col4 = vec4 (0.5, 0.5, 0.5, 0.1);
  } else if (idObj == idFlr) {
    col4 = vec4 (0.5, 0.3, 0.1, 0.1) * (0.8 + 0.2 * Fbm2 (vec2 (0.5, 4.) * qHit.xz)) *
       (1. - 0.5 * smoothstep (0., 15., length (qHit.xz) - 25.));
  }
  return col4;
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p, qHitT;
  float sh, d, h;
  int idObjT;
  isSh = true;
  sh = 1.;
  idObjT = idObj;
  qHitT = qHit;
  d = 0.02;
  for (int j = VAR_ZERO; j < 30; j ++) {
    p = ro + d * rd;
    h = min (ExtObjDf (p), IntObjDf (p));
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  idObj = idObjT;
  qHit = qHitT;
  isSh = false;
  return 0.5 + 0.5 * sh;
}

float InSphHit (vec3 ro, vec3 rd, float rad)
{
  float b, d;
  b = dot (rd, ro);
  d = b * b + rad * rad - dot (ro, ro);
  return (d > 0.) ? - b - sqrt (d) : dstFar;
}

float OutSphHit (vec3 ro, vec3 rd, float rad)
{
  float b, d;
  b = dot (rd, ro);
  d = b * b + rad * rad - dot (ro, ro);
  return (d > 0.) ? - b + sqrt (d) : dstFar;
}

vec3 BgCol (vec3 rd)
{
  vec3 col; 
  vec2 w;
  col = 0.5 * mix (vec3 (0.5, 0.5, 0.), vec3 (1., 0.9, 0.8) * (0.4 + 0.2 * rd.y), 
     Minv2 (smoothstep (0.05, 0.1, abs (sin (64. * atan (rd.zy,
     vec2 (rd.x, length (rd.xz))))))));
  if (rd.y > 0.) {
    w = Rot2D (rd.xz / rd.y, 0.25 * pi);
    col = mix (col, vec3 (0.9, 0.9, 1.), step (Maxv2 (abs (w)), 0.75) * smoothstep (0., 0.02,
       0.45 - Maxv2 (abs (fract (6. * w + 0.5) - 0.5))));
  }
  return clamp (col, 0., 1.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, colR, row, rdt, rdo, vn, vnIn, vnOut, vnw;
  float dstObj, dstSphIn, dstSphOut, dstObjInt, dstObjExt, watHt, dstWat, eta, s, sh, nDotL;
  bool inWat, inWatAny, hitSph, needRefl, inShell;
  hingSz = vec3 (1., 0.2, 0.4);
  sphPos = vec3 (0., sphHt + sphRad, 0.);
  eta = 1.3;
  watHt = sphPos.y + 0.25 * sphRad;
  bodyEx = SmoothBump (0.25, 0.75, 0.15, mod (0.4 * tCur, 1.));
  spkEx = SmoothBump (0.65, 0.85, 0.05, mod (0.2 * tCur, 1.));
  mthShp = sin (6. * tCur);
  eyeOff = vec3 (0.13);
  fishSz = 2.;
  rotDir = 1.;
  fishAng = 2. * pi * rotDir * fract (0.1 * tCur);
  fishPos.xz = sphPos.xz + Rot2D (vec2 (sphRad - 1.7, 0.), fishAng);
  fishPos.y = watHt - 0.7 - 0.3 * cos (1. * tCur);
  if (rd.y == 0.) rd.y = 0.001;
  isSh = false;
  rdo = rd;
  inWatAny = false;
  dstObjExt = ExtObjRay (ro, rd);
  dstObj = dstFar;
  dstObjInt = dstFar;
  dstSphIn = InSphHit (ro - sphPos, rd, sphRad);
  hitSph = (dstSphIn < min (dstObjExt, dstFar));
  if (hitSph) {
    ro += dstSphIn * rd;
    vnIn = (ro - sphPos) / sphRad;
    inWat = false;
    needRefl = false;
    if (ro.y < watHt) {
      inWat = true;
      inWatAny = true;
      rd = refract (rd, vnIn, 1. / eta);
    }
    dstObjInt = IntObjRay (ro, rd);
    dstSphOut = OutSphHit (ro - sphPos, rd, sphRad);
    dstWat = - (ro.y - watHt) / rd.y;
    if (dstWat > 0. && dstWat < min (dstObjInt, dstSphOut)) {
      ro += dstWat * rd;
      s = 0.001 * sin (2. * pi * (2. * length (ro.xz) - tCur));
      vnw.y = 1. - abs (s);
      vnw.xz = - sign (s) * sqrt (1. - vnw.y * vnw.y) * normalize (ro.xz);
      if (inWat) {
        rdt = refract (rd, - vnw, eta);
        inWat = (length (rdt) == 0.);
        rd = (inWat) ? reflect (rd, - vnw) : rdt;
      } else {
        row = ro;
        needRefl = true;
        rd = refract (rd, vnw, 1. / eta);
        inWat = true;
        inWatAny = true;
      }
      dstObjInt = IntObjRay (ro, rd);
      dstSphOut = OutSphHit (ro - sphPos, rd, sphRad);
    }
    if (dstSphOut < dstObjInt) {
      ro += dstSphOut * rd;
      if (inWat) {
        vnOut = - (ro - sphPos) / sphRad;
        rdt = refract (rd, vnOut, eta);
        rd = (length (rdt) > 0.) ? rdt : reflect (rd, vnOut);
      }
    }
    dstObjExt = ExtObjRay (ro, rd);
  }
  dstObj = min (dstObjInt, dstObjExt);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    if (dstObjInt < dstObjExt) {
      vn = IntObjNf (ro);
      col4 = IntObjCol (ro);
    } else {
      vn = ExtObjNf (ro);
      col4 = ExtObjCol ();
    }
    if (col4.a >= 0.) {
      nDotL = max (dot (vn, ltDir), 0.);
      if (dstObjExt < dstObjInt) nDotL *= nDotL;
      sh = ObjSShadow (ro + 0.01 * ltDir, ltDir);
      if (dstObjExt < dstObjInt && idObj == idFlr && InSphHit (qHit - sphPos, ltDir, 1.05 * sphRad) <
         dstFar) sh = min (sh, 0.9);
      col = col4.rgb * (0.2 + 0.1 * max (- dot (vn, ltDir), 0.) +
         0.8 * sh * nDotL) +
         col4.a * step (0.95, sh) * pow (max (dot (reflect (ltDir, vn), rdo), 0.), 32.);
      inShell = (length (qHit) < ShRad (qHit));
      if (dstObjInt < dstObjExt || dstObjExt < dstObjInt && idObj == idShell && inShell)
         col *= 0.1 + 0.9 * smoothstep (0.05, 0.3, opPhs);
      if (dstObjExt < dstObjInt && idObj == idShell && ! inShell)
         col += 0.1 * BgCol (reflect (rdo, vn));
    } else {
      col = col4.rgb * (0.5 + 0.5 * max (- dot (vn, rdo), 0.));
    }
  } else {
    col = BgCol (rd);
  }
  if (inWatAny) col *= vec3 (0.9, 1., 0.9);
  if (needRefl) {
    ro = row;
    rd = reflect (rdo, vnw);
    dstObj = ExtObjRay (ro, rd);
    if (dstObj < dstFar) {
      ro += dstObj * rd;
      vn = ExtObjNf (ro);
      col4 = ExtObjCol ();
      colR = col4.rgb * (0.2 + 0.1 * max (- dot (vn, ltDir), 0.) + 0.8 * sh * nDotL) +
         col4.a * step (0.95, sh) * pow (max (dot (reflect (ltDir, vn), rd), 0.), 32.);
    } else {
      colR = BgCol (rd);
    }
    col = mix (col, colR, 0.03 + 0.97 * pow (1. - abs (dot (rdo, vnw)), 5.));
  }
  if (hitSph) {
    colR = BgCol (reflect (rdo, vnIn));
    col = mix (col, 1.5 * colR, 0.03 + 0.97 * pow (1. - abs (dot (rdo, vnIn)), 5.));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  opPhs = SmoothBump (0.25, 0.85, 0.15, mod (0.07 * tCur, 1.));
  opCs = sin (0.4 * pi * opPhs + vec2 (0.5 * pi, 0.));
  shellRad = 4.;
  sphRad = 3.7;
  sphHt = 0.8 * opPhs * shellRad;
  az = pi;
  el = -0.12 * pi;
  zmFac = 4.8;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
    zmFac += 2. * abs (az - pi);
  } else {
    az += 0.4 * pi * (2. * SmoothBump (0.25, 0.75, 0.24, fract (0.01 * tCur)) - 1.); 
  }
  el = clamp (el, -0.4 * pi, 0.02 * pi);
  az = clamp (az, 0.2 * pi, 1.8 * pi);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 1.5 * shellRad, -35.);
  ro.y = max (ro.y, 0.1);
  vuPos = ro;
  ltDir = vuMat * normalize (vec3 (-1., 1., -1.));
  dstFar = 100.;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

vec4 SphFib (vec3 v, float n)
{   // (Keinert et al's inverse spherical Fibonacci mapping)
  vec4 b;
  vec3 vf, vfMin;
  vec2 ff, c;
  float fk, ddMin, dd, a, z, ni;
  ni = 1. / n;
  fk = pow (phi, max (2., floor (log (n * pi * sqrt (5.) * dot (v.xy, v.xy)) /
     log (phi + 1.)))) / sqrt (5.);
  ff = vec2 (floor (fk + 0.5), floor (fk * phi + 0.5));
  b = vec4 (ff * ni, pi * (fract ((ff + 1.) * phi) - (phi - 1.)));
  c = floor ((0.5 * mat2 (b.y, - b.x, b.w, - b.z) / (b.y * b.z - b.x * b.w)) *
     vec2 (atan (v.y, v.x), v.z - (1. - ni)));
  ddMin = 4.1;
  for (int j = 0; j < 4; j ++) {
    a = dot (ff, vec2 (j - 2 * (j / 2), j / 2) + c);
    z = 1. - (2. * a + 1.) * ni;
    vf = vec3 (sin (2. * pi * fract (phi * a) + vec2 (0.5 * pi, 0.)) * sqrt (1. - z * z), z);
    dd = dot (vf - v, vf - v);
    if (dd < ddMin) {
      ddMin = dd;
      vfMin = vf;
    }
  }
  return vec4 (sqrt (ddMin), vfMin);
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 RotToDir (vec3 v1, vec3 v2, vec3 p)
{
  vec3 n;
  float c;
  n = normalize (cross (v1, v2));
  c = dot (v1, v2);
  return c * p + sqrt (1. - c * c) * cross (n, p) + (1. - c) * dot (n, p) * n;
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float distancetoline(vec2 pos1,vec2 pos2,vec2 pos){
    vec2 v1=pos1-pos;
    vec2 v2=pos2-pos;
    vec2 v3=pos1-pos2;
    if(v1.x*v3.x+v1.y*v3.y>0. && v2.x*v3.x+v2.y*v3.y<0.){
    return abs(v1.x*v2.y-v2.x*v1.y)/min(length(pos1-pos2),10000.);}
    return 1.0;
}//点到线段距离

vec2 sq(vec2 z){
