	if (p.x < p.y) p.xy = p.yx; p.x = -p.x;
	if (p.x > p.y) p.xy = p.yx; p.x = -p.x;
	if (p.x < p.z) p.xz = p.zx; p.x = -p.x;
	if (p.x > p.z) p.xz = p.zx; p.x = -p.x;
}

//@iq thanks for the sdf's!
float cap( vec3 p, float h, float r ) {
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.) + length(max(d,0.));
}
float sdbox( vec3 p, vec3 b ) {
  vec3 q = abs(p) - b;
  return length(max(q,0.)) + min(max(q.x,max(q.y,q.z)),0.);
}
float sdbox( in vec2 p, in vec2 b ){
    vec2 d = abs(p)-b;
    return length(max(d,0.)) + min(max(d.x,d.y),0.);
}

float sdframe( vec3 p, vec3 b, float e ) {
  p = abs(p)-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.))+min(max(p.x,max(q.y,q.z)),0.),
      length(max(vec3(q.x,p.y,q.z),0.))+min(max(q.x,max(p.y,q.z)),0.)),
      length(max(vec3(q.x,q.y,p.z),0.))+min(max(q.x,max(q.y,p.z)),0.));
}

//globals
mat2 tprot,rx,ry,mmt1,mmt2,mmt3;
float tmod,ga1,ga2,ga3,ga4,tt;
float ttx,tta;

vec2 map(vec3 p) {
    vec2 res =vec2(1e5,0.);

    p.xz*=ry;
    vec4 P = vec4(p.xyz,1.);
    tet(P);

    vec3 q = P.xyz; 
    q-=vec3(.5,1.5,.5);

    q.yz*=mmt1;
    q.x=abs(q.x)-ttx;
    q.zx*=mmt2;
    q.z=abs(q.z)-tta;
    q.yx*=mmt3;

    float mainbox = sdbox(q-vec3(0,1.1,0),vec3(1.5,3,.75));
    float cutbox =  sdbox(q-vec3(.45,1.25,.775),vec3(.85,2.5,1.1));
    mainbox = max(mainbox, -cutbox);

    float frame2 = sdframe(q-vec3(0,1.1,0),vec3(1.6,3.1,.82),.075)-.0125;
    if(frame2<res.x) res = vec2(frame2,4.);
    float frame = sdbox(q-vec3(-.3,1.3,.7),vec3(.445,1.35,.05));
    float tapeB = cap(vec3(q.y,q.z,abs(q.x))-vec3(2.8,.55,.65),.225,.1);
    tapeB = min(  cap(vec3(q.y,q.z,abs(q.x))-vec3(1.95,.5,.65),.100,.1),tapeB);

    vec3 tq1 = q.yzx-vec3(2.9,.25,.55);
    tq1.x=abs(abs(tq1.x+1.65)-1.2)-.6;
    frame = min(cap(tq1+vec3(0,.35,0),.25,.5),frame);
    vec3 pq1 = tq1;
    pq1.xz *= tprot;

    modPolar(pq1.xz);
    float tcbx = sdbox(pq1-vec3(.4,0,0),vec3(.09,.09,.075));

    float tape1 = cap(tq1,.575,.05);
    tape1=max(tape1,-tcbx);
    if(tape1<res.x) res = vec2(tape1,3.);

    vec3 bq = q-vec3(-.9,3,.65);
    bq.y=abs(abs(abs(bq.y)-.4)-.2)-.1;
    bq.x=abs(bq.x)-.2;
    float btn1 = sdbox(bq,vec3(.15,.05,.25))-.0125;
    btn1 = min(sdbox(q-vec3(-.5,1.25,.85),vec3(.45,.3,.06))-.0125,btn1);
    if(btn1<res.x) res = vec2(btn1,2.);

    mainbox = min(mainbox, frame);
    if(mainbox<res.x) res = vec2(mainbox,1.);

    res.x/=P.w;
    return res;
}

vec3 normal(vec3 p, float t, float mindist) {
    t*=mindist;
    float d = map(p).x;
    vec2 e = vec2(t,0);
    vec3 n = d - vec3(
        map(p-e.xyy).x,
        map(p-e.yxy).x,
        map(p-e.yyx).x
    );
    return normalize(n);
}

vec3 render(vec3 p, vec3 rd, vec3 ro, float d, float m, inout vec3 n) 
{

    n = normal(p,d,1.);
    vec3 lpos =  vec3(5.,10,8.);
    vec3 l = normalize(lpos);
    float diff = clamp(dot(n,l),0.,1.);

    vec3 h = vec3(.3);

    if(m==1.) {
        vec3 h2 =mix(vec3(.05),vec3(.4),clamp((p.y+1.)*.25,0.,1.));
        h=mix(h2,vec3(.1),clamp((p.z+4.)*.1,0.,1.));
    }

    if(m==2.) {
        vec3 h2=mix(vec3(.596,.110,.690),vec3(.961,.000,.094),clamp((p.x+2.)*.07,0.,1.));
        h=mix(vec3(.600,0.525,0.039),h2,clamp((p.z+2.)*.07,0.,1.));
    }
    if(m==3.) {
        vec3 h2 =mix(vec3(.000,.639,.514),vec3(.698,.608,.165),clamp((p.x+4.)*.1,0.,1.));
        h=mix(h2,vec3(.871,.169,.847),clamp((p.y+3.)*.1,0.,1.));
    }
    if(m==4.) {
        vec3 h2 =mix(vec3(.5),vec3(.1),clamp((p.y+1.)*.25,0.,1.));
        h=mix(h2,vec3(.01),clamp((p.z+4.)*.1,0.,1.));
    }
    return diff*h;
}

// AA Setting
#define AA 2

vec3 renderFull( vec2 uv )
{

    float alpha = 1.;
    vec3 C = vec3(.00);

    // precal
    float time = T;
    tprot=rot(T*2.);
    tmod = mod(time,24.);
    float t1 = lsp(1.,5.,tmod);
    float t2 = lsp(8.,10.,tmod);
    float t3 = lsp(4.,7.,tmod);
    float t4 = lsp(14.,22.,tmod);
    float t6 = lsp(8.,10.,tmod);
    float t7 = lsp(3.,12.,tmod);

    ga1 = eoc(t1-t2);ga1 = ga1*ga1*ga1;
    ga2 = eoc(t3-t4);ga2 = ga2*ga2*ga2;  
    ga3 = eoc(t3-t6);ga3 = ga3*ga3*ga3;  
    ga4 = eoc(t7-t4);ga4 = ga4*ga4*ga4;

    tt = time*.137;
    mmt1=rot(ga1*PI-tt);
    mmt2=rot((ga1+ga2)*PI);
    mmt3=rot(ga3*PI+tt);

    ttx=(2.5+(2.5*ga3)+ga4);
    tta=(1.25+(2.*ga1));

    //perspective camera 
    vec3 ro = vec3(0,0,25);
    vec3 rd = normalize(vec3(uv, -1.0));

    rx =rot(.78);
    ry =rot(T*.12);
    ro.yz *= rx; 
    rd.yz *= rx; 

    vec3  p = ro + rd;
    float atten = 1.;
    float k = 1.;
    float d = 0.;
    float iv = 1.;
    int bounce = 0;

    #ifdef AA
        int steps = 75;
        int thresh = 3;
    #else
        int steps = 185;
        int thresh = 5;
    #endif

    for(int i=0;i<steps;i++)
    {
        vec2 ray = map(p);
        vec3 n=vec3(0);
        float m = ray.y;

        d = i<32 ? ray.x*.4 : ray.x;
        p += rd * d *k;

        if (d*d < 1e-8) {

            C+=render(p,rd,ro,d,ray.y,n)*atten;

            alpha *= d;
            atten *= .58;
            p += rd*.025;

            k = sign(map(p).x);

            if(m== 3.) {
                rd=reflect(-rd,n);
            } else {
                vec3 rf=refract(rd,n,iv > 0. ? .85 : .1);
                iv *= -1.;
                if(length(rf) == 0.) rf = reflect(rd,n);
                rd=rf;
            }
            p+=-n*.1;
            bounce += 1;
        } 

        if (bounce > thresh) { break; }

        if(distance(p,rd)>125.) { break; }
    }

    // draw art
    float px = 1./R.x;
    float d1 = length(uv)-.4;
    d1=abs(abs(abs(d1)-.05)-.025)-.01;
    d1=smoothstep(px,-px,d1);
    C = mix(C,C+hash21(uv)*.1,min(d1,alpha));

    return vec3(C);
}

float vmul(vec2 v) {
    return v.x * v.y;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) { 

    vec3 col = vec3(.00); 
    // to make thigns fun faster and being
    // clipping the center
    vec2 dv = (2.*fragCoord.xy-R.xy)/max(R.x,R.y);
    if(dv.x<.65&&dv.x>-.65)
    {

    float mTime = iTime;

    //mTime = .38;
    time = mTime;    

    vec2 o = vec2(0);

    // AA and motion blur from iq https://www.shadertoy.com/view/3lsSzf
    // set AA above renderFull
    #ifdef AA
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
    	// pixel coordinates
    	o = vec2(float(m),float(n)) / float(AA) - 0.5;
    	// time coordinate (motion blurred, shutter=0.5)
    	float d = 0.5*vmul(sin(mod(fragCoord.xy * vec2(147,131), vec2(PI * 2.))));
    	time = mTime - 0.1*(1.0/24.0)*(float(m*AA+n)+d)/float(AA*AA-1);
    #endif

        //time = mod(time, 1.);
    	vec2 p = (-iResolution.xy + 2. * (fragCoord + o)) / iResolution.x;
    	col += renderFull(p);

    #ifdef AA
    }
    col /= float(AA*AA);
    #endif
    }

    float px = 1./R.x;

    float d1 = sdbox(dv+vec2(-.95,.515),vec2(.015))-.005;
    d1=smoothstep(px,-px,d1);
    col=mix(col,vec3(.000,.639,.514),d1);

    d1 = sdbox(dv+vec2(-.90,.515),vec2(.015))-.005;
    d1=smoothstep(px,-px,d1);
    col=mix(col,vec3(.698,.608,.165),d1);

    d1 = sdbox(dv+vec2(-.85,.515),vec2(.015))-.005;
    d1=smoothstep(px,-px,d1);
    col=mix(col,vec3(.871,.169,.847),d1);

    d1 = sdbox(dv-vec2(-.95,.515),vec2(.015))-.005;
    d1 = min(sdbox(dv-vec2(-.90,.515),vec2(.015))-.005,d1);
    d1 = min(sdbox(dv-vec2(-.85,.515),vec2(.015))-.005,d1);

    d1 = abs(d1)-.002;
    d1=smoothstep(px,-px,d1);
    col=mix(col,vec3(.12),d1);

    if(col.r<.01&&col.g<.01&&col.b<.01) col = hash21(dv)>.5 ? col+.0065 : col;    
    col = pow( col, vec3(0.4545) );

    fragColor = vec4(col, 0);
}

#define MAX_STEPS 100.0
#define MIN_DIST 0.001
#define MAX_DIST 75.0
#define pi 3.14159
#define oz vec2(0.01,0)
#define rot(a) mat2(cos(a), sin(a), -sin(a), cos(a))
#define c1 mix(2.0*vec4(0.3, 0.25, 0.2, 0), vec4(0.5), 0.75)
#define c2 mix(2.0*vec4(0.2, 0.25, 0.3, 1.0), vec4(0.5), 0.75)
#define sat(t) clamp(t, 0.0, 1.0)

// Ray marching result struct
struct RayMarch {
    float dist;     // Raymarch distance
    float steps;    // Raymarch steps
    vec3 pos;       // Surface position
    vec3 normal;    // Surface normal
    vec4 col;       // Surface color
};

// Random [0,1]
float rand(vec2 p) {
	return fract(sin(dot(p, vec2(12.543,514.123)))*4732.12);
}

// 3D Box SDF by iq
float box(vec2 p, vec2 r) {
    vec2 d = abs(p) - r;
    return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0);
}

// Greeble surface detail
float greeble(vec3 p) {
    float r = 0.0;
    for (int i = 0; i < 4; i++) {
        p += 2.*rand(floor(p.xz));
        r += sign(sin(p.x)*sin(p.y)*sin(p.z));
        p *= 2.0;
    }
    return r;
}

// Scene distance function
float scene(vec3 p) {
    float gr = 0.01*greeble(0.5*p);
    vec3 pc = p;
    pc.z = mod(pc.z,40.0)-20.0;
    p.xy = abs(p.xy);
    p.x = mod(p.x,2.0)-1.0;
    p.z = mod(p.z,2.0)-1.0;
    p.y = mod(p.y,2.0)-.0;
    float r = 1.0-p.y;
    r = max(r, -box(pc.yz, vec2(2.5,10.)));
    r = min(r, box(p.xz, vec2(0.1)));
    r = min(r, box(p.xy-vec2(0,1), vec2(0.1)));
    r = min(r, box(p.zy-vec2(0,1), vec2(0.1)));
    return 0.9*r+gr;
}

// Texturing
vec4 textureColor(vec3 p, vec3 n) {
    int axis = (abs(n.y)>=0.9 ? 0 : (abs(n.z)>=0.9 ? 1 : 2));
    vec2 tc = (axis==0 ? p.xz : (axis==1 ? p.xy : p.yz));
    tc *= 0.5;
    tc += 0.1*p.z;
    tc += 0.25*p.y;
    vec4 col = texture(iChannel0, tc);
    col *= col*col;
    col = vec4(3.0*col.r);
    return sat(col);
}

// Ray marching
RayMarch march(vec3 cam, vec3 ray) {
    float dist = 0.0;
    float d = 0.0;
    float steps = 0.0;
    vec3 p;
    while (steps < MAX_STEPS) {
        p = cam + ray * dist;
        d = scene(p);
        dist += 0.75*d;
        if (d < MIN_DIST || dist > MAX_DIST) break;
        steps++;
    }
    vec3 n = normalize(d-vec3(scene(p-oz.xyy), scene(p-oz.yxy), scene(p-oz.yyx)));
    vec4 col = textureColor(p,n);
    return RayMarch(dist, steps, p, n, col);

}

// Main
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.y;
    uv -= iResolution.xy/iResolution.y/2.0;
    // Camera setup
    vec3 cam = vec3(0);
    cam.z += 0.5*iTime;
    vec3 ray = normalize(vec3(uv.x, uv.y, 0.8));
    float rx = (iMouse.z >= 0.5 ? iMouse.x/iResolution.x*2.0*pi-pi
                : 0.25*sin(0.2*iTime));
    float ry = (iMouse.z >= 0.5 ? iMouse.y/iResolution.y*pi-pi/2.0
                : sin(0.3*iTime)*smoothstep(-1.0, 1.0, sin(0.6*iTime)));
    ray.yz *= rot(ry);
    ray.xz *= rot(rx);
    ray.xy *= rot(pi/2.0);
    // Ray march
    RayMarch rm = march(cam, ray);
    // Lighting
    float l1 = max(dot(rm.normal, normalize(vec3(1, -1.0, 0))), 0.0);
    float l2 = max(dot(rm.normal, normalize(vec3(1, 1.0, 0))), 0.0);
    float l3 = max(dot(rm.normal, normalize(vec3(0, 0, -1))), 0.0);
    float l4 = max(dot(rm.normal, normalize(vec3(-1, 0, 0))), 0.0);
    float l5 = max(dot(rm.normal, normalize(vec3(0, 0, 1))), 0.0);
    vec4 l = rm.col*2.0*c1*((l1+l2)+0.3*(l3+l5)+0.2*l4);
    float drk = sat(rm.pos.x/15.0+1.0);
    drk *= drk*drk;
    float lght = sat(rm.pos.x/20.0-0.1);
    float d = 1.0-rm.dist/MAX_DIST;
    d *= d*d*d;
    // Final composition
    fragColor = drk*l*d+2.5*c1*lght;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float si = 54.;
    vec2 ipos = floor(si * (vec2(1,5) + uv));

    float sc = pi * floor(2.5 + 0.25 * (ipos.x - ipos.y)/si + 0.5 * iTime);

    // sc2 = 1. gives noise pattern
    // sc2 = pi gives repeated pattern
    float sc2 = mix(1., pi, step(0., uv.x)); // gives a noise-like pattern

    float m = 0.5 * mod(sc * (ipos.y * cos(sc2 * ipos.x) 
                            + ipos.x * cos(sc2 * ipos.y)), 2.);

    // do 0.5 * cos(...) for a cool effect
    m = 0.5 + 0.5 * step(0.5 + 0. * cos(ipos.x/si + iTime), m);

    vec3 e = vec3(0.5);
    vec3 col = vec3(m);
    col *= pal(0.25 * (uv.y + uv.x), e, e, e, 0.5 * vec3(0,1,2)/3.); 
    col *= 1. - pow(4. * uv.y * uv.y, 4.);

    fragColor = vec4(col,1.0);
}

