// It can be a bit fiddly filling all four channels in at once, but thankfully, this is
// all calculated at startup. The idea is to put the function you wish to use in the
// middle of the loop here, instead of writing it out four times over.
vec4 funcFace1(vec3 q3){


    vec4 col = vec4(0);

    vec2 p;

    for(int i = 0; i<4; i++){

        // Texture coordinates for this iteration.
        p = mod(floor(q3.xy*cubemapRes) + vec2(i&1, i>>1), cubemapRes)/cubemapRes;

        float df = 0.;

        if(iFrame == maxFrames){

            // Rectangle distance field.
            vec4 tx = tx0(iChannel0, p);
            tx = vec4(p - tx.zw, tx.xy);
            df = getDF(p, tx);
        }
        /*
        else {
            // Lines only.
            vec2 px = 1./cubemapRes;//iResolution.yy;
            vec4 cTex = tx0(iChannel0, p);
            df = 1.;
            // One pixel width.
            df = cTex.x>0.? 0. : df;

            // 2 pixel width... It's better to use the final
            // field above to get lines only.
            vec4 cTexL = tx0(iChannel0, (p - vec2(-px.x, 0)));
            vec4 cTexR = tx0(iChannel0, (p - vec2(px.x, 0)));
            vec4 cTexB = tx0(iChannel0, (p - vec2(0, -px.y)));
            vec4 cTexT = tx0(iChannel0, (p - vec2(0, px.y)));
            df = cTexL.x>0.?  0. : df;
            df = cTexR.x>0.?  0. : df;
            df = cTexB.x>0.?  0. : df;
            df = cTexT.x>0.?  0. : df;

        }
        */

        // Cyberjax's tip to avoid array related compiler annoyances.
        col.x = df;
        col = col.yzwx;
        // The above is equivalent to the following:
        //col[i] = df;

    }

    // Return the four function values -- One for each channel.
    return col;

}


// Snapping to cube map face 1024x1024 pixel position.
vec2 conv(vec2 p){ return (floor(p*cubemapRes) + .5)/cubemapRes; }

// The packed rectangle routine: Draw some random dots on the first frame,
// which are flagged to move either horizontally or vertically. On the 
// next few frames, move them until they hit one of the other lines. After
// that you'll have a texture full of rectangle borders, so for each pixel
// not on the line, find the distance to all four rectangle boundaries, and
// use that information to determine the rectangle center and dimensions.
//
// Kind of simple in theory, and annoying to implement, but all doable.
vec4 funcFace0(vec3 q3){



    // Coordinates.
    vec2 p = q3.xy; 


    // Texture samples, and neighboring samples to check for boundary conditions.
    vec2 px = 1./cubemapRes; 
    vec4 cTex = tx0(iChannel0, p);
    vec4 cTexL = tx0(iChannel0, (p - vec2(-px.x, 0)));
    vec4 cTexR = tx0(iChannel0, (p - vec2(px.x, 0)));
    vec4 cTexB = tx0(iChannel0, (p - vec2(0, -px.y)));
    vec4 cTexT = tx0(iChannel0, (p - vec2(0, px.y)));

    vec4 rect = cTex;


    if(textureSize(iChannel0,0).x<2 || iFrame<1){

        // On the first frame or before the texture has loaded, 
        //plot some random points.

        vec2 sc = gSc.xy; 



        float tOff = 0.;//hash21(fract(iDate.w)*vec2(1.87, 113.53));
        vec2 ip = floor((p*sc)) + .5;
        vec2 offs = vec2(hash21(ip + tOff + 3.), hash21(ip + tOff + 7.)) - .5;


        // Random or heuristic direction:
        // Direction: 0 (none), 1 (horizontal), or 2 (vertical).
        // Modulo direction.
        float rd = mod(floor(ip.x) + floor(ip.y), 2.)<.1? 2. : 1.;
        #ifdef PSEUDO_RANDOM
        // Pseudo-pandom direction.
        if(hash21(ip + .27)<.5) rd = hash21(ip + .17)<.5? 2. : 1.; 
        #endif


        //ip += offs*.75;
        ip += floor(offs*5.999*.7)/5.;

        vec2 pp = conv(p - (ip/sc));

        //float rn = step(.999, rnd3.z);
        // For accuracy, it's important to have one pixel with lines...
        // I think this is how you do it. Due to partitioning, this is 
        // necessary, but if you didn't have to worring about that, you'd
        // simply flag a pixel as on or off.
        float sq = (max(abs(pp.x), abs(pp.y))< px.y*.501)? 1. : 0.;

        // Direction: If we've hit an active pixel (see above), flag its
        // direction as either horzontal or vertical. If not, flag it as
        // inactive (set it to zero).
        float dr = sq*rd;

        // Line ID. Either it's a line, or it's not.
        float ln = -sq*2.;

        // Line direction and line ID.
        rect.xy = vec2(dr, ln);


    }
    else if(iFrame<maxFrames - 1){

        float dr = cTex.x; // Direction: 0 (none), 1 (horizontal), or 2 (vertical).
        float ln = cTex.y; // Line point: Rendered or not.

        // If the pixel has not been rendered, check to the left and right to see if there's
        // a rendered horizontal pixel, and if so, mark it as a horizontal line pixel.
        if(dr<.001 && (abs(cTexL.x - 1.)<.001 || abs(cTexR.x - 1.)<.001)){ dr = 1.; ln = -2.; }
        // Same as above, but for the vertical.
        if(dr<.001 && (abs(cTexB.x - 2.)<.001 || abs(cTexT.x - 2.)<.001)){ dr = 2.; ln = -2.; }

        rect.xy = vec2(dr, ln);

    }
    else if(iFrame == maxFrames - 1){

        // If this is not a line pixel, calculate how far it is to the left
        // and right borders, and also the top and bottom border distances.
        // Use that information to calculate the rectangle center and dimensions.

        // Pixels with no line information are flagged as "-2" in the Y channel.
        // I can't remember why I decided that, but it doesn't matter.
        if(cTex.y>-1.){ 

            /////////////////////   
            float dimL = 0.;
            float dimR = 0.;
            float dimB = 0.;
            float dimT = 0.;

            // "While" loops would ensure that these run to completion,
            // but I'd imagine the compiler would like these more. I 
            // doubt a rectangle would be larger than a quarter of the
            // entire 1024 by 1024 texture size, so we should be OK.
            #ifdef PSEUDO_RANDOM
            // Longer rectangles are possible with this arrangement.
            const int n = 384; 
            #else
            const int n = 280;
            #endif

            #define ONE min(1, iFrame)
            for(int k = ONE; k<n; k++){            
                float fk = float(k);
                vec4 cTexL2 = tx0(iChannel0, p - vec2(-px.x, 0)*fk);
                if(cTexL2.y>-1.) dimL += 1.;
                else break;            
            }

            for(int k = ONE; k<n; k++){            
                float fk = float(k);
                vec4 cTexR2 = tx0(iChannel0, p - vec2(px.x, 0)*fk);
                if(cTexR2.y>-1.) dimR += 1.;
                else break;            
            }

            for(int k = ONE; k<n; k++){            
                float fk = float(k);
                vec4 cTexT2 = tx0(iChannel0, p - vec2(0, px.y)*fk);
                if(cTexT2.y>-1.) dimT += 1.;
                else break;

            }            

            for(int k = ONE; k<n; k++){            
                float fk = float(k);
                vec4 cTexB2 = tx0(iChannel0, p - vec2(0, -px.y)*fk);
                if(cTexB2.y>-1.) dimB += 1.;
                else break;
            }


            // Rectangle width and height.
            float w = ((dimR + dimL + 1.)/cubemapRes.x);
            float h = ((dimB + dimT + 1.)/cubemapRes.y);

            // Rectangle center coordinates. And yes, I should have
            // put these in the X and Y channels... I'll fix that
            // later.
            rect.z = p.x - (dimR/cubemapRes.x - w/2.);
            rect.w = p.y + (dimB/cubemapRes.y - h/2.);

            //rect.z = conv(dim);
            //rect.w = conv(df);

            // Rectangle width and height.
            rect.x = w;
            rect.y = h;


        }

    } 

    // Rectangle dimenions (in XY) and center position (in ZW).
    return rect; 


}

// Cube mapping - Adapted from one of Fizzer's routines. 
int CubeFaceCoords(vec3 p){

    // Elegant cubic space stepping trick, as seen in many voxel related examples.
    vec3 f = abs(p); f = step(f.zxy, f)*step(f.yzx, f); 

    ivec3 idF = ivec3(p.x<.0? 0 : 1, p.y<.0? 2 : 3, p.z<0.? 4 : 5);

    return f.x>.5? idF.x : f.y>.5? idF.y : idF.z; 
}



void mainCubemap(out vec4 fragColor, in vec2 fragCoord, in vec3 rayOri, in vec3 rayDir){


    // UV coordinates.
    //
    // For whatever reason (which I'd love expained), the Y coordinates flip each
    // frame if I don't negate the coordinates here -- I'm assuming this is internal, 
    // a VFlip thing, or there's something I'm missing. If there are experts out there, 
    // any feedback would be welcome. :)
    vec2 uv = fract(fragCoord/iResolution.y*vec2(1, -1));

    // Adapting one of Fizzer's old cube mapping routines to obtain the cube face ID 
    // from the ray direction vector.
    int faceID = CubeFaceCoords(rayDir);

    // We're only using one cube map face, so don't calculate any others...
    // or give the annoying compiler a chance to calculate others.
    if(faceID > 1) return;


    // Pixel storage.
    vec4 col = vec4(0);



    // Precalculation flag: GPUs are annoying. Sometimes, they'll will calculate
    // both the "if" and "else" statements every time. The "if" part here is extremely
    // expensive, so we don't want that. The solution is to not have an "if-else"
    // statement at all.
    int preCalc = 0;


    // Initial conditions -- Performed upon initiation.
    //if(abs(tx(iChannel0, uv, 5).w - iResolution.y)>.001){
    //if(iFrame<1){
    //
    // Great hack, by IQ, to ensure that this loads either on the first frame, or in the
    // event that the texture hasn't loaded (this happens a lot), wait, then do it...
    // Well kind of. Either way, it works. It's quite clever, which means that it's something 
    // I never would have considered. :)
    if(textureSize(iChannel0,0).x<2 || iFrame<=maxFrames){



        if(iFrame>maxFrames) return;




        // Fill the first cube face with a custom 3D function.
        if(faceID==0){

            //vec3 p = convert2DTo3D(uv);      
            vec3 p = vec3(uv, 0);      

            col = funcFace0(p);
            //col = mix(col, 1.-funcFace0(p*2.), 1./16.);

            preCalc = 1;

        }


